import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"


import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalInformacionClientes from "./ModalInformacionClientes"
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"

const InformacionClientes = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmInformacionCliente');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }))
  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [tipo, settipo] = useState("");
  const [dataReturn, setDataReturn] = useState(props.dataInformacionClientes);
  const [dataDelete, setDataDelete] = useState([]);
  const [jsonClientes, setjsonClientes] = useState({
    transactId: 0,
    name: null,
    country: null,
    salePercentage: null,
    customerType: null,
    salesType: null,
    termDays: null,
    delayReason: null
  });
  console.log(props);
  const [formValid, setFormValid] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [dataClientes, setdataClientes] = useState(null);
  React.useEffect(() => {
    // Read Api Service data
    initializeData();
  }, []);
  React.useEffect(() => {
    // Read Api Service data
    dataReturn.description = props.dataInformacionClientes.description;
    dataReturn.seasonalSales = props.dataInformacionClientes.seasonalSales;
    dataReturn.seasonalPercentage = props.dataInformacionClientes.seasonalPercentage;
    setDataReturn(dataReturn)
  }, [props.dataInformacionClientes]);
  const dataAccionistas = [
    { id: 1, name: "Panafoto", country: "Panamá", salepercent: "10", clientType: "Privado", saleType: "A Crédito", crtermindays: "30-120 días", description: '' },
    { id: 2, name: "Panafoto", country: "Panamá", salepercent: "10", clientType: "Privado", saleType: "A Crédito", crtermindays: "30-120 días", description: '' },
    { id: 3, name: "Panafoto", country: "Panamá", salepercent: "10", clientType: "Privado", saleType: "A Crédito", crtermindays: "30-120 días", description: '' },
    { id: 4, name: "Panafoto", country: "Panamá", salepercent: "10", clientType: "Privado", saleType: "A Crédito", crtermindays: "30-120 días", description: '' },
  ];
  function initializeData() {
    const api = new BackendServices()
    api.consultarListaClientesIGR(dataLocation.transactionId).then(resp => {
      // console.log(resp);
      if (resp.clientsInformationListDTOList.length > 0) {
        dataReturn.dataTableInformacionClientes = resp.clientsInformationListDTOList;
        setDataReturn(dataReturn)
        setdataClientes(resp.clientsInformationListDTOList.map((data) => (
          <tr key={data.customerInfoId}>
            <td>{data.name}</td>
            <td>{data.country}</td>
            <td>{data.salePercentage}%</td>
            <td>{data.customerType}</td>
            <td>{data.salesType}</td>
            <td>{data.termDays}</td>
            <td>{data.delayReason}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarDatosClientes(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { EliminarCliente(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      }else{
        setdataClientes(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function verDatosClientes(data) {
    setjsonClientes(data)
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function editarDatosClientes(data) {
    settipo("editar")
    setjsonClientes(data)
    setbotonValidation(true);
    toggleShowModelAttachment()
  }
  function EliminarCliente(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  function addCliente(values,tipo1) {
    values.transactId = Number(dataLocation.transactionId);
    values.salePercentage=Number(values.salePercentage)
    const apiBack = new BackendServices();
    if(tipo1=="guardar"){
      apiBack.nuevoClienteIGR(values).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(error => {
        seterror_dlg(true);
      })
    }else{
      values.customerIdentification=jsonClientes.customerIdentification
      apiBack.actualizarClienteIGR(values).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(error => {
        seterror_dlg(true);
      })
    }
  }
  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }

    setFormValid(true)
    //props.onSubmit(values);
  }
  return (
    <React.Fragment>
      <h5>
        {t("ClientsInfo")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmInformacionCliente" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Name")}</th>
                  <th>{t("Country")}</th>
                  <th>{t("SalePercent")}</th>
                  <th>{t("ClientType")}</th>
                  <th>{t("SaleType")}</th>
                  <th>{t("Crtermindays")}</th>
                  <th>{t("IncaseofCollectionDelayindicateReasonforCollectionDelayandStartegia")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonClientes({
                      trasactId: 0,
                      name: null,
                      country: null,
                      salePercentage: null,
                      customerType: null,
                      salesType: null,
                      termDays: null,
                      delayReason: null
                    }); settipo("guardar");toggleShowModelAttachment();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataClientes}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDescription">{t("Description")}</Label>

              <AvField
                className="form-control"
                name="description"
                type="textarea"
                value={props.dataInformacionClientes.observations}
                onChange={(e) => { dataReturn.description = e.target.value; setDataReturn(dataReturn); }}
                id="description"
                rows="7" />
            </div>
          </Col>
        </Row>
        <Row>
          <Col md="4">
            <div className="mb-3">
              <Label htmlFor="salesCycle">{t("SalesCycle")}</Label>
              <div class="kt-checkbox-list">
                <label class="kt-checkbox">
                  <AvField
                    className="form-control"
                    name="seasonalSales"
                    type="checkbox"
                    checked={props.dataInformacionClientes.seasonalSales}
                    onChange={(e) => { dataReturn.seasonalSales = e.target.checked; setDataReturn(dataReturn); }}
                    id="seasonalSales" />{t("SeasonalSales")}
                  <span></span>
                </label>
              </div>
            </div>
          </Col>
          <Col md="4">
            <AvGroup className="mb-3">
              <Label htmlFor="percentageSalesSeasonalPeriods">{t("PercentageSalesSeasonalPeriods")}</Label>
              <AvField
                className="form-control"
                name="percSeasonalPeriodSales"
                type="number"
                value={props.dataInformacionClientes.seasonalPercentage}
                onChange={(e) => { dataReturn.percSeasonalPeriodSales = e.target.value; setDataReturn(dataReturn); }}
                id="percSeasonalPeriodSales" />
            </AvGroup>
          </Col>
        </Row>
      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarClienteIGR({ transactId: dataDelete.transactId, customerIdentification: dataDelete.customerInfoId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalInformacionClientes tipo={tipo} jsonClientes={jsonClientes} botones={botonValidation} addCliente={addCliente} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );

})

/* FlujoOperativo.propTypes = {
   onSubmit: PropTypes.func.isRequired,
 }
 */

export default InformacionClientes;
