            "facilityType": "{\"tipofacilidad\":[\"LCR\",\"LCN\",\"LCS\",\"LCC\",\"PC\",\"PI\",\"PR\",\"HP\",\"PAC\",\"PCE\",\"PS\",\"CC\",\"PAG\"]}",
            "requestType": "{\"tiposolicitud\":[\"NU\",\"REC\",\"RNC\",\"REF\"]}",
