import React, { useEffect, useState } from "react";
import { useTranslation, withTranslation } from "react-i18next";
import PropTypes from "prop-types";

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap";
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation";
import { BackendServices, CoreServices } from "../../../../../services";

import { Select } from 'antd';
import { values } from "lodash";

const ModalGobiernoCorporativo = (props) => {
  const { Option } = Select;
  const [poliza, setpoliza] = useState(null);
  const [polizaSet, setpolizaSet] = useState(null);
  const [aseguradora, setaseguradora] = useState(null);
  const [aseguradoraSet, setaseguradoraSet] = useState(null);
  const [corredora, setcorredora] = useState(null);
  const [corredoraSet, setcorredoraSet] = useState(null);
  const { t, i18n } = useTranslation();
  const backendServices = new BackendServices();
  const coreServices = new CoreServices();
  useEffect(() => {

    loadData()

  }, []);
  function loadData(data = null) {
    coreServices.getInsurersCatalog().then(resp => {
      setaseguradora(resp.Records)
    });
    coreServices.getBrokersCatalog().then(resp => {
      setcorredora(resp.Records)
    });
    coreServices.getPolicyTypeCatalog().then(resp => {
      setpoliza(resp.Records)
    });
  }
  function handleSubmit(event, errors, value) {

    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    value.company = aseguradoraSet ?? "";
    value.companyCode = corredoraSet ?? "";
    value.policyType = polizaSet ?? "";
    props.getTemporalyData(value, "new")
    props.toggle();
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}
    >
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Poliza")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close"
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="Nombreasegurado">
                          {t("Nombre Asegurado ")}
                        </Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="insuredName"
                          id="insuredName"
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="Monto">{t("Monto ")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="amount"
                          id="amount"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="Monto">
                          {t("Compa単ia Aseguradora ")}
                        </Label>
                        <Select
                          showSearch
                          style={{ width: "100%" }}
                          placeholder={t("SearchtoSelect")}
                          optionFilterProp="children"
                          // defaultValue={props.dataCapex1.back}
                          onChange={(e) => { setaseguradoraSet(e) }}
                          filterOption={(input, option) =>
                            option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                          }
                        >
                          {aseguradora != null ?

                            aseguradora.map((item, index) => (
                              <Option key={index} value={item.Description}>{item.Description}</Option>
                            ))
                            : null}
                        </Select>
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="Npoliza">
                          {t("Numero de Poliza ")}
                        </Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="policyNumber"
                          id="policyNumber"
                        />
                      </div>
                    </Col>

                    <Col md="3">
                      <div className="mb-3">
                        <Label>{t("Fecha de Emison  de la Poliza  ")}</Label>
                        <AvField
                          className="form-control"
                          name="issuedDate"
                          type="date"
                          id="issuedDate"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label>{t("Fecha Vencimiento de la Poliza ")}</Label>
                        <AvField
                          className="form-control"
                          name="dueDate"
                          type="date"
                          id="dueDate"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label>{t("Compa単ia Corredora")}</Label>
                        <Select
                          showSearch
                          style={{ width: "100%" }}
                          placeholder={t("SearchtoSelect")}
                          optionFilterProp="children"
                          // defaultValue={props.dataCapex1.back}
                          onChange={(e) => { setcorredoraSet(e) }}
                          filterOption={(input, option) =>
                            option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                          }
                        >
                          {corredora != null ?

                            corredora.map((item, index) => (
                              <Option key={index} value={item.Description}>{item.Description}</Option>
                            ))
                            : null}
                        </Select>
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label>{t("Tipo poliza")}</Label>
                        <Select
                          showSearch
                          style={{ width: "100%" }}
                          placeholder={t("SearchtoSelect")}
                          optionFilterProp="children"
                          // defaultValue={props.dataCapex1.back}
                          onChange={(e) => { setpolizaSet(e) }}
                          filterOption={(input, option) =>
                            option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                          }
                        >
                          {poliza != null ?

                            poliza.map((item, index) => (
                              <Option key={index} value={item.Description}>{item.Description}</Option>
                            ))
                            : null}
                        </Select>
                      </div>
                    </Col>
                    <Col md="12">
                      <div className="mb-6">
                        <Label htmlFor="DescripEquipo">
                          {t("Descripcion del Equipo ")}
                        </Label>
                        <AvField
                          className="form-control"
                          type="textarea"
                          name="description"
                          id="description"
                          title={t("")}
                          rows="4"
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button
                    id="btnNew"
                    color="dark"
                    type="button"
                    style={{ margin: "5px" }}
                    onClick={props.toggle}
                    data-dismiss="modal"
                  >
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                    {t("Cancel")}
                  </Button>

                  <Button
                    id="btnSearch"
                    color="success"
                    type="submit"
                    style={{ margin: "5px" }}
                  >
                    <i className="mdi mdi-content-save mdi-12px"></i>{" "}
                    {props.tipo == "guardar" ? t("Save") : t("Save")}
                  </Button>
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal>
  );
};

ModalGobiernoCorporativo.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func,
};

export default ModalGobiernoCorporativo;
