import PropTypes from 'prop-types';
import { translationHelpers } from '../../../helpers/translation-helper';
import AttachmentList from '../../Attachments/AttachmentList';


import {
  Table,
  Card,
  CardBody,
  Row,
  Col
} from "reactstrap"


const FinancialStatus = (props) => {

  const [t, c] = translationHelpers('commercial_credit', 'common');

  const { client, data } = props;

  if (!data.items) {
    return null;
  }

  const dataRows = data.items.map((item, index) => (
    <tr key={'row-' + index}>
      <td>{ item.label }</td>
      <td className="text-end">{ item.data[0] }</td>
      <td className="text-end">{ item.data[1] }</td>
      <td className="text-end">{ item.data[2] }</td>
      <td className="text-end">{ item.data[3] }</td>
    </tr>)
  );

  return (
    <Card>
      <CardBody>
        <h4 className="card-title clearfix">{ t("Financial Status") }: { client.name }</h4>
        <p className="card-title-desc"></p>
        <div className="table-responsive mt-2">
          <Table className="table mb-0">
            <thead className="table-light">
              <tr>
                <th>{ t("Results Status") }</th>
                <th className="text-end">{ data.headers[0] }</th>
                <th className="text-end">{ data.headers[1] }</th>
                <th className="text-end">{ data.headers[2] }</th>
                <th className="text-end">{ data.headers[3] }</th>
              </tr>
            </thead>
            <tbody>
              {dataRows}
            </tbody>
          </Table>
        </div>       
      </CardBody>
      <CardBody>
      <Row>
          <Col lg="12">
            <AttachmentList editMode={ true }/>
          </Col>
        </Row>
      </CardBody>
    </Card>
  );
};

FinancialStatus.propTypes = {
  client: PropTypes.object.isRequired,
  data: PropTypes.object.isRequired
};


export default FinancialStatus;
