import React, { useState } from "react"
import * as OPTs from "../../../../helpers/options_helper"
import PropTypes from 'prop-types';
import { Link } from "react-router-dom"
import { useTranslation } from "react-i18next";
import { formatDate } from '../../../../helpers';
import {
  Table,
} from "reactstrap"

import ModalNewAttachment from "./ModalNewCheckListItem";

import CheckListComponent from '../../../../components/Common/CheckList';
import { AttachmentFileInputModel } from "../../../../models/Common/AttachmentFileInputModel"

const CheckList = (props) => {
  const { t, i18n } = useTranslation();
  const {locationData}= props;

  const [displayModal, setDisplayModal] = useState(false);

  
  function toggleModal() {
    setDisplayModal(!displayModal);
  }
  const dataRows = props.items.map((item, index) => (
    <tr key={'row-checklist-' + index}>
      <th scope="row">{item.requerido ? t("Yes") : t("No")}</th>
      <td>{item.descripcion}</td>
      <td>{item.fechaExpiracion ? formatDate(item.fechaExpiracion, 'dd-mmm-yyyy') : ''}</td>
      <td>{item.comentarios}</td>
      <td className="text-end">
      </td>
    </tr>)
  );
  return (
    <React.Fragment>
      <h5>{props.title}</h5>
      <p className="card-title-desc">
      </p>

      {/*locationData?
        <CheckListComponent attachmentFileInputModel={ new AttachmentFileInputModel(locationData.transactionId,OPTs.PROCESS_SIGNCONTRACT,OPTs.ACT_NONE)} />
  :null*/}  

    </React.Fragment>
  );
};
CheckList.propTypes = {
  locationData: PropTypes.any
};
export default CheckList;
