import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'

import ModalFlujoCaja from "./ModalFlujoCaja.js"
import ModalCargaTrabajo from "./ModalCargaTrabajo.js"
import { BackendServices } from "../../../../../services/index.js"
import SweetAlert from "react-bootstrap-sweetalert"

import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper"

const FlujoCaja = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  
  const { locationData } = props;
  
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [flujoCaja1Rows, setflujoCaja1Rows] = useState(null);
  const [flujoCaja2Rows, setflujoCaja2Rows] = useState(null);
  const [flujoCaja3Rows, setflujoCaja3Rows] = useState(null);
  const [flujoCaja4Rows, setflujoCaja4Rows] = useState(null);
  const [flujoCaja5Rows, setflujoCaja5Rows] = useState(null);
  const [flujoCaja6Rows, setflujoCaja6Rows] = useState(null);
  const [tituloSet, settituloSet] = useState("");
  const [flujoCaja7Rows, setflujoCaja7Rows] = useState(null);
  const [cargaTrabajoRows, setcargaTrabajoRows] = useState(null);
  const [tipo, setTipo] = useState("");
  const [dataSet, setdataSet] = useState({
    transactId: 0,
    description: null,
    january: 0,
    february: 0,
    march: 0,
    april: 0,
    may: 0,
    june: 0,
    july: 0,
    august: 0,
    september: 0,
    october: 0,
    november: 0,
    december: 0
  });
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }
  }));

  const [showModalFlujoCaja, setShowModalFlujoCaja] = useState(false);
  const [showModalCargaTrabajo, setShowModalCargaTrabajo] = useState(false);
  const api = new BackendServices();
  React.useEffect(() => {
    // Read Api Servic e data 
    initializeData();
  }, []);

  function initializeData() {
    api.consultarFlujoCajaDollar(dataLocation.transactionId).then(resp => {
      if (resp.cashFlowDollarDTOList.length > 0) {
        setflujoCaja1Rows(resp.cashFlowDollarDTOList.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {settituloSet(t("InDollars")); verDatosCajaDolar(data, "DINERO") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {settituloSet(t("InDollars")); editarDatos(data, "EDINERO") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {settituloSet(t("InDollars")); EliminarDatos(data, "DDINERO") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja1Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaCobranzas(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setflujoCaja3Rows(resp.cashFlowCollection.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { settituloSet(t("CollectionsITBMSNotIncluded"));verDatosCajaDolar(data, "COBRANZA") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { settituloSet(t("CollectionsITBMSNotIncluded"));editarDatos(data, "ECOBRANZA") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { settituloSet(t("CollectionsITBMSNotIncluded"));EliminarDatos(data, "DCOBRANZA") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja3Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaEgresos(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setflujoCaja4Rows(resp.cashFlowOutcome.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {
                  verDatosCajaDolar(data, "EGRESO"); settituloSet(t("Expenses"));
                }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  editarDatos(data, "EEGRESO"); settituloSet(t("Expenses"));
                }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  EliminarDatos(data, "DEGRESO"); settituloSet(t("Expenses"));
                }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja4Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaIngresoFacturacion(dataLocation.transactionId).then(resp => {
      if (resp!= undefined) {
        setflujoCaja2Rows(resp.cashFlowInvoiceIncome.map((data) => (
          <tr key={data.id}>
            <td>{data.description}</td>
            <td>{data.january}</td>
            <td>{data.february}</td>
            <td>{data.march}</td>
            <td>{data.april}</td>
            <td>{data.may}</td>
            <td>{data.june}</td>
            <td>{data.july}</td>
            <td>{data.august}</td>
            <td>{data.september}</td>
            <td>{data.october}</td>
            <td>{data.november}</td>
            <td>{data.december}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => {settituloSet(t("IncomeInvoicesITBMSNotIncluded")); verDatosCajaDolar(data, "INGREFACTURA") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => {settituloSet(t("IncomeInvoicesITBMSNotIncluded")); editarDatos(data, "EINGREFACTURA") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => {settituloSet(t("IncomeInvoicesITBMSNotIncluded")); EliminarDatos(data, "DINGREFACTURA") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setflujoCaja2Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaServicioDeudas(dataLocation.transactionId).then(resp => {
      if (resp.cashFlowDebtServiceDTOList.length > 0) {
        setflujoCaja7Rows(resp.cashFlowDebtServiceDTOList.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {settituloSet(t("DebtServicing")); verDatosCajaDolar(data, "DEUDAS") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {settituloSet(t("DebtServicing")); editarDatos(data, "EDEUDAS") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {settituloSet(t("DebtServicing")); EliminarDatos(data, "DDEUDAS") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja7Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaCargaTrabajo(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        
        setcargaTrabajoRows(resp.cashFlowWorkLoad.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.customer}</td>
              <td>{data.projectName}</td>
              <td>{data.organismType}</td>
              <td>{data.contractAmount}</td>
              <td>{data.pendingAmount}</td>
              <td>{data.initialPlan}</td>
              <td>{data.endPlan}</td>
              <td>{data.executedPercentage}</td>
              <td>{data.percentageTobeExecuted}</td>
              <td>{data.expectedExecution}</td>
              <td>{data.contractType}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {
                  verDatosCajaDolar(data, "CARGATRABAJO");
                }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  editarDatos(data, "ECARGATRABAJO")
                }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  EliminarDatos(data, "DCARGATRABAJO")
                }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setcargaTrabajoRows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function verDatosCajaDolar(data, tipoData) {
    setTipo(tipoData)
    setdataSet(data)
    if (tipoData == "DINERO" || tipoData == "DEUDAS" || tipoData == "INGREFACTURA" || tipoData == "COBRANZA" || tipoData == "EGRESO") {
      toggleShowModalFlujoCaja();
    } else {
      toggleShowModalCargaTrabajo();
    }
    setbotonValidation(false);
  }
  function EliminarDatos(data, tipoData) {
    setTipo(tipoData)
    setDataDelete(data)
    setconfirm_alert(true);
  }
  function editarDatos(data, tipoData) {
    setTipo(tipoData)
    setdataSet(data)
    if (tipoData == "EDINERO" || tipoData == "EDEUDAS" || tipoData == "EINGREFACTURA" || tipoData == "ECOBRANZA" || tipoData == "EEGRESO") {
      toggleShowModalFlujoCaja();
    } else {
      setbotonValidation(true);
      toggleShowModalCargaTrabajo();
    }
  }
  function guardarDatos(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    const api = new BackendServices()
    /* ---------------------------------------------------------------------------------------------- */
    /*                                  Guardamos los datos del cash                                  */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "DINERO") {
      api.nuevoFlujoCajaDollar(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                        Guardamos los datos de la deuda nueva del cliente                       */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "DEUDAS") {
      api.nuevoFlujoCajaServicioDeudas(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*           Se realiza el ingreso de la factura Ingresos por Facturas(no incluye ITBMS)          */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "INGREFACTURA") {
      api.nuevoFlujoCajaIngresoFacturacion(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                                   Cobranzas(no incluye ITBMS)                                  */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "COBRANZA") {
      api.nuevoFlujoCajaCobranzas(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                                             Egresos                                            */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "EGRESO") {
      api.nuevoFlujoCajaEgresos(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CARGATRABAJO") {
      api.nuevoFlujoCajaCargaTrabajo(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCargaTrabajo();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function actualizarDatos(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    const api = new BackendServices()
    /* ---------------------------------------------------------------------------------------------- */
    /*                                    Editita el flujo de caja                                    */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "EDINERO") {
      api.actualizarFlujoCajaDollar(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                            Editamos las deudas que otorga el cliente                           */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "EDEUDAS") {
      api.actualizarFlujoCajaServicioDeudas(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                        Se edita Ingresos por Facturas(no incluye ITBMS)                        */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "EINGREFACTURA") {
      api.actualizarFlujoCajaIngresoFacturacion(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                            Se edita las Cobranzas(no incluye ITBMS)                            */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "ECOBRANZA") {
      api.actualizarFlujoCajaCobranzas(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "EEGRESO") {
      api.actualizarFlujoCajaEgresos(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalFlujoCaja();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECARGATRABAJO") {
      api.actualizarFlujoCajaCargaTrabajo(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCargaTrabajo();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalFlujoCaja() {
    setShowModalFlujoCaja(!showModalFlujoCaja);
    removeBodyCss()
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalCargaTrabajo() {
    setShowModalCargaTrabajo(!showModalCargaTrabajo);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    //props.onSubmit(values);
  }
  return (
    <React.Fragment>
      <h5>
        {t("CashFlow")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>

        <Row>
          <Col lg="12">
            <h6 className="float-start mt-1">{t("InDollars")}</h6>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th></th>
                  <th>{t("January")}</th>
                  <th>{t("February")}</th>
                  <th>{t("March")}</th>
                  <th>{t("April")}</th>
                  <th>{t("May")}</th>
                  <th>{t("June")}</th>
                  <th>{t("July")}</th>
                  <th>{t("August")}</th>
                  <th>{t("September")}</th>
                  <th>{t("October")}</th>
                  <th>{t("November")}</th>
                  <th>{t("December")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      transactId: 0,
                      description: null,
                      january: 0,
                      february: 0,
                      march: 0,
                      april: 0,
                      may: 0,
                      june: 0,
                      july: 0,
                      august: 0,
                      september: 0,
                      october: 0,
                      november: 0,
                      december: 0
                    }); setTipo("DINERO");settituloSet(t("InDollars")); toggleShowModalFlujoCaja()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {flujoCaja1Rows}
              </tbody>
