/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types'
import { useHistory, useLocation } from 'react-router-dom'

import { Link } from "react-router-dom"

import {
  Card,
  CardBody,
  Col,
  Button,
  CardFooter,
  Row,
} from "reactstrap"
import { Tabs, Tab } from 'react-bootstrap';

//Import Breadcrumb
import Breadcrumbs from "../../../../../components/Common/Breadcrumb"

//i18n
import { withTranslation } from "react-i18next"

import DatosGenerales from "./DatosGenerales"
import DatosGeneralesBL from "./DatosGenerales2"
import SweetAlert from "react-bootstrap-sweetalert"
import { BpmServices } from "../../../../../services"
import * as url from "../../../../../helpers/url_helper";

import { BackendServices, CoreServices, } from "../../../../../services";

import { AvForm, AvField } from "availity-reactstrap-validation"
import { useTranslation } from "react-i18next"
import LoadingOverlay from "react-loading-overlay"






const PantallaBusqueda = props => {
  const { t, i18n } = useTranslation();


  const history = useHistory();

  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");

  const apiServiceBackend = new BackendServices();
  const apiServiceCore = new CoreServices();
  const location = useLocation()

  const [dataLocation, setdataLocation] = useState(location.data);

  const [instructiveType, setinstructiveType] = useState(null);
  const [facilityId, setfacilityId] = useState(null);
  const [tabsFacility, settabsFacility] = useState(null);
  const [Facility, setFacility] = useState(null);
  const [datosUsuario, setdatosUsuario] = useState(null);
  const [isActiveLoading, setIsActiveLoading] = useState(false);
  const [facilityType, setfacilityType] = useState(null);

  React.useEffect(async () => {

    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        location.data.transactionId = 0;
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setdataLocation(location.data);
        loadData(location.data)
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setdataLocation(result);
        loadData(result)
      }
    }

    apiServiceBackend.consultPrincipalDebtor(dataLocation.transactionId)
      .then((data) => {
        setdatosUsuario(data)
      });


  }, []);

  function loadData(data) {
    setIsActiveLoading(true)
    apiServiceBackend.consultGeneralDataPropCred(data.transactionId)
      .then((data) => {
        apiServiceBackend.consultarFacilidades(data[0].requestId).then(resp => {
          resp = resp.filter($$ => $$.debtor != '  ');
          setFacility(resp);
          if (resp.length > 0) {
            setinstructiveType(resp[0].debtor)
            setfacilityId(resp[0].facilityId)
            setfacilityType(resp[0]);
            settabsFacility(resp.map((items, index) => (
              <Tab className="m-0" key={index} eventKey={index} title={items.debtor}></Tab>
            )));
          }
          setIsActiveLoading(false)
        }).catch(err => {
          setIsActiveLoading(false)
        });
      }).catch(err => {
        setIsActiveLoading(false)
      });
  }

  function handleSelect(e) {
    let data = Facility.at(e);
    setfacilityType(data);
    setinstructiveType(data.debtor)
    setfacilityId(data.facilityId)
  }

  function onSaveProcess(option, values = null) {
    return new Promise(async (resolve, reject) => {
      apiServiceBackend.saveDisbursementInstructiveByFacility(values).then(resp => {
        resolve();
        if (resp.status.statusCode == 200) {
          setsuccessSave_dlg(true)
        } else {
          seterror_dlg(true)
        }
      }).catch(err => {
        resolve();
        seterror_dlg(true)
      });
    })

  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("DisbursementInstructions")} />
        <Card>
          <CardBody>

            <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
              <Row>
                <Col md="12">
                  <h4 className="card-title mb-3">{t("")}</h4>
                  <h4 className="card-title">{datosUsuario != null ? (datosUsuario.typePerson === "1" ? (datosUsuario.name + " " + datosUsuario.name2 + " " + datosUsuario.lastName + " " + datosUsuario.lastName2) : (datosUsuario.name)) + "       " : ""}           #{dataLocation.transactionId}</h4>
                  <h4 className="card-title">{t("DisbursementInstructions")}</h4>
                  <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                </Col>
              </Row>
            </LoadingOverlay>

            <Tabs defaultActiveKey="0" id="uncontrolled-tab-example" className="mb-3" onSelect={(e) => { handleSelect(e) }}>
              {tabsFacility}
            </Tabs>

            {facilityType && facilityType?.facilityType[0] != 'L' &&
              <DatosGenerales
                facilityId={facilityId}
                instructiveType={instructiveType}
                onSaveProcess={onSaveProcess}
              />}
            {facilityType && facilityType?.facilityType[0] == 'L' &&
              <DatosGeneralesBL
                facilityId={facilityId}
                instructiveType={instructiveType}
                onSaveProcess={onSaveProcess}
              />}

          </CardBody>
        </Card>


        {successSave_dlg ? (
          <SweetAlert
            success
            title={props.t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)

              history.push(url.URL_DASHBOARD);
            }}
          >
            {props.t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={props.t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
      </div>

    </React.Fragment>
  )
}

PantallaBusqueda.propTypes = {
  selectedId: PropTypes.any,
  onClose: PropTypes.func.isRequired
}

export default (withTranslation()(PantallaBusqueda))
