          <Col md="12">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Tipo de garantía")}</th>
                  <th>{t("Monto")}</th>
                  <th>{t("Compañia aseguradora")}</th>
