                    {garantiaTypeSet != null ? <Link
                      className="float-end"
                      onClick={() => {
                        toggleShowModelAttachment();
                        setdataSet({
                          "transactId": 0,
                          "facilityId": 0,
                          "guaranteeId": 0,
                          "guaranteeType": "",
                          "insuredName": "",
                          "amount": 0,
                          "company": "",
                          "policyNumber": "",
                          "policyType": "",
                          "companyCode": "",
                          "issuedDate": "",
                          "dueDate": ""
                        });
                        settype("new");
                      }}
                    >
                      <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
