import React, { useState } from "react"
import { useTranslation } from 'react-i18next'
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalInfoAccionistas from "./ModalInfoAccionistas"

import ApiServicesCommon from "../../../../../services/Common/ApiServicesCommon";
import { BackendServices } from "../../../../../services"
import SweetAlert from "react-bootstrap-sweetalert"

const InformacionAccionista = React.forwardRef((props, ref) => {

  const { t, i18n } = useTranslation();
  const location = useLocation()
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmInformacionAccionista');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }))

  const [formValid, setFormValid] = useState(false);

  function handleChange(e) {
    setFormValid(false);
  }

  // Form Submission
  function handleSubmit(event, errors, values) {

    event.preventDefault();
    if (errors.length > 0) {
      setFormValid(false);
      return;
    }
    setFormValid(true);
    // props.onSubmit();
  }

  const [botonValidation, setbotonValidation] = useState(false);
  const [dataGeneralIA, setdataGeneralIA] = useState(null);
  const [dataLocation, setData] = useState(location.data);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [shareholderRows, setShareholderRows] = useState([]);
  const [accionistasJSON, setAccionistasJson] = useState(props.jsonAccionistas);
  const [dataReturn, setDataReturn] = useState(props.jsonAccionistas);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [tipo, settipo] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataAcciones, setdataAcciones] = useState({
    transactId: 0,
    personType: "",
    identificationType: "",
    identificationNumber: "",
    shareholderFirstName: "",
    shareholderMiddleName: "",
    shareholderFirstSurname: "",
    shareholderSecondSurname: "",
    nationality: {
      code: "",
      name: ""
    },
    birthDate: 0,
    participation: 0,
    yearsExperience: 0
  });

  //On Mounting (componentDidMount)
 
  React.useEffect(() => {
    // Read Api Service data  
    loadInitialData();
    setDataReturn(props.jsonAccionistas)
  }, [props.jsonAccionistas]);


  function loadInitialData() {

    const api = new BackendServices()
    /* ---------------------------------------------------------------------------------------------- */
    /*           Cargamos la lista de accionistas pasandole como parametro el transactionId           */
    /* ---------------------------------------------------------------------------------------------- */
    api.consultarDatosAccionistas(dataLocation.transactionId).then(resp => {
      if (resp.length > 0) {
        setdataGeneralIA(resp);
        dataReturn.datosTablaAccionistas = resp;
        setDataReturn(dataReturn)
        setShareholderRows(resp.map((data) => (
          <tr key={data.shareholderIdentification}>
            <td>{data.identificationNumber}</td>
            <td>{data.shareholderFirstName + " " + data.shareholderFirstSurname}</td>
            <td>{data.nationality}</td>
            <td>{data.birthDate}</td>
            <td>{data.participation}%</td>
            <td>{data.yearsExperience} {t("Year")}s</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarDatosAccionista(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { eliminarAccionista(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)))
      } else {
        setShareholderRows(
          <tr key={1}>
            <td colSpan="5" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);

      }

    }).catch((error) => {

    });

    // api.getShareholderList()
    //   .then((response) => {
    //     setDataList(response);
    //     setShareholderList(shareholderList);

    //     ));
    //   })
    //   .catch((error) => {
    //     console.error('Error fetching documents', error);
    // });

  }
  /* ---------------------------------------------------------------------------------------------- */
  /*         Procedemos a guardar el accionista al servicio y despues renderizar esta parte         */
  /* ---------------------------------------------------------------------------------------------- */
  function agregarAccionista(values, tipo) {
    dataReturn.age = values.yearsExprience;
    dataReturn.sectorExperience = values.yearsExprience;
    dataReturn.birthDate = values.startDate;
    dataReturn.nationality = values.codigoPais.label;
    dataReturn.nationalityIdentification = 2;
    // dataReturn.nationalityIdentification=values.codigoPa is.value;
    dataReturn.shareholderIdentification = Math.floor(Math.random() * (100));
    dataReturn.shareholderName = values.name;
    dataReturn.shareholding = 3;
    dataReturn.transactId = Number(dataLocation.transactionId);
    dataReturn.status = true;
    setDataReturn(dataReturn);
    let dato = {
      transactId: Number(dataLocation.transactionId),
      nationalityIdentification: values.codigoPais.value,
      nationality: values.codigoPais.label,
      shareholding: Number(values.participation),
      sectorExperience: Number(values.yearsExprience),
    }
    if (values.identificationType != "RUC") {
      dato.shareholderName = values.firstName + " " + values.secondName + " " + values.firstLastName + " " + values.secondLastName;
      dato.birthDate = values.startDate;
    } else {
      dato.shareholderName = values.firstName;
      dato.birthDate = "2000-01-01";
    }
    let dataSet = {
      transactId: Number(dataLocation.transactionId),
      personType: values.personType,
      identificationType: values.identificationType,
      identificationNumber: values.identificationNumber,
      shareholderFirstName: values.firstName ? values.firstName : (values.firstName2 ? values.firstName2 : ""),
      shareholderMiddleName: values.secondName ? values.secondName : "",
      shareholderFirstSurname: values.firstLastName ? values.firstLastName : "",
      shareholderSecondSurname: values.secondLastName ? values.secondLastName : "",
      nationality: {
        code: values.codigoPais.value,
        name: values.codigoPais.label
      },
      birthDate: dato.birthDate?dato.birthDate:"2000-01-01",
      participation: Number(values.participation),
      yearsExperience: Number(values.yearsExprience)
    }
    /* ---------------------------------------------------------------------------------------------- */
    /*                               Procedemos a guardar el accionista                               */
    /* ---------------------------------------------------------------------------------------------- */
    const apiBack = new BackendServices();
    if (tipo == "guardar") {

      apiBack.nuevoAccionista(dataSet).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(error => {
        seterror_dlg(true);
      })
    } else {
      dataSet.shareholderId = values.shareholderId;
      apiBack.actualizarAccionista(dataSet).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(error => {
        seterror_dlg(true);
      })
    }
  }

  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {

    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }
  function verDatosAccionista(data) {
    setdataAcciones(data)
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function editarDatosAccionista(data) {
    setdataAcciones(data)
    settipo("editar");
    setbotonValidation(true);
    toggleShowModelAttachment()
  }
  function procesarDatosTabla(option, value) {
    switch (option) {
      case 1: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                       Aqui procedemos a agregar un accionista a la lista                       */
        /* ---------------------------------------------------------------------------------------------- */
        // console.log(value);
        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                         Procedemos a eliminar el accionista de la lista                        */
        /* ---------------------------------------------------------------------------------------------- */
        break;
      }
    }
  }
  function eliminarAccionista(data) {
    setDataDelete(data)
    setconfirm_alert(true)

  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }


  return (
    <React.Fragment>
      <h5>
        {t("Shareholder Information")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmInformacionAccionista" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("ID Number")}</th>
                  <th>{t("Name")}</th>
                  <th>{t("Nationality")}</th>
                  <th>{t("DBO")}</th>
                  <th>{t("Participation")}</th>
                  <th>{t("YearsExprience")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setdataAcciones({
                      transactId: 0,
                      personType: "",
                      identificationType: "",
                      identificationNumber: "",
                      shareholderFirstName: "",
                      shareholderMiddleName: "",
                      shareholderFirstSurname: "",
                      shareholderSecondSurname: "",
                      nationality: {
                        code: "",
                        name: ""
                      },
                      birthDate: 0,
                      participation: 0,
                      yearsExperience: 0
                    }); settipo("guardar"); toggleShowModelAttachment()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {shareholderRows}
              </tbody>
            </Table>
          </Col>
        </Row>

        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDetails">{t("Details")}</Label>
              <AvField onChange={(e) => { dataReturn.observations = e.target.value; setDataReturn(dataReturn); }}
                className="form-control"
                name="informacionAccionistaDetails"
                id="informacionAccionistaDetails"
                type="textarea"
                rows="7"
                value={props.jsonAccionistas.observations}
              />
            </div>
          </Col>
        </Row>

      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            loadInitialData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            loadInitialData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarAccionista({ transactId: dataDelete.transactId, shareholderId: dataDelete.shareholderId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalInfoAccionistas dataGeneralIA={dataGeneralIA} tipo={tipo} agregarAccionista={agregarAccionista} botones={botonValidation} dataAcciones={dataAcciones} procesarDatosTabla={procesarDatosTabla} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );

})

InformacionAccionista.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}

export default InformacionAccionista;
