  //     }
  // ]

  function initializeData2(dataSession) {
    apiBack.consultGeneralDataPropCred(dataSession.transactionId).then((proposal) => {
      let requestId = proposal.length === 0 ? undefined : proposal[0].requestId;
      apiBack.consultarFacilidades(requestId).then(facilities => {
        for (let facility of facilities) {
          apiBack.consultarGarantiaPropCred(facility.facilityId).then(guarantees => {
            console.log(guarantees);
            let mueblesArr = guarantees.filter(guarantee => guarantee.guaranteeTypeName === '100');
            let inMueblesArr = guarantees.filter(guarantee => guarantee.guaranteeTypeName === '200');
            let depositoArr = guarantees.filter(guarantee => guarantee.guaranteeTypeName === '300' || guarantee.guaranteeTypeName === '400');
            setMuebles(muebles => ({ fastValue: (muebles?.fastValue ?? 0) + mueblesArr.reduce((ac, crr) => ac + crr.fastValue, 0), ltv: (muebles?.ltv ?? 0) + mueblesArr.reduce((ac, crr) => ac + crr.ltv, 0), commercialValue: (muebles?.commercialValue ?? 0) + mueblesArr.reduce((ac, crr) => ac + crr.commercialValue, 0) }));
            setInmuebles(inMuebles => ({ fastValue: (inMuebles?.fastValue ?? 0) + inMueblesArr.reduce((ac, crr) => ac + crr.fastValue, 0), ltv: (inMuebles?.ltv ?? 0) + inMueblesArr.reduce((ac, crr) => ac + crr.ltv, 0), commercialValue: (inMueblesArr?.commercialValue ?? 0) + inMueblesArr.reduce((ac, crr) => ac + crr.commercialValue, 0) }));
            setDeposito(deposito => ({ fastValue: (deposito?.fastValue ?? 0) + depositoArr.reduce((ac, crr) => ac + crr.fastValue, 0), ltv: (deposito?.ltv ?? 0) + depositoArr.reduce((ac, crr) => ac + crr.ltv, 0), commercialValue: (depositoArr?.commercialValue ?? 0) + depositoArr.reduce((ac, crr) => ac + crr.commercialValue, 0) }));
          }).catch(err => { console.log(err) })
        }
      }).catch(err => { console.log(err) })
    }).catch(err => { console.log(err) })
  }
  return (


    <Card>
      <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
        <Row>
          <Col md="6">
            <h5 className="card-sub-title">{t("WarrantyDetails")}</h5>
          </Col>
        </Row>
        <CardBody>

          <Row>
            <div style={{ minHeight: "100px" }}>
              <Col className="table-responsive styled-table-div">
                <Table className="table table-striped table-hover styled-table table mb-0">
                  <thead>
                    <tr>
                      <th>{t("Type")}</th>
                      <th>{t("Commercial Value")}</th>
                      <th>{t("QuickValue")}</th>
                      <th>{t("LTV")}</th>
                    </tr>
                  </thead>
                  <tbody>
                    {/* {bodyRows1}
                    {bodyRows2}
                    {bodyRows3} */}
                    <tr>
                      <td>{t("Furniture")}</td>
                      <td>{currencyData.format(muebles?.commercialValue ?? 0)}</td>
                      <td>{currencyData.format(muebles?.fastValue ?? 0)}</td>
                      <td>{currencyData.formatPercent(muebles?.ltv ?? 0)}</td>
                    </tr>
                    <tr>
                      <td>{t("Estate")}</td>
                      <td>{currencyData.format(Inmuebles?.commercialValue ?? 0)}</td>
                      <td>{currencyData.format(Inmuebles?.fastValue ?? 0)}</td>
                      <td>{currencyData.formatPercent(Inmuebles?.ltv ?? 0)}</td>
                    </tr>
                    <tr>
                      <td>{t("Deposits")}</td>
                      <td>{currencyData.format(desposito?.commercialValue ?? 0)}</td>
                      <td>{currencyData.format(desposito?.fastValue ?? 0)}</td>
                      <td>{currencyData.formatPercent(desposito?.ltv ?? 0)}</td>
                    </tr>
                  </tbody>
                </Table>
              </Col>
            </div>
          </Row>
        </CardBody>
      </LoadingOverlay>
    </Card>
  );

};


