const PantallaBusqueda = (props) => {
  const history = useHistory();
  const location = useLocation();
  const [successSave_dlg2, setsuccessSave_dlg2] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [dataLocation, setdataLocation] = useState(undefined);
  const [showResults, setShowResults] = useState(false);
  const [dataList, setDataList] = useState([]);
  const backendServices = new BackendServices();
  const [loading, setLoading] = useState(false);
  const [typeGarantia, settypeGarantia] = useState("nuevo");
  const [displayClienteModal, setDisplayClienteModal] = useState(false);
  const [dataGeneral, setdataGeneral] = useState({
    "transactId": 0,
    "lineId": "",
    "revisionFrequency": "",
    "dueDate": "2022-03-09",
    "limitDate": "2022-03-09",
    "amount": 0,
    "maxTotal": 0,
    "modifiedAmount": 0,
    "status": true
  });
  const { t, i18n } = useTranslation();
  useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        location.data.transactionId = 0;
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        if (location.data?.dataGeneral) {
          setdataGeneral(location.data.dataGeneral);
          settypeGarantia("editar")
        }
        setdataLocation(location.data);
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        if (result?.dataGeneral) {
          setdataGeneral(result.dataGeneral);
          settypeGarantia("editar")
        }
        setdataLocation(result);
      }
    }

  }, []);
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    const datosJson = {
      "transactId": Number(dataLocation.transactionId),
      "lineId": dataGeneral.lineId,
      "revisionFrequency": "FRECUENCIA REVISION",
      "dueDate": "2022-03-09",
      "limitDate": "2022-03-09",
      "amount": Number(values.amount),
      "maxTotal": Number(values.maxTotal),
      "modifiedAmount": Number(values.modifiedAmount),
      "status": true
    }
    backendServices.updateCreditLine(datosJson).then(resp => {
      if (resp !== null && resp !== undefined) {
        setsuccessSave_dlg2(true)
      } else {
        seterror_dlg(true);
      }
    }).catch(err => {
    })

  }

  return (
    <React.Fragment>
      <h5>{t("Datos Generales")}</h5>
      <p className="card-title-desc"></p>

      <AvForm id="frmGarantiasMuebles" className="needs-validation" onSubmit={handleSubmit} autoComplete="off">
        <Row>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Frecuebnciarevi">
                {t("Frecuencia de  Revision")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="revisionFrequency"
                value={dataGeneral?.revisionFrequency ?? ""}
                id="revisionFrequency"
                title={t("Mensual")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Fecha de Vencimiento")}</Label>
              <AvField
                className="form-control"
                name="dueDate"
                type="date"
                value={dataGeneral?.dueDate ?? ""}
                id="dueDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Fecha de Limite")}</Label>
              <AvField
                className="form-control"
                name="limitDate"
                type="date"
                value={dataGeneral?.limitDate ?? ""}
                id="limitDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Monto">
                {t("Monto")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="amount"
                value={dataGeneral?.amount ?? 0}
                id="amount"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Totmaximo">
                {t("Total Maximo")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="maxTotal"
                value={dataGeneral?.maxTotal ?? 0}
                id="maxTotal"
                title={t("")}
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Monto Modificado")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="modifiedAmount"
                value={dataGeneral?.modifiedAmount ?? 0}
                id="modifiedAmount"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
                title={t("")}
              />
            </div>
          </Col>
        </Row>
        <Row>
          <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
            <Link
              to={{
                pathname: '/creditocomercial/garantias/',
                data: { facilityId: dataLocation?.facilityId ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
              }}
            >
              <Button
                id="btnNew"
                color="dark"
                type="button"
                style={{ margin: "5px" }}
                onClick={props.toggle}
                data-dismiss="modal"
              >
                <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                {t("Cancel")}
              </Button>
            </Link>
            <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
              <i className="mdi mdi-content-save mdi-12px"></i>
              {" "} {t("Edit")}</Button>
          </Col>
        </Row>
      </AvForm>
      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          confirmButtonText= {t("Confirm")}
          cancelButtonText= {t("Cancel")}          
          onConfirm={() => {
            seterror_dlg(false)
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {successSave_dlg2 ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          confirmButtonText= {t("Confirm")}
          cancelButtonText= {t("Cancel")}          
          onConfirm={() => {
            setsuccessSave_dlg2(false)
            history.push({
              pathname: '/creditocomercial/garantias/',
              data: { facilityId: dataLocation?.facilityId ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
            });
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );
};

export default withTranslation()(PantallaBusqueda);
