  async getCorporateExhibition(partyId) {
    let params = {
      PartyId: partyId
    };
    let data = qs.stringify(params);
    var dataResult = [];
    var longTerm = {
      name: "Facilidades Largo Plazo",
      approvalRisk: 0,
      proposalRisk: 0,
      variationRisk: 0
    };
    var shortTerm = {
      name: "Facilidades Corto Plazo",
      approvalRisk: 0,
      proposalRisk: 0,
      variationRisk: 0
    };
    var totalTerm = {
      name: "Total de Facilidades",
      approvalRisk: 0,
      proposalRisk: 0,
      variationRisk: 0
    };
    var pledged = {
      name: "Monto Pignorado",
      approvalRisk: 0,
      proposalRisk: 0,
      variationRisk: 0
    };
    var netExposition = {
      name: "Exposicion Neta",
      approvalRisk: 0,
      proposalRisk: 0,
      variationRisk: 0
    };

    try {
      //servicio retorna CP y LP (Corto y Largo Plazo)
      var result = await this.get(url.URL_CORE_FACILIDADES + "?" + data);
      console.log("getCorporateExhibition result", result); //Armamos la data a mostrar en pantalla

      result.AcctLoans.AcctLoan.map(function (item, i) {
        var openYear = parseInt(String(item.AcctOpeningInfo.OpenDt).split('-')[0]);
        var dueYear = parseInt(String(item.CreditAcctData.DueDt).split('-')[0]);

        if (dueYear - openYear > 1) {
          //Largo Plazo
          longTerm.approvalRisk += Number(item.AcctOpeningInfo.InitialAmt.Amt);
          longTerm.proposalRisk += Number(item.AcctBal[0].CurAmt.Amt);
        } else {
          //Corto Pazo                    
          shortTerm.approvalRisk += Number(item.AcctOpeningInfo.InitialAmt.Amt);
          shortTerm.proposalRisk += Number(item.AcctBal[0].CurAmt.Amt);
        }
      });
      console.log("getCorporateExhibition item", longTerm);
      longTerm.variationRisk = longTerm.approvalRisk - longTerm.proposalRisk;
      shortTerm.variationRisk = shortTerm.approvalRisk - shortTerm.proposalRisk;
      dataResult.push(longTerm);
      dataResult.push(shortTerm);
      totalTerm.approvalRisk = shortTerm.approvalRisk + longTerm.approvalRisk;
      totalTerm.proposalRisk = shortTerm.proposalRisk + longTerm.proposalRisk;
      totalTerm.variationRisk = totalTerm.approvalRisk - totalTerm.proposalRisk;
      dataResult.push(totalTerm);
    } catch (err) {
      console.error("URL_CORE_FACILIDADES", err);
    }

    try {
      //servicio retorna Pignorados
      var result = await this.get(url.URL_CORE_PIGNORADO + "?" + data); //Armamos la data a mostrar en pantalla

      result.Collaterals.CollateralInfo.map(function (item, i) {
        if (item.AcctKey.AcctReference !== null) {
          pledged.approvalRisk += Number(item.Collateral.CollateralAmt.Amt);
          pledged.proposalRisk += Number(item.Collateral.CollateralAmt.Amt);
          pledged.variationRisk += Number(item.Collateral.CollateralAmt.Amt);
        }
      });
      dataResult.push(pledged);
    } catch (err) {
      console.error("URL_CORE_PIGNORADO", err);
    }

    if (dataResult.length > 0) {
      netExposition.approvalRisk = totalTerm.approvalRisk - pledged.approvalRisk;
      netExposition.proposalRisk = totalTerm.proposalRisk - pledged.proposalRisk;
      netExposition.variationRisk = totalTerm.approvalRisk - pledged.proposalRisk;
      dataResult.push(netExposition);
    }

    return dataResult;
  } //ExposicionCorporativa se le pasa el Numero de Cliente de T24


