import React, { useEffect, useState } from "react"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"
import { useTranslation, withTranslation } from "react-i18next"

import {
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"
import { Link } from "react-router-dom"

// UI Components
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import {
  FacilityListBpm,FacilityListCore,ChangeSummaryReadOnly, CorporateExposure, CorporateExposureClient,
  FacilityList, FinancialSummary,
  GeneralSection
} from "../../../components/PropuestaCreditoComercial"


import SweetAlert from "react-bootstrap-sweetalert"
import AttachmentFileCore from "../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../helpers/options_helper";
import { AttachmentFileInputModel } from '../../../models/Common/AttachmentFileInputModel';
import HeaderSections from "../../../components/Common/HeaderSections";

// Services
import { BackendServices, CoreServices, BpmServices, } from "../../../services";
import LoadingOverlay from "react-loading-overlay"
import { translationHelpers, formatCurrency } from '../../../helpers';
import { success } from "toastr"

const CommercialCreditProposalPage = (props) => {


  const history = useHistory();
  const location = useLocation();

  const { t, i18n } = useTranslation();
  const [locationData, setLocationData] = useState(null);

  const sumaryData = {
    client: {
      name: 'Nombre Deudor'
    },
    financialStatus: {
      headers: ['Auditados Dic-18', 'Auditados Dic-19', 'Auditados Dic-20', 'Proyecciones Dic-21'],
      items: [
        {
          label: 'Clasificación SAFI',
          data: ['-', '-', '5:6 Riesgo', '-']
        },
        {
          label: 'Crecimiento',
          data: ['-', '-3.4%', '-12.5%', '0.0%']
        },
        {
          label: 'Costo de Ventas',
          data: ['(1550)66.2%', '(1510)66.8%', '(1360)68.8%', '(1360)68.8%']
        },
        {
          label: 'Beneficio Mutuo en Operaciones',
          data: ['(790)66.2%', '(751)66.8%', '(618)68.8%', '(618)68.8%']
        },
        {
          label: 'Margen Bruto',
          data: ['', '', '', '']
        }
      ]
    },
    balances: {
      active: {
        title: t('Active Balance'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      },
      passive: {
        title: t('Passive Balance and Capital'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      },
      application: {
        title: t('Origin and Application Status'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      },
      indicators: {
        title: t('Indicators'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      }
    },
    debts: {
      short: {
        items: [
          {
            bank: 'Capital Bank',
            purpose: 'Linea de Crédito',
            approvedAmount: 200,
            balanceLastYear: 200,
            balanceLast6Months: 154,
            balanceForecast: 200,
            rate: 7.0,
            surety: 'Fianza'
          },
          {
            bank: 'St Georges',
            purpose: 'Linea de Crédito',
            approvedAmount: 320,
            balanceLastYear: 200,
            balanceLast6Months: 436,
            balanceForecast: 450,
            rate: 7.5,
            surety: 'Fianza'
          },
          {
            bank: 'St Georges',
            purpose: 'TDC',
            approvedAmount: 11,
            balanceLastYear: 11,
            balanceLast6Months: 0,
            balanceForecast: 11,
            rate: null,
            surety: ''
          }
        ]
      },
      long: {
        items: [
          {
            bank: 'Banesco',
            purpose: 'Préstamo',
            approvedAmount: 126,
            balanceLastYear: 0,
            balanceLast6Months: 0,
            balanceForecast: 126,
            rate: 6.5,
            surety: 'Bien Inmueble'
          }
        ]
      }
    },
    history: {
      headers: [
        t('# days', { days: 30 }),
        t('# days', { days: 60 }),
        t('# days', { days: 90 }),
        t('# days', { days: 120 }),
        t('# days', { days: 150 }),
        t('# days', { days: 180 }),
        t('# days', { days: 210 }),
        t('# days', { days: 240 }),
        t('# days', { days: 270 }),
        t('# days', { days: 300 }),
        t('# days', { days: 330 }),
        t('+# days', { days: 331 }),
        t('Totals')
      ],
      data: [
        {
          name: 'Banesco',
          data: [100, 80, 80, 80, 100, 80, 80, 80, 100, 80, 80, 200, 0]
        },
        {
          name: 'Banesco',
          data: [100, 80, 80, 80, 100, 80, 80, 80, 100, 80, 80, 200, 0]
        }
      ]
    }
  }
  
  const [proposal, setProposal] = useState(null);
  const [changeSummaryItems, setChangeSummaryItems] = useState([]);
  const [facilityListItems, setFacilityListItems] = useState([]);
  const [identificationList, setIdentificationList] = useState([]);
  const [financialSummary, setFinancialSummary] = useState(sumaryData);
  const [prospect, setProspect] = useState({});
  const [isActiveLoading, setIsActiveLoading] = useState(true);
  const [personId, setpersonId] = useState(null);

  const [mainDebtor, setmainDebtor] = useState(null);
  
  //mostrar el modal para la opción de finalizar proceso
  const [displayModalEndProcess, setdisplayModalEndProcess] = useState(false);
  const [messageDlg, setmessageDlg] = useState({msg:"",show:false,error:false});
  
  
  //Servicios
  const [backendServices, setbackendServices] = useState(new BackendServices());
  const [bpmServices, setbpmServices] = useState(new BpmServices());
  const [coreServices, setcoreServices] = useState(new CoreServices());

  //On Mounting (componentDidMount)
  useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        fetchData(location.data);
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        fetchData(result);
      }
    }     
  }, []);
  
  //Caraga Inicial de datos
  function fetchData(locationData) {
    
    console.log("fetchData",locationData);
    //chequear si la tarea no ha sido iniciada
    bpmServices.checkAndStartTask(locationData)
      .then((iniresult) => {
        if (iniresult === false) {
          history.push(url.URL_DASHBOARD);
        }
      })      
      
      loadUserProspect(locationData.transactionId)
      getProspect();
  }

  function loadUserProspect(transactionId) {
    // consultarDeudorPrincipal
    backendServices.consultPrincipalDebtor(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setmainDebtor(data);
        }
      });
  }
  
  
  function fetchProposal() {

    setProposal({
      proposal: {
        id: 1010,
        revisionType: 'Bimensual',
        preapproved: false,
        minSib: 'Provision',
        relatedPart: false,
        approvalLevel: 'Nivel',
        flowType: 'Normal',
        applicationDate: '24-sep-2021',
        lastApplicationDate: '24-sep-2021',
        nextRevisionDate: '24-sep-2021',
        expirationDate: '24-sep-2021'
      },
      client: {
        number: '3223213',
      },
      economicGroup: {
        number: '32132006176',
        name: 'Grupo Tova SA',
        business: 'Actividad seleccionada'
      },
      risk: {
        countryClass: 'Panamá',
        classification: 'Medio',
        countryRisk: 'Nicaragua'
      },
      accountExecutive: {
        name: 'Elibeth'
      },
      responsibleUnit: {
        name: 'Unidad'
      }
    });

    setChangeSummaryItems([
      { description: 'Incremento / Disminución (Exposición)' },
      { description: 'Plazos: Fac 1' },
      { description: 'Tasa: Fac 1' },
      { description: 'Cuotas' },
      { description: 'Garantías' },
      { description: 'Covenats' },
      { description: 'Fiadores / Codeudores' },
      { description: 'Otras condiciones' },
    ]);

    setFinancialSummary({
      client: {
        name: 'Nombre Deudor'
      },
      financialStatus: {
        headers: ['Auditados Dic-18', 'Auditados Dic-19', 'Auditados Dic-20', 'Proyecciones Dic-21'],
        items: [
          {
            label: 'Clasificación SAFI',
            data: ['-', '-', '5:6 Riesgo', '-']
          },
          {
            label: 'Crecimiento',
            data: ['-', '-3.4%', '-12.5%', '0.0%']
          },
          {
            label: 'Costo de Ventas',
            data: ['(1550)66.2%', '(1510)66.8%', '(1360)68.8%', '(1360)68.8%']
          },
          {
            label: 'Beneficio Mutuo en Operaciones',
            data: ['(790)66.2%', '(751)66.8%', '(618)68.8%', '(618)68.8%']
          },
          {
            label: 'Margen Bruto',
            data: ['', '', '', '']
          }
        ]
      },
      balances: {
        active: {
          title: t('Active Balance'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        passive: {
          title: t('Passive Balance and Capital'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        application: {
          title: t('Origin and Application Status'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        indicators: {
          title: t('Indicators'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        }
      },
      debts: {
        short: {
          items: [
            {
              bank: 'Capital Bank',
              purpose: 'Linea de Crédito',
              approvedAmount: 200,
              balanceLastYear: 200,
              balanceLast6Months: 154,
              balanceForecast: 200,
              rate: 7.0,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'Linea de Crédito',
              approvedAmount: 320,
              balanceLastYear: 200,
              balanceLast6Months: 436,
              balanceForecast: 450,
              rate: 7.5,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'TDC',
              approvedAmount: 11,
              balanceLastYear: 11,
              balanceLast6Months: 0,
              balanceForecast: 11,
              rate: null,
              surety: ''
            }
          ]
        },
        long: {
          items: [
            {
              bank: 'Banesco',
              purpose: 'Préstamo',
              approvedAmount: 126,
              balanceLastYear: 0,
              balanceLast6Months: 0,
              balanceForecast: 126,
              rate: 6.5,
              surety: 'Bien Inmueble'
            }
          ]
        }
      }
    });

  }

  function loadIdentifications() {

    return new Promise((resolve, reject) => {
      backendServices.consultarCatalogoTipoIdentificacion()
        .then((data) => {
          if (data !== null && data !== undefined) {
            setIdentificationList(data);
          }
          resolve();
        })
        .catch((error) => {
          resolve();
          console.error('api error: ', error);
        });
    })
  }

  function handleReturn() {
    history.push(url.URL_DASHBOARD);
  }

  function handleClose() {
    history.push(url.URL_DASHBOARD);
  }

  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }

  function getProspect() {
    Promise.allSettled([
      //backServices.consultarUsuarioProspecto(location.data.transactionId),
      //backServices.consultProspectUser(location.data.transactionId),
      backendServices.consultPrincipalDebtor(location.data.transactionId),
      loadIdentifications()
    ]).then((resp) => {
      const [{ value: PrincipalDebtor }] = resp;
      console.log(resp, location, PrincipalDebtor);
      setIsActiveLoading(false);
      let customerNumberT24 = PrincipalDebtor.customerNumberT24;
      setProspect(customerNumberT24);
      loadFacilitiesList(customerNumberT24);
      setpersonId(PrincipalDebtor.personId);
    }).catch(err => {
      // console.log(err);
    });

    // backServices.consultProspectUser(location.data.transactionId).then(async (resp) => {
    //   console.log(resp);
    //   setIsActiveLoading(false)
    //   // customerNumberT24 = resp.customerNumberT24;
    //   setProspect(customerNumberT24);
    //   loadFacilitiesList(customerNumberT24);
    //   fetchProposal();
    // });

    // backServices.consultPrincipalDebtor(location.data.transactionId).then(async (resp) => {
    //   setpersonId(resp.personId);
    // });

  }


  function loadFacilitiesList(idcliente) {
    return new Promise(function (resolve, reject) {
      coreServices.getFacilities(idcliente)
        .then(resp => {
          setFacilityListItems(resp);
          resolve();
        })
        .catch(err => {
          resolve();
        });
    })
  }

  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }


  ////////// PARTE ES LA FINAL DEL PROCESO ///////
  //Modal Para mostrar Comentario de Fin de Proceso
  function showModalEndProcess(show = true) {
    setdisplayModalEndProcess(show);
  }
  
  
  //salvar comentario de end process
  function onSaveEndProcess(values) {    
    onSaveProcess(OPTs.PROCESS_CANCELPROCESS);
  }
  function onSaveProcess(option) {
    switch (option) {
      case OPTs.PROCESS_CANCELPROCESS: {
        break;
      }
    }
    saveJBPMProcess(option);
  }
  function saveJBPMProcess(option) {
    switch (option) {
      case OPTs.PROCESS_CANCELPROCESS: {
        bpmServices.abortProcess(locationData.instanceId)
          .then((data) => {
            if (data !== undefined) {
              history.push(url.URL_DASHBOARD);
            }
            else {
              showMessage({msg:t("TheProcessCouldNotFinish"),error:true,show:true});
            }
          });
        break;
      }
      case OPTs.PROCESS_ANALISISCREDITO: {        
        var values = {
          "processId": OPTs.PROCESS_ANALISISCREDITO.toString(),
          "activityId": OPTs.ACT_NONE.toString(),
          "transactionId": locationData.transactionId.toString(),
        };
        bpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {
            if (data !== undefined) {
              history.push(url.URL_DASHBOARD);
            }
            else {
              showMessage({msg:t("ErrorSaveMessage"),error:true,show:true});
            }
          });
        break;
      }
      case OPTs.OPT_ENVIARPROPUESTACREDITO: {
        var values = {
          "processId": OPTs.PROCESS_PROPUESTACREDITO.toString(),
          "activityId": OPTs.ACT_NONE.toString(),
          "transactionId": locationData.transactionId,
          "customerId": locationData.customerId,
          "applicationNumber": "",
          "procedureNumber": "",
          "decision": "si"
        };
        console.log("saveJBPMProcess value", values, locationData);
        bpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {
            if (data !== undefined) {
              setsuccessSave_dlg(true);
              history.push(url.URL_DASHBOARD);
            }
            else {
              seterror_msg(props.t("ErrorSaveMessage"))
              seterror_dlg(true)
            }
          });
        break;
      }
    }
  }
  
  function showMessage(data=null){
    if(data!==null){
      setmessageDlg(data)
    }
    else{
      setmessageDlg({msg:"",show:false,error:false})
    }
  }

  return (
    <div className="page-content">

      <Breadcrumbs title={t("Commercial Credit")} breadcrumbItem={t("Credit Analysis")} />
     
      <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
      <Card>
            <CardBody>
              <h4 className="card-title">{mainDebtor != null ? mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2 : ""}    #{locationData?.transactionId}</h4>
              <h4 className="card-title">{t("Credit Analysis")}</h4>
              <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
              <Row>
            <Col xl="12">
              <GeneralSection proposal={proposal} prospect={prospect} />
            </Col>
          </Row>
          <Row>
            <Col xl="12">
              {mainDebtor?
              <CorporateExposure title={t("CorporateExhibition")} customerNumberT24={mainDebtor?.customerNumberT24} />
              :null}              
            </Col>
          </Row>

          <Row>
            <Col xl="12">
            {mainDebtor?
              <CorporateExposureClient title={t("CorporateExhibitionClient")} customerNumberT24={mainDebtor?.customerNumberT24} />
              :null}
            </Col>
          </Row>

          <Row>
            <Col xl="12">
              {locationData?
              <ChangeSummaryReadOnly transactionId={locationData?.transactionId} />
              :null}
            </Col>
          </Row>

          <Row>
            <Col xl="12">
            {mainDebtor?
              <FacilityListCore customerNumberT24={mainDebtor?.customerNumberT24} />
              :null}              
            </Col>
          </Row>

          <Row>
            <Col xl="12">
            {locationData?
              <FacilityListBpm transactionId={locationData?.transactionId} />
              :null}              
            </Col>
          </Row>

          <Row>
            <Col xl="12">
            <CardBody>
            <HeaderSections title="Financial Summary" t={t}></HeaderSections>
            {locationData?
        <AttachmentFileCore attachmentFileInputModel={ new AttachmentFileInputModel(locationData.transactionId,OPTs.PROCESS_ANALISISCREDITO)} />
        :null}    
        </CardBody>          
            </Col>
          </Row>

          <Row>
            <Col xl="12">
            <CardBody>
            <FinancialSummary client={financialSummary.client}
              financialStatus={financialSummary.financialStatus}
              balances={financialSummary.balances}
              debts={financialSummary.debts}
              debtorId={personId}
            />
          </CardBody>
            </Col>
          </Row>
          
            </CardBody>
            <CardFooter>
                            
            <Row>
              <Col md={3} style={{ textAlign: "left", marginTop: "10px" }}>
                <Button color="danger" type="button" style={{ margin: '5px' }} onClick={() => { showModalEndProcess() }}><i className="mdi mdi-cancel mid-12px"></i> {t("Cancel")}</Button>
              </Col>
                      <Col md={9} style={{ textAlign: "right", marginTop: "10px" }}>
                        <Button color="primary" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(OPTs.OPT_SALVAPARCIAL) }}><i className="mdi mdi-content-save-outline mid-12px"></i> {t("Making")}</Button>
                        <Button color="success" type="button" style={{ margin: '5px' }} 
                          onClick={() => {
                            saveDataTabs(); setIsActiveLoading(true); setTimeout(() => {
                            saveDataTabs();
                            setTimeout(() => {
                              onSaveProcess(OPTs.OPT_SALVAFULL)
                            }, 1000);
                          }, 1000);
                        }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {t("Continue")}</Button>
                      </Col>
                    </Row>

            <Row className="my-2">
            <Col xl="12 text-end">
              <Button id="btnClose" color="dark" type="button" style={{ margin: '5px' }} onClick={handleClose}>
                {t("Close")}
              </Button>
              <Button id="btnSave" color="success" type="button" style={{ margin: '5px' }} onClick={handleReturn}>
                {t("Return")}
              </Button>
              <Link to={{ pathname: "/creditocomercial/analisiscredito/informefinanciero", data: location.data }} className="btn btn-success" style={{ margin: '5px' }}>{t("Financial Report")}</Link>

            </Col>
          </Row>
          
            </CardFooter>
          </Card>
          

      </LoadingOverlay >

        {
          messageDlg && messageDlg.show ? (
            <SweetAlert
              type={ messageDlg.error?"error":"success"}
              title={messageDlg.error?t("Error"):t("Message")}
              onConfirm={() => { showMessage(); }}>
              {messageDlg.msg}
            </SweetAlert>
          ) : null
        }

    </div>
  );

}


export default CommercialCreditProposalPage;
