import React, { useState } from "react"
import PropTypes from 'prop-types';
import { useLocation,useHistory } from 'react-router-dom'
import * as OPTs from "../../../../helpers/options_helper"
import * as url from "../../../../helpers/url_helper"

import {
  Row,
  Col,
  Card,
  CardBody,
  Label,
  CardFooter,
  Button,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"
//Import Breadcrumb
import Breadcrumbs from "../../../../components/Common/Breadcrumb"
//i18n
import { useTranslation } from "react-i18next";
//Import SweetAlert
import SweetAlert from "react-bootstrap-sweetalert";
import { Search,ToolkitProvider } from 'react-bootstrap-table2-toolkit';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import LoadingOverlay from 'react-loading-overlay';
import AttachmentFileCore from "../../../../components/Common/AttachmentFileCore";


import {BackendServices,CoreServices,BpmServices,} from "../../../../services";

const PreRequest = props => {
  
  const { t, i18n } = useTranslation();

  const history = useHistory();
  const location = useLocation();

  //Data que recibimos en el location
  const [locationData, setLocationData] = useState(undefined);     
  const [isActiveLoading, setIsActiveLoading] = useState(false);

  //Datos de la Tabla de Clientes
  const [dataClientList, setdataClientList ] = useState([]);
  const [tableClientOptions, settableClientOptions] = useState([]);
  const [tableClientHeader, settableClientHeader] = useState([]);
  const [tableClientBody, settableClientBody] = useState([]);

  const backendServices = new BackendServices();
  const bpmServices = new BpmServices();
    
  //On Mounting (componentDidMount)
  React.useEffect(() => {
    if(location.data!==null && location.data!==undefined){
      setLocationData(location.data);
      fetchData(location.data);    
    }    
  }, []);

    //Caraga Inicial de datos
    function fetchData(locationData) { 
        
        createProcess();
        clientTableConfiguration();
        getProcessClients();
    }   

    //Instanciamo y Creamos Proceso en BPM
    function createProcess() {

      if(locationData!==undefined && locationData!== null && locationData.instanceId!==null && locationData.instanceId!==undefined){
          return;
      }
      
      bpmServices.startProcess().then((number) => {
          //retorna Id del Proceso 
      if(number!== null && number !==undefined && number > 0){     
        //buscamos la tarea que crea el nuevo proceso
        bpmServices.getTasksByPrcess(number)
        .then((result) => {                                
          if(result !== undefined){                                         
            //Inicializamos la nueva tarea en BPM   
            bpmServices.startedStatusTask(result.taskId)
            .then((iniresult) => {                       
              if(iniresult!==undefined){
                 //datos que le enviamos a la pantalla de busqueda y descarte
                 // Guaradamos los datos
                 locationData.customerId="";
                 locationData.transactionId=result.instanceId;
                 locationData.processId=OPTs.PROCESS_BUSQUEDADESCARTE.toString();
                 locationData.activityId=OPTs.ACT_NONE.toString();
                 locationData.instanceId=result.instanceId;
                 locationData.taskId=result.taskId;
                 locationData.taskStatus=result.status;  
                 
                 locationData(locationData);
              }                
            })
          }
        })          
      }
  })
  .catch((error) => {
    //Mostrar Mensaje Proceso no instanciado
    console.error('api error: ', error);
    history.push(url.URL_DASHBOARD);
  });
    }

  //se configura la tabla de Clientes 
    function clientTableConfiguration(){
      
      //Opciones de la Tabla de Cliente
      let options = {
        textLabels: {
          body: {
            noMatch: t("SorryNoMatchingRecordsWereFfound"),
            toolTip:  t("Order"),
            columnHeaderTooltip: (column) => `${t("OrderBy")} ${column.label}`,
          },
          pagination: {
            next: t("Next"),
            previous: t("Prev"),
            rowsPerPage: t("RowsPerPage"),
            displayRows: t("From"),
          },
          toolbar: {
            search: t("Search"),
            downloadCsv: t("DownloadCSV"),
            print: t("Print"),
            viewColumns: t("ViewColumns"),
            filterTable: t("FilterTable"),
          },
          filter: {
            all: t("All"),
            title: t("Filter"),
            reset: t("Restore"),
          },
          viewColumns: {
            title: t("ShowColumns"),
            titleAria: t("ShowHideColumns"),
          },
          selectedRows: {
            text: t("SelectedRows"),
            delete: t("Delete"),
            deleteAria: t("DeleteSelectedRows"),
          },
        },
      };
  
      //Encabezado de la Tabla de Cliente
      let columns = [
        { text: <strong>{t("PersonType")}</strong>, dataField: 'personType', sort: true },
        { text: <strong>{t("FullName")}</strong>, dataField: 'fullName', sort: true },
        { text: <strong>{t("IdentificationType")}</strong>, dataField: 'identificationType', sort: true },
        { text: <strong>{t("IdentificationNumber")}</strong>, dataField: 'identificationNumber', sort: true },
        { text: <strong>{t("Roles")}</strong>, dataField: 'roles', sort: true },
        { text: <strong>{t("BlackList")}</strong>, dataField: 'blackList', sort: true },
        { text: "", dataField: 'action' },
      ];

      settableClientHeader(columns);
      tableClientOptions(options);
    }
 
    //se buscan todos los clientes de este proceso 
    function getProcessClients(){

    }
  

  return (
     <div className="page-content">
        
        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("PreRequest")} />

        <LoadingOverlay active={isActiveLoading} spinner text={props.t("WaitingPlease")}>
        
        <Card>
            <CardBody>

            <ToolkitProvider
                          keyField="id"
                          data={tableClientBody}
                          columns={tableClientHeader}
                          search>
                          {
                            props => (
                              <div>
                                <Search className="custome-search-field float-end" delay={1000} placeholder={t("Search")} {...props.searchProps} />
                                <BootstrapTable
                                  bootstrap4
                                  bordered={false}
                                  striped
                                  hover
                                  condensed
                                  style={{ cursor: "pointer" }}
                                  data={tableClientBody} columns={tableClientHeader}
                                  {...props.baseProps}
                                  pagination={paginationFactory(tableClientOptions)}
                                />
                              </div>
                            )
                          }
                        </ToolkitProvider>

            </CardBody>
        </Card>       
      </LoadingOverlay>       

      </div>      
    
  )
}

export default PreRequest
