import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation, useHistory } from "react-router-dom"
import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  CardTitle,
  InputGroup,
  Table,
  CardFooter
} from "reactstrap"
import { AvForm, AvField } from "availity-reactstrap-validation"
import ModalDeudasCortoPlazo from "./ModalDeudasCortoPlazo"
import ModalDeudasLargoPlazo from "./ModalDeudasLargoPlazo"
import ModalSow from "./ModalSow"
//Import Flatepicker
import moment from "moment";

import "flatpickr/dist/themes/material_blue.css";
import Flatpickr from "react-flatpickr";
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"
import * as url from "../../../../../helpers/url_helper"
const shorttermdebts = [
  { id: 1, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo' },
  { id: 2, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo' },
  { id: 3, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo' },
  {
    id: 4, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000',
    debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo'
  },
];
const longtermdebts = [
  { id: 1, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo' },
  { id: 2, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo' },
  { id: 3, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo' },
  {
    id: 4, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000',
    debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo'
  },
];
const sowactual = [
  { id: 1, otherbank: "8050011.78", banesco: '1158000', total: '9208011.78', sow: '13%' },
];
const sowproposed = [
  { id: 1, otherbank: "8050011.78", banesco: '1158000', total: '9208011.78', sow: '13%' },
];
const estiloTotales = { backgroundColor: "lightgrey", color: "black" }
const RelacionesBancarias = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmRelacionesBancaria');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [dataReturn, setdataReturn] = useState(props.dataRelacionesBancarias);
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  /* ---------------------------------------------------------------------------------------------- */
  /*                          Variables de estados generales del componente                         */
  /* ---------------------------------------------------------------------------------------------- */
  const [formValid, setFormValid] = useState(false);
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [showModalDeudasCortoPlazo, setShowModalDeudasCortoPlazo] = useState(false);
  const [showModalDeudasLargoPlazo, setShowModalDeudasLargoPlazo] = useState(false);
  const [showModalSow, setShowModalSow] = useState(false);
  const [shorttermdebtsRows, setshorttermdebtsRows] = useState(null);
  const [longtermdebtsRows, setlongtermdebtsRows] = useState(null);
  const [sowactualRows, setsowactualRows] = useState(null);
  const [sowproposedRows, setsowproposedRows] = useState(null);
  const [tipo, setTipo] = useState("");
  const [dataSet, setdataSet] = useState({
    transactId: 0,
    otherBanks: 0,
    banesco: 0,
    total: 0,
    sow: 0
  });
  const [locationData, setLocationData] = useState(null);
  const history = useHistory();

  React.useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }


    setdataReturn(props.dataRelacionesBancarias);
    initializeData(dataSession);
  }, [props.activeTab == 14]);
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    // props.onSubmit(values);
  }
  function initializeData(dataSession) {
    loadSowNow(dataSession);
    uploadSowProposed(dataSession);
    loadTablesShortTerm(dataSession);
    loadLongTermTables(dataSession);
  }
  function loadSowNow(dataLocation) {
    const api = new BackendServices()
    // consultarSowActualIGR
    api.consultSowActualIGR(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablSowActual = resp.currentSOWDTOList;
      setdataReturn(dataReturn)
      setsowactualRows(resp.currentSOWDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td>{data.otherBanks}</td>
            <td>{data.banesco}</td>
            <td>{data.total}</td>
            <td>{data.sow}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editSowCurrent(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { deleteSowCurrent(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  function uploadSowProposed(dataLocation) {
    const api = new BackendServices()
    // consultarSowPropuestoIGR
    api.consultSowProposedIGR(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablSowPropuesto = resp.proposedSOWDTOList;
      setdataReturn(dataReturn)
      setsowproposedRows(resp.proposedSOWDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td>{data.otherBanks}</td>
            <td>{data.banesco}</td>
            <td>{data.total}</td>
            <td>{data.sow}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editSowProposed(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { removeSowProposed(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  function loadTablesShortTerm(dataLocation) {
    const api = new BackendServices()
    // consultarRelacionesBancariasDeudasCP
    api.consultBankingRelationsDebtsCP(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablDeudaCorto = resp.getBankingRelationCPDTOList;
      dataReturn.sumatoriaDeudaCorto = { monto: 0, saldo1: 0, saldo2: 0, saldo3: 0 }
      setdataReturn(dataReturn);
      for (let i = 0; i < resp.getBankingRelationCPDTOList.length; i++) {
        dataReturn.sumatoriaDeudaCorto.monto = parseFloat(dataReturn.sumatoriaDeudaCorto.monto) + parseFloat(resp.getBankingRelationCPDTOList[i].amount);
        dataReturn.sumatoriaDeudaCorto.saldo1 = parseFloat(dataReturn.sumatoriaDeudaCorto.saldo1) + parseFloat(resp.getBankingRelationCPDTOList[i].debitBalance1);
        dataReturn.sumatoriaDeudaCorto.saldo2 = parseFloat(dataReturn.sumatoriaDeudaCorto.saldo2) + parseFloat(resp.getBankingRelationCPDTOList[i].debitBalance2);
        dataReturn.sumatoriaDeudaCorto.saldo3 = parseFloat(dataReturn.sumatoriaDeudaCorto.saldo3) + parseFloat(resp.getBankingRelationCPDTOList[i].debitBalance3);
      }
      setdataReturn(dataReturn)
      setshorttermdebtsRows(resp.getBankingRelationCPDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td>{data.bank}</td>
            <td>{data.facilityType}</td>
            <td>{data.amount}</td>
            <td>{formatDate(data.date)}</td>
            <td>{formatDate(data.expirationDate)}</td>
            <td>{data.debitBalance1}</td>
            <td>{data.debitBalance2}</td>
            <td>{data.debitBalance3}</td>
            <td>{data.rate}</td>
            <td>{data.bail}</td>
            <td>{data.fee}</td>
            <td>{data.fundDestiny}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editShortTerm(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { removeShortTerm(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  // function cerrarModal() {
  //   setShowModelAttachment(false);
  //   removeBodyCss()
  // }
  // function abrirModal() {
  //   setShowModelAttachment(true);
  //   removeBodyCss()
  // }
  function loadLongTermTables(dataLocation) {
    const api = new BackendServices()
    // consultarRelacionesBancariasDeudasLP
    api.consultBankRelationsDebtsLP(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablDeudaLargo = resp.bankingRelationLPDTOList;
      dataReturn.sumatoriaDeudaLargo = { monto: 0, saldo1: 0, saldo2: 0, saldo3: 0 }
      setdataReturn(dataReturn);
      for (let i = 0; i < resp.bankingRelationLPDTOList.length; i++) {
        dataReturn.sumatoriaDeudaLargo.monto = parseFloat(dataReturn.sumatoriaDeudaLargo.monto) + parseFloat(resp.bankingRelationLPDTOList[i].amount);
        dataReturn.sumatoriaDeudaLargo.saldo1 = parseFloat(dataReturn.sumatoriaDeudaLargo.saldo1) + parseFloat(resp.bankingRelationLPDTOList[i].debitBalance1);
        dataReturn.sumatoriaDeudaLargo.saldo2 = parseFloat(dataReturn.sumatoriaDeudaLargo.saldo2) + parseFloat(resp.bankingRelationLPDTOList[i].debitBalance2);
        dataReturn.sumatoriaDeudaLargo.saldo3 = parseFloat(dataReturn.sumatoriaDeudaLargo.saldo3) + parseFloat(resp.bankingRelationLPDTOList[i].debitBalance3);
      }
      setdataReturn(dataReturn)
      setlongtermdebtsRows(resp.bankingRelationLPDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td>{data.bank}</td>
            <td>{data.facilityType}</td>
            <td>{data.amount}</td>
            <td>{formatDate(data.date)}</td>
            <td>{formatDate(data.expirationDate)}</td>
            <td>{data.debitBalance1}</td>
            <td>{data.debitBalance2}</td>
            <td>{data.debitBalance3}</td>
            <td>{data.rate}</td>
            <td>{data.bail}</td>
            <td>{data.fee}</td>
            <td>{data.fundDestiny}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editLongTerm(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { removeLongTerm(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  function formatDate(date) {
    return moment(date).format("DD/MM/YYYY");
  }
  /* ---------------------------------------------------------------------------------------------- */
  /*                           Funciones para los Snow actual y propuesto                           */
  /* ---------------------------------------------------------------------------------------------- */
  function saveData(data, tipo) {
    data.transactId = locationData.transactionId;
    const api = new BackendServices()
    if (tipo == "Actual") {
      // nuevoSowActualIGR
      let datoJson = {
        transactId: Number(data?.transactId) ?? 0,
        otherBanks: Number(data?.otherBanks) ?? 0,
        banesco: Number(data?.banesco) ?? 0,
        total: Number(data?.total) ?? 0,
        sow: Number(data?.sow) ?? 0
      }
      api.newSowCurrentIGR(datoJson).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalSow(false);
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "Propuesto") {
      // nuevoSowPropuestoIGR
      let datoJson = {
        transactId: Number(data?.transactId) ?? 0,
        otherBanks: Number(data?.otherBanks) ?? 0,
        banesco: Number(data?.banesco) ?? 0,
        total: Number(data?.total) ?? 0,
        sow: Number(data?.sow) ?? 0
      }
      api.newSowProposedIGR(datoJson).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalSow(false);
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CP") {
      let datos = {
        "facilityType": data.facilityType ?? " ",
        "amount": Number(data?.amount) ?? 0,
        "date": data?.date ?? "2021-11-11",
        "expirationDate": data?.expirationDate ?? "2021-11-11",
        "debitBalance1": Number(data?.debitBalance1) ?? 0,
        "debitBalance2": Number(data?.debitBalance2) ?? 0,
        "debitBalance3": Number(data?.debitBalance3) ?? 0,
        "rate": Number(data?.rate) ?? 0,
        "fee": Number(data?.fee) ?? 0,
        "bail": data.bail ?? " ",
        "fundDestiny": data.fundDestiny ?? " ",
        "bank": data.bank ?? " ",
        "transactId": Number(data?.transactId) ?? 0
      }
      // nuevoRelacionesBancariasDeudasCP
      api.newBankingRelationsDebtsCP(datos).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalDeudasCortoPlazo(false)
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CL") {
      let datos = {
        "facilityType": data?.facilityType ?? " ",
        "amount": Number(data?.amount) ?? 0,
        "date": data?.date ?? "2021-11-11",
        "expirationDate": data?.expirationDate ?? "2021-11-11",
        "debitBalance1": Number(data?.debitBalance1) ?? 0,
        "debitBalance2": Number(data?.debitBalance2) ?? 0,
        "debitBalance3": Number(data?.debitBalance3) ?? 0,
        "rate": Number(data?.rate) ?? 0,
        "fee": Number(data?.fee) ?? 0,
        "bail": data?.bail ?? " ",
        "fundDestiny": data?.fundDestiny ?? " ",
        "bank": data?.bank ?? " ",
        "transactId": Number(data?.transactId) ?? 0
      }
      // nuevoRelacionesBancariasDeudasLP
      api.newBankingRelationsDebtsLP(datos).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalDeudasLargoPlazo(false)
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function updateData(data, tipo) {
    data.transactId = locationData.transactionId;
    const api = new BackendServices()
    if (tipo == "EditarActual") {
      // actualizarSowActualIGR
      let jsonSow = {
        transactId: Number(data?.transactId) ?? 0,
        currentSowId: Number(data?.currentSowId) ?? 0,
        otherBanks: Number(data?.otherBanks) ?? 0,
        banesco: Number(data?.banesco) ?? 0,
        total: Number(data?.total) ?? 0,
        sow: Number(data?.sow) ?? 0,
        status: true
      }
      api.updateSowActualIGR(jsonSow).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalSow(false);
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "EditarPropuesto") {
      // actualizarSowPropuestoIGR
      let datoJson = {
        transactId: Number(data?.transactId) ?? 0,
        currentSowId: Number(data?.currentSowId) ?? 0,
        otherBanks: Number(data?.otherBanks) ?? 0,
        banesco: Number(data?.banesco) ?? 0,
        total: Number(data?.total) ?? 0,
        sow: Number(data?.sow) ?? 0,
        status: true
      }
      api.updateSowProposedIGR(datoJson).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalSow(false);
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECP") {
      console.log("ECP",data);
      let datos = {
        "facilityType": data?.facilityType ?? "",
        "amount": Number(data?.amount) ?? 0,
        "date": data?.date ?? "2022-01-01",
        "expirationDate": data?.expirationDate ?? "2022-01-01",
        "debitBalance1": Number(data?.debitBalance1) ?? 0,
        "debitBalance2": Number(data?.debitBalance2) ?? 0,
        "debitBalance3": Number(data?.debitBalance3) ?? 0,
        "rate": Number(data?.rate) ?? 0,
        "fee": Number(data?.fee) ?? 0,
        "bail": data?.bail ?? "",
        "fundDestiny": data?.fundDestiny ?? "",
        "bank": data?.bank ?? "",
        "transactId": Number(data?.transactId) ?? 0,
        "debtId": data?.debtId ?? 0,
        "status": true,
      }
      // actualizarRelacionesBancariasDeudasCP
      api.updateBankRelationsDebtsCP(datos).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalDeudasCortoPlazo(false)
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECL") {
      console.log("ECL",data);

      let datos = {
        "facilityType": data?.facilityType ?? "",
        "amount": Number(data?.amount) ?? 0,
        "date": data?.date ?? "2022-01-01",
        "expirationDate": data?.expirationDate ?? "2022-01-01",
        "debitBalance1": Number(data?.debitBalance1) ?? 0,
        "debitBalance2": Number(data?.debitBalance2) ?? 0,
        "debitBalance3": Number(data?.debitBalance3) ?? 0,
        "rate": Number(data?.rate) ?? 0,
        "fee": Number(data?.fee) ?? 0,
        "bail": data?.bail ?? "",
        "fundDestiny": data?.fundDestiny ?? "",
        "bank": data?.bank ?? "",
        "transactId": Number(data?.transactId) ?? 0,
        "debtId": data?.debtId ?? "",
        "status": true,
      }
      // actualizarRelacionesBancariasDeudasLP
      api.updateBankRelationsDebtsLP(datos).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          initializeData(locationData);
          toggleShowModalDeudasLargoPlazo(false)
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function removeSowProposed(data) {
    setTipo("Propuesto");
    setDataDelete(data)
    setconfirm_alert(true);
  }
  // Sow Actual validaciones
  function editSowCurrent(data) {
    // setjsonClientes(data)
    setdataSet(data);
    setTipo("EditarActual");
    setbotonValidation(true);
    toggleShowModalSow(true);
  }
  function editShortTerm(data) {
    // setjsonClientes(data)
    setdataSet(data);
    setTipo("ECP");
    setbotonValidation(true);
    toggleShowModalDeudasCortoPlazo(true);
  }
  function editLongTerm(data) {
    // setjsonClientes(data)
    setdataSet(data);
    setTipo("ECL");
    setbotonValidation(true);
    toggleShowModalDeudasLargoPlazo(true);
  }
  function editSowProposed(data) {
    // setjsonClientes(data)
    setdataSet(data);
    setbotonValidation(true);
    setTipo("EditarPropuesto");
    toggleShowModalSow(true);
  }
  function deleteSowCurrent(data) {
    setTipo("Actual");
    setDataDelete(data)
    setconfirm_alert(true);
  }
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Funciones para las cuentas a corto y largo plazo                        */
  /* ---------------------------------------------------------------------------------------------- */
  // Ventana de corto plazo validaciones
  function removeShortTerm(data) {
    setTipo("DCP");
    setDataDelete(data)
    setconfirm_alert(true);
  }
  // Ventana de largo plazo validaciones
  function removeLongTerm(data) {
    setTipo("DCL");
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalDeudasCortoPlazo(estado) {
    setShowModalDeudasCortoPlazo(estado);
    removeBodyCss()
  }
  function toggleShowModalDeudasLargoPlazo(estado) {
    setShowModalDeudasLargoPlazo(estado);
    removeBodyCss()
  }
  function toggleShowModalSow(estado) {
    setShowModalSow(estado);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  return (
    <React.Fragment>
      <h5>{t("CurrentBankingRelations")}</h5>
      <p className="card-title-desc"></p>

    


      <AvForm id="frmRelacionesBancaria" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <h6 className="float-start my-1">{t("ShortTermDebts")}</h6>
            <Link className="float-end" onClick={() => {
              setTipo("CP"); setbotonValidation(true); setdataSet({
                transactId: 0,
                bank: null,
                facilityType: null,
                amount: 0,
                date: null,
                expirationDate: null,
                debitBalance1: 0,
                debitBalance2: 0,
                debitBalance3: 0,
                rate: 0,
                fee: 0,
                bail: null,
                destinationOfFunds: null
              }); toggleShowModalDeudasCortoPlazo(true)
            }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
          </Col>
        </Row>
        <Row>
          <Col md="12"  style={{"overflowX":"scroll"}}>
            <Table className="table mt-1" >
              <thead className="table-light">
                <tr>
                  <th>{t("Bank")}</th>
                  <th>{t("FacilityType")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("GrantDate")}</th>
                  <th>{t("DueDate")}</th>
                  <th>{t("CurrentYear")}</th>
                  <th>1 {t("YearAgo")}</th>
                  <th>2 {t("YearAgo")}</th>
                  <th>{t("Rate")}</th>
                  <th>{t("Bond")}</th>
                  <th>{t("Term") + "(" + t("days") + ")"}</th>
                  <th>{t("DestinationOfFunds")}</th>
                  <th style={{ textAlign: "right" }}></th>
                </tr>
              </thead>
              <tbody>
                {shorttermdebtsRows}
                <tr style={estiloTotales}>
                  <th>{t("Total")}</th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.monto}</th>
                  <th></th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.saldo1}</th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.saldo2}</th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.saldo3}</th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>
        <div className="my-3"></div>
        <Row>
          <Col md="12">
            <h6 className="float-start my-1">{t("LongTermDebts")}</h6>
            <Link className="float-end" onClick={() => {
              setTipo("CL"); setbotonValidation(true); setdataSet({
                transactId: 0,
                bank: null,
                facilityType: null,
                amount: 0,
                date: null,
                expirationDate: null,
                debitBalance1: 0,
                debitBalance2: 0,
                debitBalance3: 0,
                rate: 0,
                fee: 0,
                bail: null,
                destinationOfFunds: null
              }); toggleShowModalDeudasLargoPlazo(true)
            }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
          </Col>
        </Row>
        <Row>
          <Col md="12"  style={{"overflowX":"scroll"}}>
            <Table className="table mt-1" >
              <thead className="table-light">
                <tr>
                  <th>{t("Bank")}</th>
                  <th>{t("FacilityType")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("GrantDate")}</th>
                  <th>{t("DueDate")}</th>
                  <th>{t("CurrentYear")}</th>
                  <th>1 {t("YearAgo")}</th>
                  <th>2 {t("YearAgo")}</th>
                  <th>{t("Rate")}</th>
                  <th>{t("Bond")}</th>
                  <th>{t("Term") + "(" + t("days") + ")"}</th>
                  <th>{t("DestinationOfFunds")}</th>
                  <th style={{ textAlign: "right" }}></th>
                </tr>
              </thead>
              <tbody>
                {longtermdebtsRows}
                <tr style={estiloTotales}>
                  <th>{t("Total")}</th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.monto}</th>
                  <th></th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.saldo1}</th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.saldo2}</th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.saldo3}</th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>
        <div className="my-3"></div>
        <Row>
          <Col lg="6">
            <Row>
              <Col lg="12">
                <h6 className="float-start my-1">{t("ActualSow")}</h6>
                <Link className="float-end" onClick={() => {
                  setdataSet({
                    transactId: 0,
                    otherBanks: 0,
                    banesco: 0,
                    total: 0,
                    sow: 0
                  }); setTipo("Actual"); toggleShowModalSow(true)
                }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
              </Col>
            </Row>
            <Row>
              <Col lg="12">
                <Table className="table mt-1" >
                  <thead className="table-light">
                    <tr>
                      <th>{t("OtherBank")}</th>
                      <th>{t("Banesco")}</th>
                      <th>{t("Total")}</th>
                      <th>{t("Sow")}</th>
                      <th style={{ textAlign: "right" }}></th>
                    </tr>
                  </thead>
                  <tbody>
                    {sowactualRows}
                  </tbody>
                </Table>
              </Col>
            </Row>
          </Col>
          <Col lg="6">
            <Row>
              <Col lg="12">
                <h6 className="float-start my-1">{t("ProposedSow")}</h6>
                <Link className="float-end" onClick={() => {
                  setdataSet({
                    transactId: 0,
                    otherBanks: 0,
                    banesco: 0,
                    total: 0,
                    sow: 0
                  }); setTipo("Propuesto"); toggleShowModalSow(true)
                }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
              </Col>
            </Row>
            <Row>
              <Col lg="12">
                <Table className="table mt-1" >
                  <thead className="table-light">
                    <tr>
                      <th>{t("OtherBank")}</th>
                      <th>{t("Banesco")}</th>
                      <th>{t("Total")}</th>
                      <th>{t("Sow")}</th>
                      <th style={{ textAlign: "right" }}></th>
                    </tr>
                  </thead>
                  <tbody>
                    {sowproposedRows}
                  </tbody>
                </Table>
              </Col>
            </Row>
          </Col >
        </Row >

        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDetails">{t("Details")}</Label>
              <AvField
                type="textarea"
                name="companyHistoryDetails"
                id="companyHistoryDetails"
                maxLength="1000"
                rows="7"
                value={props.dataRelacionesBancarias.observations}
                onChange={(e) => { setdataReturn({ observations: e.target.value }) }}
              />
            </div>
          </Col>
        </Row>
      </AvForm >
      <ModalDeudasCortoPlazo updateData={updateData} botones={botonValidation} jsonSow={dataSet} saveData={saveData} tipo={tipo} isOpen={showModalDeudasCortoPlazo} toggle={() => { toggleShowModalDeudasCortoPlazo(false) }} />
      <ModalDeudasLargoPlazo updateData={updateData} botones={botonValidation} jsonSow={dataSet} saveData={saveData} tipo={tipo} isOpen={showModalDeudasLargoPlazo} toggle={() => { toggleShowModalDeudasLargoPlazo(false) }} />
      <ModalSow updateData={updateData} botones={botonValidation} jsonSow={dataSet} saveData={saveData} tipo={tipo} isOpen={showModalSow} toggle={() => { toggleShowModalSow(false) }} />
      {/* LLAMADA A LOS COMPONENTES DE ALERTA PARA MENSAJES DE CONFIRMACION */}
      {
        successSave_dlg ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)
              initializeData(locationData);
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }
      {
        error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
              initializeData(locationData);
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
      {
        confirm_alert ? (
          <SweetAlert
            title={t("Areyousure")}
            warning
            showCancel
            confirmButtonText={t("Yesdeleteit")}
            confirmBtnBsStyle="success"
            cancelBtnBsStyle="danger"
            onConfirm={() => {
              const apiBack = new BackendServices();
              if (tipo == "Actual") {
                // eliminarSowActualIGR
                apiBack.removeSowCurrentIGR({ transactId: locationData.transactionId, currentSowId: dataDelete.currentSowId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    initializeData(locationData);
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "Propuesto") {
                // eliminarSowPropuestoIGR
                apiBack.deleteSowProposedIGR({ transactId: locationData.transactionId, currentSowId: dataDelete.currentSowId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    initializeData(locationData);
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCP") {
                // eliminarRelacionesBancariasDeudasCP
                apiBack.eliminateBankingRelationshipsDebtsCP({ transactId: locationData.transactionId, debtId: dataDelete.debtId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    initializeData(locationData);
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCL") {
                // eliminarRelacionesBancariasDeudasLP
                apiBack.eliminateBankingRelationshipsDebtsLP({ transactId: locationData.transactionId, debtId: dataDelete.debtId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    initializeData(locationData);
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
            }}
            onCancel={() => setconfirm_alert(false)}
          >
            {t("Youwontbeabletorevertthis")}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );
});
RelacionesBancarias.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
export default RelacionesBancarias;
