import PropTypes from 'prop-types';
import { formatCurrency, translationHelpers } from '../../../../helpers';
import {
  Table,
  Card,
  CardBody,
} from "reactstrap"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import React, { useEffect, useState } from "react"
import { useTranslation } from 'react-i18next';
import { useLocation, useHistory } from 'react-router-dom';
import * as url from "../../../../helpers/url_helper"

const ExposicionCorportativa = (props) => {
  const location = useLocation();
  const history = useHistory();
  const [locationData, setLocationData] = useState(null);
  const coreServices = new CoreServices();
  const { t, i18n } = useTranslation();
  const [dataRows, setdataRows] = React.useState(null);
  const [ExposicionCorportativa, setExposicionCorportativa] = useState(null);
  const [customerNumberT24, setcustomerNumberT24] = React.useState(props.customerNumberT24);
  useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }


    getCoporateExhibition(dataSession);
  }, []);
  function getCoporateExhibition(dataSession) {
    const backendServices = new BackendServices();
    backendServices.consultPrincipalDebtor(dataSession.transactionId)
      .then((data) => {
        if (data !== undefined) {
          coreServices.getCorporateExhibition(data.customerNumberT24).then(resp => {
            if (resp === undefined) {
              return;
            }
            setExposicionCorportativa(resp.length > 0 ? resp : null);
            setdataRows(resp.map((item, index) => (
              <tr key={'row-' + index}>
                <td data-label={t("type")} scope="row" width="30%">{item.name}</td>
                <td data-label={t("Variation")} scope="row" className="table-right">{formatCurrency('$', item.approvalRisk, 0)}</td>
                <td data-label={t("ProposedRisk")} scope="row" className="table-right">{formatCurrency('$', item.variationRisk, 0)}</td>
                <td data-label={t("Variation")} scope="row" className="table-right">{formatCurrency('$', item.proposalRisk, 0)}</td>
              </tr>
            )
            ));
          });
        }
      });

  }
  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <h5 className="card-title">{props.title}</h5>
          <p className="card-title-desc">
          </p>
          <div className="responsive-table scroll">
            <Table className="responsive-table">
              <thead className="table-light">
                <tr>
                  <th className="table-right">{t("type")}</th>
                  <th className="table-right">{t("ApprovedRisk")}</th>
                  <th className="table-right">{t("ProposedRisk")}</th>
                  <th className="table-right">{t("Variation")}</th>
                </tr>
              </thead>
              <tbody>
                {dataRows}
                <tr>
                  <th scope="row" width="30%">{t("Total")}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => (acu.approvalRisk ?? acu) + crr.approvalRisk), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => ((acu.variationRisk ?? acu) + crr.variationRisk)), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => ((acu.proposalRisk ?? acu) + crr.proposalRisk)), 0)}</th>
                </tr>
              </tbody>
            </Table>
          </div>
        </CardBody>
      </Card>
    </React.Fragment >
  );
};
ExposicionCorportativa.propTypes = {
  title: PropTypes.string,
  customerNumberT24: PropTypes.string,
};
export default ExposicionCorportativa;
