        setdataSet(dataSet);
      }
    })
  }, []);
  function handleSubmit(event, errors, values) {
    // if (propostalSectionRef.current.dataReturn.proposalTypeId == null) {
    //   dataValidation.tipoPropuestaRequerido = true;
    //   setdataValidation(dataValidation);
