                          validate={{
                            required: { value: true, errorMessage: t("Required") }
                          }}
                          type="textarea"
                          rows="4" />
                      </AvGroup></Col>
                  </Row>
                </Col>
              </Row>
              {/* InterestRateSection */}
              <Row className="mb-3">
