import React, { useState } from "react";
import PropTypes from "prop-types";
import { useTranslation } from "react-i18next";

import { Row, Col, Card, CardBody, Button, Label } from "reactstrap";
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation";
import ApiServicesCommon from "../../../../../services/Common/ApiServicesCommon";
import { CoreServices } from "../../../../../services";
import Select from "react-select";
import Switch from "react-switch";
import Breadcrumb from "../../../../../components/Common/Breadcrumb";
import { withTranslation } from "react-i18next";

const Offsymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      No
    </div>
  );
};

const OnSymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      Si
    </div>
  );
};

const PantallaBusqueda = (props) => {
  const [showResults, setShowResults] = useState(false);
  const [dataList, setDataList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [displayClienteModal, setDisplayClienteModal] = useState(false);

  return (
    <React.Fragment>
      <h5>{props.t("Datos Generales")}</h5>
      <p className="card-title-desc"></p>

      <AvForm id="frmGarantiasMuebles" className="needs-validation">
        <Row>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="NumerodeArrachment">
                {props.t("Numero de Adjunto ")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="searchfield"
                id="searchfield"
                title={props.t(
                  "Customer No., Customer Name, Identification No., No. Procedure"
                )}
              />
            </div>
          </Col>

          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Numerodeprestamo">
                {props.t("Numero de Prestamo o Desembolso")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="searchfield"
                id="searchfield"
                title={props.t(
                  "Customer No., Customer Name, Identification No., No. Procedure"
                )}
              />
            </div>
          </Col>

          <Col md="2">
            <div className="mb-2">
              <Label>{props.t("Moneda")}</Label>
              <Select />
            </div>
          </Col>

          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Numerodecliente">
                {props.t("Numero de cliente")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="searchfield"
                id="searchfield"
                title={props.t(
                  "Customer No., Customer Name, Identification No., No. Procedure"
                )}
              />
            </div>
          </Col>

          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoGarantia">
                {props.t("Monto de la Garantia")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="searchfield"
                id="searchfield"
                title={props.t(
                  "Customer No., Customer Name, Identification No., No. Procedure"
                )}
              />
            </div>
          </Col>

          <Col md="3">
            <div className="mb-3">
              <Label>{props.t("Tipos de Garantia")}</Label>
              <Select />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{props.t("Banco")}</Label>
              <Select />
            </div>
          </Col>
             <Row></Row>       
          <Col md="3">
            <div className="mb-3">
              <Label>{props.t("Agencia(Banca)")}</Label>
              <Select />
            </div>
          </Col>

          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="NoCuenta">
                {props.t("No. de Cuenta o Deposito")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="searchfield"
                id="searchfield"
                title={props.t(
                  "Customer No., Customer Name, Identification No., No. Procedure"
                )}
              />
            </div>
          </Col>
            <Row></Row>
          <Col md="3">
            <div className="mb-3">
              <Label>{props.t("Fecha de Expiracion")}</Label>
              <AvField
                className="form-control"
                name="estimatedDate"
                type="date"
                id="estimatedDate"
              />
            </div>
          </Col>
       
        </Row>
      </AvForm>
    </React.Fragment>
  );
};

export default withTranslation()(PantallaBusqueda);
