  function calculateSowData(){
    var sowApproved = {otherBank:0,banesco:0,total:0,sow:0}
    var sowProposal = {otherBank:0,banesco:0,total:0,sow:0}

    sowApproved.otherBank=0;
    sowApproved.banesco=0;

    sowProposal.otherBank=0;
    sowProposal.banesco=0;

    if(debtsT24!==null){

      //sowShort,sowLong
      for (var short of debtsT24.shortTermresult) {               
        if(short.bank.toLowerCase().indexOf('banesco') >= 0){
          sowApproved.banesco += Number(short.approvalRisk)
          sowProposal.banesco += Number(short.proposalRisk)
        }
        else{
          sowApproved.otherBank += Number(short.approvalRisk)
          sowProposal.otherBank += Number(short.proposalRisk)
        }
      }
    
      for (var long of debtsT24.longTermresult) {       
        if(long.bank.toLowerCase().indexOf('banesco') >= 0){
          sowApproved.banesco += Number(long.approvalRisk)
          sowProposal.banesco += Number(long.proposalRisk)
        }
        else{
          sowApproved.otherBank += Number(long.approvalRisk)
          sowProposal.otherBank += Number(long.proposalRisk)
        }
      }
    }
    
    /*
    sowApproved.otherBank += Number((dataReturn?.sumatoriaDeudaCorto?.monto ?? 0) + (dataReturn?.sumatoriaDeudaLargo?.monto ?? 0))
    sowProposal.otherBank += Number((dataReturn?.sumatoriaDeudaCorto?.saldo1 ?? 0) + (dataReturn?.sumatoriaDeudaLargo?.saldo1 ?? 0))
    */
    sowApproved.total = sowApproved.banesco + sowApproved.otherBank;
    sowProposal.total = sowProposal.banesco + sowProposal.otherBank;
    
    sowApproved.sow = sowApproved.total>0?Number(sowApproved.banesco/sowApproved.total * 100):0;     
    sowProposal.sow = sowProposal.total>0?Number(sowProposal.banesco/sowProposal.total * 100):0;
    
    console.log("calculateSowData",sowApproved,sowProposal)
    setsowApproved(sowApproved);
    setsowProposal(sowProposal);
  }

