import { useEffect, useState } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Card,
    Row,
    Col,
    Form,
    Container,
    Button
} from "react-bootstrap";
import Select from 'react-select'
import { AvForm } from 'availity-reactstrap-validation';

import FormContainerQuoter from './FormContainerQuoter'
import FormQuoterTypeNatural from './FormQuoterTypeNatural';
import FormDataQuoter from './FormDataQuoter';
import FormCommonFields from './FormCommonFields';
import TableResultQuoter from './TableResultQuoter';
import quoterCompuesto from "./dummy/quoterCompuesto";
import axios from 'axios';
import { BackendServices, CoreServices } from "../../../services";

const usePaymentPlan = () => {
    const [paymentPlan, setPaymentPlan] = useState(quoterCompuesto.payments)

    // setPaymentPlan(rowsData)
    console.log("paymentPlan:", paymentPlan)

}

const FormQuoterScreen = () => {
    const services = new BackendServices()
    // hooks
    const { t, i18n } = useTranslation();
    // custom hooks
    const [form, setForm] = useState({})
    const [disableSelect, setDisableSelect] = useState(true)
    const [typePerson, setTypePerson] = useState("Natural")
    const [paymentPlan, setPaymentPlan] = useState([])

    // handlers
    const handleChange = (evt, propertyName) => {

        const name = evt?.target
            ? evt.target.name
            : propertyName.name

        const value = evt?.target
            ? evt.target.value
            : evt.value

        if (propertyName?.name) {
            handleSelect(propertyName.name, value)
        }

        setForm({
            ...form,
            [name]: value
        })

    }

    const handleSelect = (propertyName, value) => {
        switch (propertyName) {
            case 'feciPercentage':
                const disableFeci = value == "Si"
                    ? false
                    : true
                setDisableSelect(disableFeci)
                return
            case 'personType':
                const type = value == "Natural"
                    ? "Natural"
                    : "Juridica"
                setTypePerson(type)
                return
            default:
                break;
        }
    }

    const handleFormSubmit = async () => {
        console.group("Handle form submit!")
        console.log("data submitted", form)
        const loanType = form.loanType == "compuesto"
            ? "compuesto"
            : "abono"
        const dataPaymentPlan = await services.getQuoterPaymentPlan(form.loanType, form.amountDebt, form.anualRate, form.feciRate, form.term)
        console.info("data payment plan", dataPaymentPlan.payments)
        console.groupEnd()

        setPaymentPlan(dataPaymentPlan.payments)
        
    }

    return (
        <>
            <FormContainerQuoter>
                <Card>
                    <Card.Body>
                        <Card.Title>
                            {t("ClientData")}
                        </Card.Title>
                        {/* <AvForm onSubmit={testHandleSubmit}> */}
                        <Row>
                            <FormCommonFields
                                onChange={handleChange}
                                form={form}
                                typePerson={typePerson}
                            />
                        </Row>
                        {/* <Row>
                            <FormQuoterTypeNatural
                                onChange={handleChange}
                                form={form}
                            />
                        </Row> */}
                        <Row>
                            <FormDataQuoter
                                onChange={handleChange}
                                form={form}
                                disableSelect={disableSelect}
                            />
                        </Row>
                        <Row >
                            <Col className='d-grid mt-5'>
                                <Button
                                    size='lg'
                                    className='float-end'
                                    type='submit'
                                    onClick={handleFormSubmit}
                                >
                                    {t("Calculate")}
                                </Button>
                            </Col>
                        </Row>
                        {/* </AvForm> */}
                    </Card.Body>
                </Card>
            </FormContainerQuoter>
            <TableResultQuoter
                paymentPlan={paymentPlan}
            />
        </>
    )
}

export default FormQuoterScreen
