import PropTypes from 'prop-types';
import { formatCurrency, translationHelper } from '../../../helpers';

import {
  Table,
} from "reactstrap"
import HeaderSections from '../../Common/HeaderSections';


const FacilityDetails = (props) => {

  console.log(props);
  const t = translationHelper('financial_report');

  // Items
  const rows = props.items?.map((item) => (
    <tr key={"row-facility-" + item.id}>
      <td>{item.debtor.name}</td>
      <td>{item.facility.name}</td>
      <td>{formatCurrency('$', 0, 0)}</td>
      <td>{item.status}</td>
      <td>{item.classification}</td>
      <td className="text-end">{formatCurrency('$', item.amount)}</td>
      <td className="text-end">{formatCurrency('$', item.balance)}</td>
      <td className="text-end">{formatCurrency('$', item.share)}</td>
      <td className="text-end">{formatCurrency('$', item.capital)}</td>
      <td>{item.interests}%</td>
      <td className="text-end">{formatCurrency('$', item.charges)}</td>
      <td>{item.refi1}</td>
      <td>{item.refi2}</td>
      <td>{item.currentStatus}</td>
      <td>{item.comments}</td>
    </tr>
  ));

  return (
    <div className="table-responsive">
      {/* <h5>{t("FacilityDetails.Title")}</h5> */}
      <HeaderSections title="FacilityDetails.Title" t={t}></HeaderSections>

      <div className="table-responsive styled-table-div">
        <Table className="table table-striped table-hover styled-table table mb-0">
          <thead>
            <tr>
              <th>{t("FacilityDetails.Debtor")}</th>
              <th>{t("FacilityDetails.Facility")}</th>
              <th>{t("FacilityDetails.Loan")}</th>
              <th>{t("FacilityDetails.Status")}</th>
              <th>{t("FacilityDetails.Classification")}</th>
              <th>{t("FacilityDetails.Amount")}</th>
              <th>{t("FacilityDetails.Balance")}</th>
              <th>{t("FacilityDetails.Share")}</th>
              <th>{t("FacilityDetails.Capital")}</th>
              <th>{t("FacilityDetails.Interests")}</th>
              <th>{t("FacilityDetails.Charges")}</th>
              <th>{t("FacilityDetails.CurrentCreditFacility")}</th>
              <th>{t("FacilityDetails.Refi1")}</th>
              <th>{t("FacilityDetails.Refi2")}</th>
              <th>{t("FacilityDetails.CurrentStatus")}</th>
              <th>{t("FacilityDetails.Comments")}</th>
            </tr>
          </thead>
          <tbody>
            {rows}
          </tbody>
        </Table>
      </div>
    </div>
  );

};


export default FacilityDetails;
