import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'

import ModalFlujoCaja from "./ModalFlujoCaja.js"
import ModalCargaTrabajo from "./ModalCargaTrabajo.js"
import { BackendServices } from "../../../../../services/index.js"
import SweetAlert from "react-bootstrap-sweetalert"

import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper"

const FlujoCaja = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  
  const { locationData } = props;
  
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [flujoCaja1Rows, setflujoCaja1Rows] = useState(null);
  const [flujoCaja2Rows, setflujoCaja2Rows] = useState(null);
  const [flujoCaja3Rows, setflujoCaja3Rows] = useState(null);
  const [flujoCaja4Rows, setflujoCaja4Rows] = useState(null);
  const [flujoCaja5Rows, setflujoCaja5Rows] = useState(null);
  const [flujoCaja6Rows, setflujoCaja6Rows] = useState(null);
  const [flujoCaja7Rows, setflujoCaja7Rows] = useState(null);
  const [cargaTrabajoRows, setcargaTrabajoRows] = useState(null);
  const [tipo, setTipo] = useState("");
  const [dataSet, setdataSet] = useState({
    transactId: 0,
    description: null,
    january: 0,
    february: 0,
    march: 0,
    april: 0,
    may: 0,
    june: 0,
    july: 0,
    august: 0,
    september: 0,
    october: 0,
    november: 0,
    december: 0
  });
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }
  }));

  const [showModalFlujoCaja, setShowModalFlujoCaja] = useState(false);
  const [showModalCargaTrabajo, setShowModalCargaTrabajo] = useState(false);
  const api = new BackendServices();
  React.useEffect(() => {
    // Read Api Servic e data 
    initializeData();
  }, []);

  function initializeData() {
    api.consultarFlujoCajaDollar(dataLocation.transactionId).then(resp => {
      if (resp.cashFlowDollarDTOList.length > 0) {
        setflujoCaja1Rows(resp.cashFlowDollarDTOList.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { verDatosCajaDolar(data, "DINERO") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { editarDatos(data, "EDINERO") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { EliminarDatos(data, "DDINERO") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja1Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaCobranzas(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setflujoCaja3Rows(resp.cashFlowCollection.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { verDatosCajaDolar(data, "COBRANZA") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { editarDatos(data, "ECOBRANZA") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { EliminarDatos(data, "DCOBRANZA") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja3Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaEgresos(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setflujoCaja4Rows(resp.cashFlowOutcome.map((data) => (
          data[14] ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => {
                  verDatosCajaDolar(data, "EGRESO")
                }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  editarDatos(data, "EEGRESO")
                }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => {
                  EliminarDatos(data, "DEGRESO")
                }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setflujoCaja4Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaIngresoFacturacion(dataLocation.transactionId).then(resp => {
      if (resp.cashFlowInvoiceIncome.length > 0) {
        setflujoCaja2Rows(resp.cashFlowInvoiceIncome.map((data) => (
          <tr key={data.id}>
            <td>{data.description}</td>
            <td>{data.january}</td>
            <td>{data.february}</td>
            <td>{data.march}</td>
            <td>{data.april}</td>
            <td>{data.may}</td>
            <td>{data.june}</td>
            <td>{data.july}</td>
            <td>{data.august}</td>
            <td>{data.september}</td>
            <td>{data.october}</td>
            <td>{data.november}</td>
            <td>{data.december}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { verDatosCajaDolar(data, "INGREFACTURA") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { editarDatos(data, "EINGREFACTURA") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { EliminarDatos(data, "DINGREFACTURA") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setflujoCaja2Rows(
          <tr key={1}>
            <td colSpan="12" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarFlujoCajaServicioDeudas(dataLocation.transactionId).then(resp => {
      if (resp.cashFlowDebtServiceDTOList.length > 0) {
        setflujoCaja7Rows(resp.cashFlowDebtServiceDTOList.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.january}</td>
              <td>{data.february}</td>
              <td>{data.march}</td>
              <td>{data.april}</td>
              <td>{data.may}</td>
              <td>{data.june}</td>
              <td>{data.july}</td>
              <td>{data.august}</td>
              <td>{data.september}</td>
              <td>{data.october}</td>
              <td>{data.november}</td>
              <td>{data.december}</td>
              <td style={{ textAlign: "right" }}>
