import React, { useEffect, useState, useRef } from "react"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"
import RiesgoAmbientalContext from './RiesgoAmbientalContext'
import * as OPTs from "../../../helpers/options_helper"
import {
  Row,
  Col,
  Button,
  Card,
  CardBody
} from "reactstrap"
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import SweetAlert from "react-bootstrap-sweetalert"
import LoadingOverlay from "react-loading-overlay"
import { BackendServices, CoreServices, BpmServices, } from "../../../services";

import { useTranslation, withTranslation } from "react-i18next"
import Analisis from "./riesgoCredito/Analisis"
import FormConclusiones from "./riesgoCredito/Conclusiones"

const RiesgoCreditoDetalle = (props) => {

  const [isActiveLoading, setIsActiveLoading] = useState(false); //loading de la pagina
  const history = useHistory();
  const location = useLocation();
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  
  const [estado, setestado] = useState(false)
  const [context, setContext] = useState({});
  
  const [locationData, setLocationData] = useState(null);
  const [modelo, setmodelo] = useState({
    dataAspectosAmbientales: {
      transactId: 0,
      riskPreClassification: null,
      sustainableCreditRating: "2",
      riskClassificationConfirmation: null,
      natureLocationProject: false,
      physicalResettlement: false,
      environmentalPermits: false,
      newProject: false,
      tipo: false,
      workersContractors: false
    },
    datosGenerales: {
      codigoTipoIdentificacion: null,
      numeroCliente: null,
      primerNombre: null,
      segundoNombre: null,
      primerApellido: null,
      segundoApellido: null,
      numeroIdentificacion: null,
      transactId: 0,
      economicGroup: {
        code: "",
        name: ""
      },
      economicActivity: {
        code: "",
        name: ""
      },
      subeconomicActivity: {
        code: "",
        name: ""
      },
      bank: {
        code: "",
        name: ""
      },
    }
  });
  
  //Servicios
  const [backendServices, setbackendServices] = useState(new BackendServices());
  const [bpmServices, setbpmServices] = useState(new BpmServices());
  //const [coreServices, setcoreServices] = useState(new CoreServices());

  const { t, i18n } = useTranslation();
  
  // Component Refs
  const formConclusionesRef = useRef();

  useEffect(() => {

    if (!location.data) {
      location.data = {
        "requestId": "XYZ892022",
        "facilityId": "",
        "customerId": "68",
        "transactionId": "110",
        "processId": "6",
        "activityId": "0",
        "instanceId": 9104,
        "taskId": 456,
        "taskStatus": "InProgress"
      }
    }

    // update context
    console.log('context', location.data);
    setContext({
      transactionId: location.data.transactionId,
      customerId: location.data.customerId,
      instanceId: location.data.instanceId,
      requestId: location.data.requestId,
    });

  }, []);


  function handleSubmit() {
    formConclusionesRef.current.submit();
  }

  function handleSave() {
    handleReturn();
  }

  function handleReturn() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                                      Avanzamos el proceso                                      */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BpmServices();
    let taskId = "";
    api.completedStatusTask(taskId).then(resp => {
      if (resp !== undefined) {
        setsuccessSave_dlg(true);
      }
      else {
        //Mensaje ERROR
        seterror_msg(props.t("ErrorSaveMessage"))
        seterror_dlg(true)
      }
    }).catch(err => {
      console.log(err);
    })
  }

  function handleClose() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BpmServices();
    // let instanceId = location.data.instanceId
    let instanceId = 0
    api.abortProcess(instanceId).then((data) => {
      if (data !== undefined) {
        history.push(url.URL_DASHBOARD);
      }
      else {
        //Mensaje ERROR
        seterror_msg(props.t("TheProcessCouldNotFinish"))
        seterror_dlg(true)
      }
    });
  }
  
  //"statuscredito":"completo"

  return (

    <React.Fragment>
      {locationData !== null && locationData !== undefined ?
        <RiesgoAmbientalContext.Provider value={context}>

          <div className="page-content">

          <Breadcrumbs title={t("Commercial Credit")} breadcrumbItem={t("Credit Risk")} />
            <LoadingOverlay active={isActiveLoading} spinner text={t("Processinginformation")}>

              <Card>
                <CardBody>
                  <Row>
                    <Col xl="12">
                    <Analisis locationData={loca}/>
                    </Col>
                  </Row>
                </CardBody>
              </Card>
              <Row>
                <Col xl="12">
                  <FormConclusiones ref={formConclusionesRef} locationData={locationData} />
                </Col>
              </Row>
            </LoadingOverlay>

            <Row className="my-2">
              <Col xl="12 text-end">
                <Button id="btnClose" color="dark" type="button" style={{ margin: '5px' }} onClick={handleClose}>
                  <i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i>
                  {" "} {c("Close")}
                </Button>
                <Button id="btnSubmit" color="success" type="button" style={{ margin: '5px' }}
                  onClick={() => {
                    submitData(); setIsActiveLoading(true); setTimeout(() => {
                      submitData();
                      setTimeout(() => {
                        submitData();
                      }, 1000);
                    }, 1000);
                  }}>
                  <i className="mdi mdi-content-save mdi-12px"></i>
                  {" "}{c("Save")}
                </Button>

              </Col>
            </Row>

          </div>
          {successSave_dlg ? (
            <SweetAlert
              success
              title={t("SuccessDialog")}
              onConfirm={() => {
                setsuccessSave_dlg(false)
              }}
            >
              {t("SuccessSaveMessage")}
            </SweetAlert>
          ) : null}

          {error_dlg ? (
            <SweetAlert
              error
              title={t("ErrorDialog")}
              onConfirm={() => {
                seterror_dlg(false)
              }}
            >
              {error_msg}
            </SweetAlert>
          ) : null}
        </RiesgoAmbientalContext.Provider>
        : null}
    </React.Fragment>
  );

}


export default (withTranslation()(RiesgoCreditoDetalle));
