    ////BORRAR EN BD 
    // data = { transactId: data.transactId, onBaseIdentification: data.onBaseIdentification };
    backendServices.eliminarCheckListTramite(attachmentFileInputModel.transactId,mainPerson.personId, data.id).then((resultBackend) => {

      if (resultBackend) {
        // SI TODO OK ACTUALIZAR LA LISTA DE ADJUNTOS
