import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"
import Select from "react-select";
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { CoreServices } from "../../../../../services";

const ModalInformacionProveedores = (props) => {
  const { t, i18n } = useTranslation();
  const [paises, setPaises] = useState([]);
  const [codigoPais, setCodigoPais] = useState(null);
  const [paisSelect, setpaisSelect] = useState(undefined);
  const [dataReturn, setDataReturn] = useState([]);
  const [campoRequeridoPais, setcampoRequeridoPais] = useState(false);
  React.useEffect(() => {
    // Read Api Service data
    var defaultVal = null;
    setpaisSelect(undefined)
    try {
        if (paises.length > 0 && props.jsonProveedores.country !== null && paisSelect === undefined) {
            defaultVal = paises.find(x => x.label === props.jsonProveedores.country);
            console.log(props.jsonProveedores);
            console.log(defaultVal);
            if (defaultVal !== undefined) {
                setpaisSelect(defaultVal);
            }
        }
    }
    catch (err) { }

}, [props,paises]);
  React.useEffect(() => {
    // Read Api Service data
    cargarPaises()
    setcampoRequeridoPais(false);
  }, []);
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    if (dataReturn.country == null || dataReturn.country == "") {
      setcampoRequeridoPais(true);
      return;
    } else {
      setcampoRequeridoPais(false);
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.country = dataReturn.country;
    if (props.tipoDato == "guardar") {
      props.dataManagament(values, props.tipoDato)
    } else {
      values.providerId = props.jsonProveedores.providerId;
      props.dataManagament(values, props.tipoDato)

    }
    // props.onSubmit(values);
  }
  function cargarPaises() {
    const api = new CoreServices();
    api.getPaisesCatalogo()
      .then(response => {
        if(response === null){ return;}
        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        const optionGroup1 = [
          {
            label: t("SelectCountry"),
            options: json,
          },

        ];
        setPaises(json);
      });
  }
  function changeAll(e, tipo) {

    if (tipo == "name") {
      dataReturn.name = e.target.value;
    }
    if (tipo == "country") {
      dataReturn.country = e.label;
    }
    if (tipo == "salePercentage") {
      dataReturn.salePercentage = e.target.value;
    }
    if (tipo == "customerType") {
      dataReturn.customerType = e.target.value;
    }
    if (tipo == "salesType") {
      dataReturn.salesType = e.target.value;
    }
    if (tipo == "termDays") {
      dataReturn.termDays = e.target.value;
    }
    if (tipo == "delayReason") {
      dataReturn.delayReason = e.target.value;
    }
    setDataReturn(dataReturn)
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("ProvidersInfo")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="name">{t("FullName")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="name"
                          value={props.jsonProveedores.name}
                          id="name"
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>
                    </Col>
                    <Col md="2">
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="idcountry">{t("Country")}</Label>
                        <Select
                          onChange={(e) => {
                            setpaisSelect(paises.find(x => x.label === e.label)); changeAll(e, "country");
                          }}
                          options={paises}
                          id="paises"
                          value={paisSelect}
                          classNamePrefix="select2-selection"
                          placeholder={t("SelectCountry")}
                        />
                        {campoRequeridoPais ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <AvGroup className="mb-3">
                        <Label htmlFor="ageofrelation">{t("AgeofRelationship")}</Label>
                        <AvField
                          className="form-control"
                          name="oldRelationship"
                          type="text"
                          value={props.jsonProveedores.oldRelationship}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="oldRelationship" />
                      </AvGroup>
                    </Col>
                    <Col md="3">
                      <AvGroup className="mb-3">
                        <Label htmlFor="buyPercent">{t("BuyPercent")}</Label>
                        <AvField
                          className="form-control"
                          name="percentPurchases"
                          type="number"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          value={props.jsonProveedores.percentPurchases}
                          id="percentPurchases" />
                      </AvGroup>
                    </Col>
                    <Col md="2"></Col>
                    <Col md="4">
                      <AvGroup className="mb-3">
                        <Label htmlFor="crtermindays">{t("Crtermindays")}</Label>
                        <AvField
                          className="form-control"
                          name="creditDays"
                          type="number"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          value={props.jsonProveedores.creditDays}
                          id="creditDays" />
                      </AvGroup>
                    </Col>

                  </Row>
                  <Row>
                    <Col md="12">
                      <div className="mb-3">
                        <Label htmlFor="specialTradingConditions">{t("SpecialTradingConditions")}</Label>

                        <AvField
                          className="form-control"
                          name="negotiationConditions"
                          type="textarea"
                          maxLength="1000"
                          rows="7"
                          value={props.jsonProveedores.negotiationConditions}
                          id="negotiationConditions" />
                      </div>
                    </Col>
                  </Row>

                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>

                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}>
                      <i className="mdi mdi-content-save mdi-12px"></i>
                      {" "}{t("Save")}
                    </Button> : null}
                </CardFooter>
