                        });
                    }
                    catch (err) { }
                })
            }
            else{
                var item =result.ConsultarAPCResponse.ConsultarAPCResult.DetCompromisoAPC.DetCompromisoAPC;
                try {
                    dataResult.push({
                        "year": moment(item.dc_fec_inicio_rel).format("YYYY"), "month": moment(item.dc_fec_inicio_rel).format("MMMM"),
