  function sumdisbursementTotalAmount() {
    let othersAmount1 = parseFloat(document.getElementById("othersAmount") ? document.getElementById("othersAmount").value : 0);
    let transferAmount1 = parseFloat(document.getElementById("transferAmount") ? document.getElementById("transferAmount").value : 0);
    let checkAmount1 = parseFloat(document.getElementById("checkAmount") ? document.getElementById("checkAmount").value : 0);
    let creditAccountAmount1 = parseFloat(document.getElementById("creditAccountAmount") ? document.getElementById("creditAccountAmount").value : 0);
    setdisbursementTotalAmount(((isNaN(+othersAmount1) ? 0 : +othersAmount1) ?? 0) + ((isNaN(+transferAmount1) ? 0 : +transferAmount1) ?? 0) + ((isNaN(+checkAmount1) ? 0 : +checkAmount1) ?? 0) + ((isNaN(+creditAccountAmount1) ? 0 : +creditAccountAmount1) ?? 0));
  }

