import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
    Row,
    Col,
    Button,
    Label,
    Input,
    Modal,
    Card,
    CardBody,
    CardFooter,
    InputGroup,
} from "reactstrap"
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
const ModalMatrizCompetitivaPosicionBanesco = (props) => {
    const { t, i18n } = useTranslation();
    // Submitimos formulario para busqueda de clientes
    function handleSubmit(event, errors, values) {
        event.preventDefault();
        if (errors.length > 0) {
            return;
        }        
        if (props.tipo == "posicionBanesco"||props.tipo == "productosTransaccionales") {
            props.guardarDatos(values, props.tipo);
        } else {
            props.actualizarDatos(values, props.tipo);
        }
    }
    return (
        <Modal
            size="xl"
            isOpen={props.isOpen}
            toggle={props.toggle}
            centered={true}>
            <div className="modal-header">
                <h5 className="modal-title mt-0">{props.tipo=="posicionBanesco" || props.tipo=="EposicionBanesco"?t("BanescoPosition"):t("BanescoTransactionalProducts")}</h5>
                <button
                    type="button"
                    onClick={props.toggle}
                    data-dismiss="modal"
                    className="close"
                    aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
                <Row>
                    <Col xl="12">
                        <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
                            <Card>
                                <CardBody>
                                    <Row>
                                        <Col md="6">
                                            <div className="mb-3">
                                                <Label htmlFor="quantity">{t("VolClosborder-colorar")}</Label>
                                                <AvField
                                                    className="form-control"
                                                    name="closeVol"
                                                    id="closeVol"
                                                    type="text"
                                                    value={props.tipo == "posicionBanesco" || props.tipo == "EposicionBanesco" || props.tipo == "productosTransaccionales" || props.tipo == "EproductosTransaccionales" ? props.jsonSow.closeVol : ""}
                                                    validate={{
                                                        required: { value: true, errorMessage: t("Required Field") },
                                                        number: { value: true, errorMessage: t("InvalidField") },
                                                    }}
                                                />
                                            </div>
                                        </Col>
                                        <Col md="6">
                                            <div className="mb-3">
                                                <Label htmlFor="quantity">{t("ParticipationBanesco")}</Label>
                                                <AvField
                                                    className="form-control"
                                                    name="participation"
                                                    id="participation"
                                                    type="text"
                                                    value={props.tipo == "posicionBanesco" || props.tipo == "EposicionBanesco" || props.tipo == "productosTransaccionales" || props.tipo == "EproductosTransaccionales" ? props.jsonSow.participation : ""}
                                                    validate={{
                                                        required: { value: true, errorMessage: t("Required Field") },
                                                        number: { value: true, errorMessage: t("InvalidField") },
                                                    }}
                                                />
                                            </div>
                                        </Col>
                                        <Col md="12">
                                            <div className="mb-3">
                                                <Label htmlFor="description">{t("Producto")}</Label>
                                                <AvField
                                                    className="form-control"
                                                    name="product"
                                                    id="product"
                                                    type="textarea"
                                                    rows="7"
                                                    value={props.tipo == "posicionBanesco" || props.tipo == "EposicionBanesco" || props.tipo == "productosTransaccionales" || props.tipo == "EproductosTransaccionales" ? props.jsonSow.product : ""}
                                                    errorMessage={t("Required Field")}
                                                    validate={{ required: { value: true } }}
                                                />
                                            </div>
                                        </Col>
                                    </Row>
                                </CardBody>
                                <CardFooter style={{ textAlign: "right" }}>
                                    <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                                        <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                                    </Button>
                                    {props.botones ?
                                        <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                                            {" "} {props.tipo == "posicionBanesco"||props.tipo == "productosTransaccionales" ? t("Save") : t("Edit")}
                                        </Button> : null}
                                </CardFooter>
                            </Card>
                        </AvForm>
                    </Col>
                </Row>
            </div>
        </Modal>
    );
};

ModalMatrizCompetitivaPosicionBanesco.propTypes = {
    isOpen: PropTypes.bool,
    toggle: PropTypes.func
};

export default ModalMatrizCompetitivaPosicionBanesco;
