import React, { useState } from "react"
import PropTypes from 'prop-types';
import { translationHelpers } from '../../../../helpers';
import { Link } from "react-router-dom"

import {
  Row,
  Col,
  Table,
} from "reactstrap"


import ModalNewSurety from "../ModalNewSurety";


const SuretiesList = (props) => {

  const [displayModal, setDisplayModal] = useState(false);

  function handleAdd() {
    setDisplayModal(true);
  }

  function toggleModal() {
    setDisplayModal(!displayModal);
  }

  if( !props.items ) {
    return null;
  }

  const [t, c] = translationHelpers('commercial_credit', 'common');

  const displayTable = props.items.length > 0;

  const dataRows = props.items.map((item, index) => {
    return (<tr key={ 'row-surety-' + index }>
      <td>{ item.type }</td>
      <td>{ item.description }</td>
      <td className="text-end">
        <Link to="#" onClick={ () => { props.onDeleteItem(item) } }>{ c("Delete") }</Link>
      </td>
    </tr>)
  });

  return (
    <React.Fragment>
      <h5>{ t("Sureties") }</h5>

      { displayTable ? (
      <Row className="mb-3">
        <Col md="12">
          <Table className="table" responsive>
            <thead className="table-light">
              <tr>
                <th>{ t("Surety Type") }</th>
                <th>{ t("Description") }</th>
                <th className="text-end">
                  <Link to="#" onClick={ handleAdd }>{ c("Add") }</Link>
                </th>
              </tr>
            </thead>
            <tbody>
              {dataRows}
            </tbody>
          </Table>
        </Col>
      </Row>
    ) : (
      <Row className="mb-3">
        <Col md="12">
          <Table className="table" responsive>
            <thead className="table-light">
              <tr>
                <th className="text-end">
                  <Link to="#" onClick={ handleAdd }>{ c("Add") }</Link>
                </th>
              </tr>
            </thead>
          </Table>
        </Col>
      </Row>
      ) }

      <ModalNewSurety isOpen={ displayModal } toggle={ toggleModal } onSave={ props.onSaveItem } />
      </React.Fragment>
  );

};


const SuretiesSection = (props) => {
  return (
    <SuretiesList items={ props.sureties } onSaveItem={ props.onSaveItem } onDeleteItem={ props.onDeleteItem } />
  );
};

SuretiesSection.propTypes = {
  sureties: PropTypes.array.isRequired,
  onSaveItem: PropTypes.func.isRequired,
  onDeleteItem: PropTypes.func.isRequired,
};

export default SuretiesSection;
