import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"
import {
  Row,
  Col,
  Button,
  Table,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import ModalCapex1 from "./ModalCapex1.js"
import ModalCapex2 from "./ModalCapex2.js"
import ModalCapex3 from "./ModalCapex3.js"
import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper";
import { AttachmentFileInputModel } from '../../../../../models/Common/AttachmentFileInputModel';
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services/index.js"
const Capex = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const { locationData } = props;
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [dataCapex1, setdataCapex1] = useState({
    "transactId": 0,
    "personId": 0,
    "capexId": 0,
    "observations": "",
    "thousandUse": 0,
    "shareholder": "",
    "back": "",
    "status": true
  });
  const [dataCapex2, setdataCapex2] = useState({
    "transactId": 0,
    "capexId": 0,
    "budget": "",
    "amount": 0,
    "status": true
  });
  const [dataCapex3, setdataCapex3] = useState({
    "transactId": 0,
    "capexDetailsId": 0,
    "observations": "",
    "amount": 0,
    "status": true
  });
  const [tipo, setTipo] = useState(null);
  const [formValid, setFormValid] = useState(false);
  const [dataCapex1Rows, setdataCapex1Rows] = useState(null);
  const [dataCapex2Rows, setdataCapex2Rows] = useState(null);
  const [dataCapex3Rows, setdataCapex3Rows] = useState(null);
  const [usuarioData, setusuarioData] = useState(null);
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }
  }));
  const [showModalCapex1, setShowModalCapex1] = useState(false);
  const [showModalCapex2, setShowModalCapex2] = useState(false);
  const [showModalCapex3, setShowModalCapex3] = useState(false);
  React.useEffect(() => {
    // Read Api Service data
    initializeData();
  }, []);
  const api = new BackendServices();
  function initializeData() {
    api.consultarListaCapexAC(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataCapex1Rows(resp.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.observations}</td>
              <td>{data.thousandUse}</td>
              <td>{data.shareholder}</td>
              <td>{data.back}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { updateData(data, "ECAPEX1") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { deleteData(data, "DCAPEX1") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setdataCapex1Rows(
          <tr key={1}>
            <td colSpan="4" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarListaCapexPresupuestoAC(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataCapex2Rows(resp.map((data) => (
          <tr key={data.capexId}>
            <td>{data.budget}</td>
            <td>{data.amount}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { updateData(data, "ECAPEX2") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { deleteData(data, "DCAPEX2") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setdataCapex2Rows(
          <tr key={1}>
            <td colSpan="2" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarListaCapexDetallesAC(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataCapex3Rows(resp.map((data) => (
          <tr key={data.capexDetailsId}>
            <td>{data.details}</td>
            <td>{data.amount}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { updateData(data, "ECAPEX3") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { deleteData(data, "DCAPEX3") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setdataCapex3Rows(
          <tr key={1}>
            <td colSpan="2" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    /* ---------------------------------------------------------------------------------------------- */
    /*                                   Consultar deudor principal                                   */
    /* ---------------------------------------------------------------------------------------------- */
     api.consultPrincipalDebtor(dataLocation.transactionId).then(resp => {
      setusuarioData(resp);
    })
  }
  /* ---------------------------------------------------------------------------------------------- */
  /*                                     actualizamos los datos                                     */
  /* ---------------------------------------------------------------------------------------------- */
  function updateData(data, tipo2) {
    setTipo(tipo2);
    if (tipo2 == "ECAPEX1") {
      setdataCapex1(data);
      toggleShowModalCapex1()
    }
    if (tipo2 == "ECAPEX2") {
      setdataCapex2(data);
      toggleShowModalCapex2()
    }
    if (tipo2 == "ECAPEX3") {
      setdataCapex3(data);
      toggleShowModalCapex3()
    }
    setbotonValidation(true);
  }
  function deleteData(data, tipo2) {
    setTipo(tipo2);
    if (tipo2 == "ECAPEX1") {
      setdataCapex1(data);
    }
    if (tipo2 == "ECAPEX2") {
      setdataCapex2(data);
    }
    if (tipo2 == "ECAPEX3") {
      setdataCapex3(data);
    }
    setconfirm_alert(true);
  }
  function SaveData(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    data.personId = usuarioData?.personId;
    /* ---------------------------------------------------------------------------------------------- */
    /*                                  Guardamos los datos del cash                                  */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "CAPEX1") {
      data.thousandUse = Number(data.useInMiles);
      data.observations = data.description;
      data.back = data.bank;
      // guardarListaCapexAC
      api.guardarListaCapexAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex1();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CAPEX2") {
      data.thousandUse = Number(data.useInMiles);
      data.observations = data.description;
      data.back = data.bank;
      // guardarListaCapexPresupuestoAC
      api.guardarListaCapexPresupuestoAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex2();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CAPEX3") {
      data.details = data.observations;
      // guardarListaCapexDetallesAC
      api.guardarListaCapexDetallesAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex3();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function UpdateData(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    data.personId = usuarioData?.personId;
    /* ---------------------------------------------------------------------------------------------- */
    /*                                    Editita el flujo de caja                                    */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "ECAPEX1") {
      data.thousandUse = Number(data.useInMiles);
      data.observations = data.description;
      data.back = data.bank;
      // actualizarListaCapexAC
      api.actualizarListaCapexAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex1();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECAPEX2") {
      data.thousandUse = Number(data.useInMiles);
      data.observations = data.description;
      data.back = data.bank;
      // actualizarListaCapexPresupuestoAC
      api.actualizarListaCapexPresupuestoAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex2();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECAPEX3") {
      data.details = data.observations;
      // actualizarListaCapexDetallesAC
      api.actualizarListaCapexDetallesAC(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex3();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalCapex1() {
    setShowModalCapex1(!showModalCapex1);
    removeBodyCss()
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalCapex2() {
    setShowModalCapex2(!showModalCapex2);
    removeBodyCss()
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalCapex3() {
    setShowModalCapex3(!showModalCapex3);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
  }
  return (
    <React.Fragment>
      <h5>
        {t("Capex")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Description")}</th>
                  <th>{t("UsesInMiles")}</th>
                  {/* <th>{t("SourcesThousands")}</th> */}
                  <th>{t("Shareholder")}</th>
                  <th>{t("Bank")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setdataCapex1({
                      "transactId": 1,
                      "personId": 1,
                      "capexId": 0,
                      "observations": "",
                      "thousandUse": 0,
                      "shareholder": "",
                      "back": "",
                      "status": true
                    }); setTipo("CAPEX1"); toggleShowModalCapex1()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataCapex1Rows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Budget")}</th>
                  <th>{t("Amount")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setdataCapex2({
                      "transactId": 0,
                      "capexId": 0,
                      "budget": "",
                      "amount": 0,
                      "status": true
                    }); setTipo("CAPEX2"); toggleShowModalCapex2()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataCapex2Rows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("ProjectDetails")}</th>
                  <th>{t("Amount")}</th>
                  <th> <Link className="float-end" onClick={() => {
                    setbotonValidation(true); setdataCapex3({
                      "transactId": 0,
                      "capexDetailsId": 0,
                      "observations": "",
                      "amount": 0,
                      "status": true
                    }); setTipo("CAPEX3"); toggleShowModalCapex3()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataCapex3Rows}
              </tbody>
            </Table>
          </Col>
        </Row>
        {locationData?
        <AttachmentFileCore attachmentFileInputModel={ new AttachmentFileInputModel(locationData.transactionId,OPTs.ACT_FLUJOOPERATIVO,OPTs.ACT_CUENTASPORCOBRAR)} />
        :null}
        
        <AttachmentFileCore locationData={{ instanceId: locationData.instanceId, processId: locationData.processId, activityId: OPTs.ACT_CAPEX }} />
      </AvForm>
      <ModalCapex1 SaveData={SaveData} botones={botonValidation} tipo={tipo} UpdateData={UpdateData} dataCapex1={dataCapex1} isOpen={showModalCapex1} toggle={() => { toggleShowModalCapex1() }} />
      <ModalCapex2 SaveData={SaveData} botones={botonValidation} tipo={tipo} UpdateData={UpdateData} dataCapex2={dataCapex2} isOpen={showModalCapex2} toggle={() => { toggleShowModalCapex2() }} />
      <ModalCapex3 SaveData={SaveData} botones={botonValidation} tipo={tipo} UpdateData={UpdateData} dataCapex3={dataCapex3} isOpen={showModalCapex3} toggle={() => { toggleShowModalCapex3() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            if (tipo == "DCAPEX1") {
              // eliminarListaCapexAC
              apiBack.eliminarListaCapexAC(dataLocation.transactionId, usuarioData?.personId, dataCapex1.capexId).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DCAPEX2") {
              // eliminarListaCapexPresupuestoAC
              apiBack.eliminarListaCapexPresupuestoAC(dataLocation.transactionId, usuarioData?.personId, dataCapex2.capexId).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DCAPEX3") {
              // eliminarListaCapexDetallesAC
              apiBack.eliminarListaCapexDetallesAC(dataLocation.transactionId, usuarioData?.personId, dataCapex3.capexDetailId).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );

});
Capex.propTypes = {
  locationData: PropTypes.any,
}
export default Capex;
