                  <div className="mb-3">
                    <Label htmlFor="Autonomia">
                      {t("Autonomy")}
                    </Label>
                    <AvField
                      className="form-control"
                      type="text"
                      name="autonomy"
                      id="autonomy"
                    />
                  </div>
                </Col> */}
                <Col md="6">
                  <div className="mb-2">
                    <Label>{t("Autonomía")}</Label>
                    <Select noOptionsMessage={() => ""}
                      showSearch
                      style={{ width: "100%" }}
                      placeholder={t("SearchtoSelect")}
                      optionFilterProp="children"
                      // defaultValue={props.dataCapex1.back}
                      onChange={(e) => { setAutonomySet(JSON.parse(e)) }}
                      filterOption={(input, option) =>
                        option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      {autonomy != null ?

                        autonomy.map((item, index) => (
                          <Option key={index} value={JSON.stringify(item)}>{item.Description}</Option>
                        ))
                        : null}
                    </Select>
                  </div>
                </Col>

                <Col md="6">
              <div className="mb-3">
                <Label htmlFor="autonomyUser">
                  {t("Usuario de Autonomía")}
                </Label>
                <AvField
                  className="form-control"
                  name="autonomyUser"
                  type="text"
                  id="autonomyUser"
                />
              </div>
            </Col>

              </Row>
              
            </Col>
          </Row>
          <Row>
                <Col md="12">
                  <div className="mb-3">
                    <Label htmlFor="additionalInfo">
                      {t("Additional Information")}
                    </Label>
                    <AvField
                      className="form-control"
                      type="textarea"
                      rows="3"
                      name="additionalInfo"
                      id="additionalInfo"
                    />
                  </div>
                </Col>
              </Row>
          <Row>
            <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
              <Link
                to={{
                  pathname: '/creditocomercial/garantias/',
                  data: { facilityId: dataLocation?.facilityId ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                }}
              >
                <Button
                  id="btnNew"
                  color="danger"
                  type="button"
                  style={{ margin: "5px" }}
                  onClick={props.toggle}
                  data-dismiss="modal"
                >
                  <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                  {t("Cancel")}
                </Button>
              </Link>
              <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
                <i className="mdi mdi-content-save mdi-12px"></i>
                {" "} {typeGarantia == "nuevo" ? t("Save") : t("Edit")}</Button>
            </Col>
          </Row>
        </AvForm>
      </LoadingOverlay>
      {
        successSave_dlg2 ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              setsuccessSave_dlg2(false)
              props?.cerrarModalLinea()
              // window.location.reload();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }
      {
        error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
      {
        error_dlgT24 ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              seterror_dlgT24(false)
            }}
          >
            {"La Línea no pudo ser creada en T24"}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );
};

