import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"
import {
  Row,
  Col,
  Button,
  Table,
} from "reactstrap"
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import ModalPrincipalesCompetidores from "./ModalPrincipalesCompetidores"
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"
const PrincipalesCompetidores = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const [formValid, setFormValid] = useState(false);
  const location = useLocation()
  const [dataReturn, setDataReturn] = useState(props.dataPrincipalesCompetidores);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [tipo, settipo] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dataDelete, setDataDelete] = useState([]);
  const [dataAccionistasDataRows, setdataAccionistasDataRows] = useState(null);
  const [jsonCompetidores, setjsonCompetidores] = useState({
    transactId: 0,
    name: "",
    description: ""
  });
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const dataAccionistas = [
    { id: 1, name: "Salvador Carbone", description: "Prueba de descrpcion 234" },
    { id: 2, name: "Salvador Carbone", description: "Prueba de descrpcion 234" },
    { id: 3, name: "Salvador Carbone", description: "Prueba de descrpcion 234" },
    { id: 4, name: "Salvador Carbone", description: "Prueba de descrpcion 234" },
  ];
  React.useEffect(() => {
    // Read Api Service data
    initializeData();
  }, []);
  function initializeData() {

    const apiBack = new BackendServices();
    // consultarCompetidores
    apiBack.consultCompetitors(dataLocation.transactionId).then(resp => {
      if (resp.competitors.length > 0) {
        dataReturn.dataAccionistas = resp.competitors;
        setDataReturn(dataReturn);
        setdataAccionistasDataRows(resp.competitors.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.name}</td>
              <td>{data.description}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { updateComptitors(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { deleteCompetitors(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setdataAccionistasDataRows(
          <tr key={1}>
            <td colSpan="2" style={{ textAlign: 'center' }}></td>
          </tr>);
      }
    });
  }
  function updateComptitors(data) {
    settipo("editar")
    setjsonCompetidores(data)
    setbotonValidation(true);
    toggleShowModelAttachment()
  }
  function dataManagament(data, tipo2) {
    const api = new BackendServices()
    data.transactId = Number(dataLocation.transactionId);
    if (tipo2 == "guardar") {
      api.newCompetitorIGR(data).then(resp => {
        initializeData();
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      data.competitorId = jsonCompetidores.competitorId;
      api.updateCompetitorIGR(data).then(resp => {
        initializeData();
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  function deleteCompetitors(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
  }
  return (
    <React.Fragment>
      <h5>
        {t("MainCompetitorsintheMarket")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmProveedores" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Name")}</th>
                  <th>{t("Description")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonCompetidores({
                      transactId: 0,
                      name: "",
                      description: ""
                    }); settipo("guardar"); toggleShowModelAttachment();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataAccionistasDataRows}
              </tbody>
            </Table>
          </Col>
        </Row>
      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            // eliminarCompetidorIGR
            apiBack.removeCompetitorIGR({ transactId: dataLocation.transactionId, competitorId: dataDelete.competitorId }).then(resp => {
              initializeData();
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalPrincipalesCompetidores tipo={tipo} botones={botonValidation} jsonCompetidores={jsonCompetidores} dataManagament={dataManagament} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );
});
/*
PrincipalesCompetidores.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
*/

export default PrincipalesCompetidores;
