import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"
import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  CardTitle,
  InputGroup,
  Table,
  CardFooter
} from "reactstrap"
import { AvForm, AvField } from "availity-reactstrap-validation"
import ModalDeudasCortoPlazo from "./ModalDeudasCortoPlazo"
import ModalDeudasLargoPlazo from "./ModalDeudasLargoPlazo"
import ModalSow from "./ModalSow"
//Import Flatepicker
import "flatpickr/dist/themes/material_blue.css";
import Flatpickr from "react-flatpickr";
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"
const shorttermdebts = [
  { id: 1, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo' },
  { id: 2, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo' },
  { id: 3, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo' },
  {
    id: 4, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000',
    debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', term: '365', destinationoffunds: 'Capital de Trabajo'
  },
];
const longtermdebts = [
  { id: 1, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo' },
  { id: 2, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo' },
  { id: 3, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000', debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo' },
  {
    id: 4, bank: "Scotiabank", facilitytype: 'LCR', amount: '1000', grantdate: '2018', duedate: 'Renovable 6 meses', debitbalance1: '600000',
    debitbalance2: '500', debitbalance3: '200', rate: '6.25%', bond: 'Fianza de Accionista', quota: '2000', destinationoffunds: 'Capital de Trabajo'
  },
];
const sowactual = [
  { id: 1, otherbank: "8050011.78", banesco: '1158000', total: '9208011.78', sow: '13%' },
];
const sowproposed = [
  { id: 1, otherbank: "8050011.78", banesco: '1158000', total: '9208011.78', sow: '13%' },
];
const estiloTotales = { backgroundColor: "lightgrey", color: "black" }
const RelacionesBancarias = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmRelacionesBancaria');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [dataReturn, setdataReturn] = useState(props.dataRelacionesBancarias);
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  /* ---------------------------------------------------------------------------------------------- */
  /*                          Variables de estados generales del componente                         */
  /* ---------------------------------------------------------------------------------------------- */
  const [formValid, setFormValid] = useState(false);
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [showModalDeudasCortoPlazo, setShowModalDeudasCortoPlazo] = useState(false);
  const [showModalDeudasLargoPlazo, setShowModalDeudasLargoPlazo] = useState(false);
  const [showModalSow, setShowModalSow] = useState(false);
  const [shorttermdebtsRows, setshorttermdebtsRows] = useState(null);
  const [longtermdebtsRows, setlongtermdebtsRows] = useState(null);
  const [sowactualRows, setsowactualRows] = useState(null);
  const [sowproposedRows, setsowproposedRows] = useState(null);
  const [tipo, setTipo] = useState("");
  const [dataSet, setdataSet] = useState({
    transactId: 0,
    otherBanks: 0,
    banesco: 0,
    total: 0,
    sow: 0
  });

  React.useEffect(() => {
    setdataReturn(props.dataRelacionesBancarias);
    initializeData();
  }, [props.dataRelacionesBancarias]);
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    // props.onSubmit(values);
  }
  function initializeData() {
    loadSowNow();
    uploadSowProposed();
    loadTablesShortTerm();
    loadLongTermTables();
  }
  function loadSowNow() {
    const api = new BackendServices()
