  function saveData(data, tipo) {
    data.transactId = dataLocation.transactionId;
    const api = new BackendServices()
    if (tipo == "Actual") {
      // nuevoSowActualIGR
      api.newSowCurrentIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalSow();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "Propuesto") {
      // nuevoSowPropuestoIGR
      api.newSowProposedIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalSow();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CP") {
      let datos = {
        "facilityType": data.facilityType,
        "amount": Number(data.amount),
        "date": data.expirationDate,
        "expirationDate": data.expirationDate,
        "debitBalance1": Number(data.debitBalance1),
        "debitBalance2": Number(data.debitBalance2),
        "debitBalance3": Number(data.debitBalance3),
        "rate": Number(data.rate),
        "fee": Number(data.fee),
        "bail": data.bail,
        "fundDestiny": data.fundDestiny,
        "bank": data.bank,
        "transactId": Number(data.transactId)
      }
      // nuevoRelacionesBancariasDeudasCP
      api.newBankingRelationsDebtsCP(datos).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalDeudasCortoPlazo()
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CL") {
      let datos = {
        "facilityType": data.facilityType,
        "amount": Number(data.amount),
        "date": data.expirationDate,
        "expirationDate": data.expirationDate,
        "debitBalance1": Number(data.debitBalance1),
        "debitBalance2": Number(data.debitBalance2),
        "debitBalance3": Number(data.debitBalance3),
        "rate": Number(data.rate),
        "fee": Number(data.fee),
        "bail": data.bail,
        "fundDestiny": data.fundDestiny,
        "bank": data.bank,
        "transactId": Number(data.transactId)
      }
      // nuevoRelacionesBancariasDeudasLP
      api.newBankingRelationsDebtsLP(datos).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalDeudasLargoPlazo()
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function updateData(data, tipo) {
    data.transactId = dataLocation.transactionId;
    const api = new BackendServices()
    if (tipo == "EditarActual") {
      // actualizarSowActualIGR
