      {
        confirm_alert ? (
          <SweetAlert
            title={t("Areyousure")}
            warning
            showCancel
            confirmButtonText={t("Yesdeleteit")}
            confirmBtnBsStyle="success"
            cancelBtnBsStyle="danger"
            onConfirm={() => {
              const apiBack = new BackendServices();
              if (tipo == "Actual") {
                // eliminarSowActualIGR
                apiBack.removeSowCurrentIGR({ transactId: dataLocation.transactionId, currentSowId: dataDelete.currentSowId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "Propuesto") {
                // eliminarSowPropuestoIGR
                apiBack.deleteSowProposedIGR({ transactId: dataLocation.transactionId, currentSowId: dataDelete.currentSowId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCP") {
                // eliminarRelacionesBancariasDeudasCP
                apiBack.eliminateBankingRelationshipsDebtsCP({ transactId: dataLocation.transactionId, debtId: dataDelete.debtId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCL") {
                // eliminarRelacionesBancariasDeudasLP
                apiBack.eliminateBankingRelationshipsDebtsLP({ transactId: dataLocation.transactionId, debtId: dataDelete.debtId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
            }}
            onCancel={() => setconfirm_alert(false)}
          >
            {t("Youwontbeabletorevertthis")}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );
});
RelacionesBancarias.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
export default RelacionesBancarias;
