import PropTypes from 'prop-types';
import { translationHelpers } from '../../helpers/translation-helper';

import {
  Table,
  Card,
  CardBody,
  Row,
  Col,
  Button
} from "reactstrap"
import React, { useState } from 'react';
import { BackendServices } from "../../services";
import { useLocation } from 'react-router-dom';
import ModalFinancialInformation from "../../pages/CommercialCredit/6_AnalisisCredito/ModalFinancialInformation"
import SweetAlert from "react-bootstrap-sweetalert"
const FinancialInformation = (props) => {
  React.useEffect(() => {
    LoadFinancialInformation();
  }, []);

  const location = useLocation()
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")


  const [financialInformation, setFinancialInformation] = useState(null);
  const [dataLocation, setData] = useState(location.data);
  const [ShowModelFinancialInformation, setShowModelFinancialInformation] = useState(false);
  const [DataUpdate, setDataUpdate] = useState(null);
  const [tipo, setTipo] = useState('guardar');

  var aligCell = { verticalAlign: "middle", textAlign: "center", };

  // setFinancialInformation({
  //   headers: ['Yarly Reyes (sep-19)', 'Yarly Reyes (sep-20)'],
  //   data: [
  //     { key: 'Participación', values: ['50%', '50%'] },
  //     { key: '- ACTIVOS', values: null },
  //     { key: 'Efectivos', values: ['$2400', '$3000'] },
  //     { key: 'Acciones', values: ['$10000', '$10000'] },
  //     { key: 'TOTAL ACTIVOS', values: ['$12400', '$13000'] },
  //     { key: '- PASIVOS', values: null },
  //     { key: 'Prestamos', values: ['$2800', '$2800'] },
  //     { key: '- PATRIMONIO', values: null },
  //     { key: 'Patrimonio Personal', values: ['$10000', '$10000'] },
  //     { key: '- TOTAL PASIVO Y PATRIMONIO', values: ['$12800', '$12800'] }
  //   ]
  // });

  const [t, c] = translationHelpers('commercial_credit', 'common');

  // const { headers, data } = financialInformation.data;


  // const dataRows = data.map((item, index) => (
  //   <tr key={'row-' + index}>
  //     <td>{item.key}</td>
  //     {item.values ? item.values.map(col => (
  //       <td>{col}</td>
  //     )) : <td colSpan="2">&nbsp;</td>}
  //   </tr>)
  // );

  function LoadFinancialInformation() {
    const api = new BackendServices()
    api.consultarInformiacionFinancieraFiadores(dataLocation.transactionId).then(data => {
      data && DrawInformation(data)
    });
  }

  function toggleShowModel() {
    setShowModelFinancialInformation(!ShowModelFinancialInformation);
  }

  function SendDataToServer(data) {
    console.log(data);
    data = { transactId: dataLocation.transactionId, debtorId: 2, ...data };
    if (tipo == 'guardar') {
      InserData(data);
    } else {
      UpdateData(data);
    }
  }

  function InserData(data) {
    const api = new BackendServices()
    api.nuevoInformacionFinancieraFiadores(data).then(resp => {
      LoadFinancialInformation();
      setsuccessSave_dlg(true);
    })
  }

  function UpdateData(data) {
    const api = new BackendServices()
    api.actualizarInformacionFinancieraFiadores(data).then(resp => {
      LoadFinancialInformation();
      setsuccessSave_dlg(true)
    })
  }

  function EditDataFiadores(data) {
    setDataUpdate(data);
    toggleShowModel()
  }

  function DeleteDataFiadores(data) {
    setconfirm_alert(true);
  }

  function DrawInformation(data) {
    setTipo('editar');
    let Cells = data?.map((item, index) => (
      <tr key={'row-' + index}>
        <td style={{ textAlign: "center" }}>{item.secondName} {item.firstName}</td>
        <td style={{ textAlign: "center" }}>{item.personType}</td>
        <td style={{ textAlign: "center" }}>{item.activeCash}</td>
        <td style={{ textAlign: "center" }}>{item.activeShares}</td>
        <td style={{ textAlign: "center" }}>{(item.activeCash + item.activeShares)}</td>
        <td style={{ textAlign: "center" }}>{item.passiveLoan}</td>
        <td style={{ textAlign: "center" }}>{item.passiveLoan}</td>
        <td style={{ textAlign: "center" }}>{item.personalWealth}</td>
        <td style={{ textAlign: "center" }}>{(item.passiveLoan + item.personalWealth)}</td>
        <td style={{ textAlign: "right" }}>
          <Button type="button" color="link" onClick={(resp) => { EditDataFiadores(item) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
          <Button type="button" color="link" onClick={(resp) => { DeleteDataFiadores(item) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
        </td>
      </tr>
    ))
    setFinancialInformation(Cells);
  }

  return (
    <Card>
      <CardBody>
        <h4 className="card-title">{t("Guarantors Financial Information")}
          <Button onClick={() => { toggleShowModel() }} id="btnNew" color="success" type="button" className="float-end" style={{ margin: '5px' }}>
            {c("New")}
          </Button>
        </h4>
        <p className="card-title-desc">{t("Natural and Legal Person")}</p>


        <Row>
          <Col lg="12">
            <div className="table-responsive">
              <Table className="table mb-0">
                <thead className="table-light">
                  <tr>
                    {/* <th>&nbsp;</th>
                    <th>{headres}</th> */}
                    <th rowSpan="2" style={aligCell}>{t("Client")}</th>
                    <th rowSpan="2" style={aligCell}>{t("Participation")}</th>
                    <th colSpan="2" style={aligCell}>{t("ASSETS")}</th>
                    <th rowSpan="2" style={aligCell}><b>{t("TOTALASSETS")}</b></th>
                    <th style={aligCell}>{t("PASSIVE")}</th>
                    <th rowSpan="2" style={aligCell}><b>{t("TOTALLIABILITIES")}</b></th>
                    <th style={aligCell}>{t("HERITAGE")}</th>
                    <th rowSpan="2" style={aligCell}><b>{t("TOTALLIABILITIESANDEQUITY")}</b></th>
                    <th rowSpan="2" style={aligCell}></th>
                  </tr>
                  <tr>
                    <th style={aligCell}>Efectivos</th>
                    <th style={aligCell}>Acciones</th>
                    <th style={aligCell}>Prestamos</th>
                    <th style={aligCell}>Patrimonio Personal</th>
                  </tr>
                </thead>
                <tbody>
                  {financialInformation}
                </tbody>
              </Table>
            </div>
          </Col>
        </Row>
      </CardBody>
      <ModalFinancialInformation isOpen={ShowModelFinancialInformation} toggle={() => { toggleShowModel() }} tipo={tipo} SendDataToServer={SendDataToServer} DataUpdate={DataUpdate} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            LoadFinancialInformation();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            LoadFinancialInformation();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarInformacionFinancieraFiadores(dataLocation.transactionId, DataUpdate.debtorId, DataUpdate.guarantorId).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </Card>
  );
};

FinancialInformation.propTypes = {
  products: PropTypes.array
};

export default FinancialInformation;
