import React, { useEffect, useState } from "react"
import { translationHelpers } from "../../../helpers"
import { Link } from "react-router-dom"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"

import {
  Row,
  Col,
  Button
} from "reactstrap"

import Breadcrumbs from "../../../components/Common/Breadcrumb"
import { FinancialSummary } from "../../../components/PropuestaCreditoComercial"
import {
  Background, Capex, CreditProposal, CreditPolicyReview,
  ExchangeRisk, FinancialInformation, FormCreditRisk,
  Projections, Workload, FormRecommendations, DocumentFooter
} from "../../../components/InformeFinanciero"
import AvForm from "availity-reactstrap-validation/lib/AvForm"

import { Tabs, Tab } from 'react-bootstrap';
import { BackendServices, CoreServices, BpmServices, } from "../../../services";


const FinancialReportPage = (props) => {

  const history = useHistory();
  const location = useLocation()

  const [proposal, setProposal] = useState(null);
  const [policies, setPolicies] = useState([]);
  const [financialSummary, setFinancialSummary] = useState(null);
  const [financialInformation, setFinancialInformation] = useState(null);
  const [capex, setCapex] = useState(null);
  const [background, setBackground] = useState(null);
  const [document, setDocument] = useState(null);

  const [dataLocation, setData] = useState(location.data);

  useEffect(() => {
    fetchFinancialReport();
  }, []);

  function fetchFinancialReport() {

    setProposal({
      products: [
        {
          id: 1,
          product: 'Línea de Crédito Rotativa',
          debtor: { name: 'Inversion Yarlys SA' },
          rate: 7.0,
          safi: '5-6: Riesgo Standard',
          currentApprovedRisk: 0,
          usedBalance: 0,
          proposedRisk: 126.0
        },
        {
          id: 2,
          product: 'Línea de Crédito Rotativa',
          debtor: { name: 'Inversion Yarlys SA' },
          rate: 7.0,
          safi: '5-6: Riesgo Standard',
          currentApprovedRisk: 0,
          usedBalance: 0,
          proposedRisk: 126.0
        }
      ]
    });

    setFinancialSummary({
      client: {
        name: 'Nombre Deudor'
      },
      financialStatus: {
        headers: ['Auditados Dic-18', 'Auditados Dic-19', 'Auditados Dic-20', 'Proyecciones Dic-21'],
        items: [
          {
            label: 'Clasificación SAFI',
            data: ['-', '-', '5:6 Riesgo', '-']
          },
          {
            label: 'Crecimiento',
            data: ['-', '-3.4%', '-12.5%', '0.0%']
          },
          {
            label: 'Costo de Ventas',
            data: ['(1550)66.2%', '(1510)66.8%', '(1360)68.8%', '(1360)68.8%']
          },
          {
            label: 'Beneficio Mutuo en Operaciones',
            data: ['(790)66.2%', '(751)66.8%', '(618)68.8%', '(618)68.8%']
          },
          {
            label: 'Margen Bruto',
            data: ['', '', '', '']
          }
        ]
      },
      balances: {
        active: {
          title: t('Active Balance'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        passive: {
          title: t('Passive Balance and Capital'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        application: {
          title: t('Origin and Application Status'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        indicators: {
          title: t('Indicators'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        }
      },
      debts: {
        short: {
          items: [
            {
              bank: 'Capital Bank',
              purpose: 'Linea de Crédito',
              approvedAmount: 200,
              balanceLastYear: 200,
              balanceLast6Months: 154,
              balanceForecast: 200,
              rate: 7.0,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'Linea de Crédito',
              approvedAmount: 320,
              balanceLastYear: 200,
              balanceLast6Months: 436,
              balanceForecast: 450,
              rate: 7.5,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'TDC',
              approvedAmount: 11,
              balanceLastYear: 11,
              balanceLast6Months: 0,
              balanceForecast: 11,
              rate: null,
              surety: ''
            }
          ]
        },
        long: {
          items: [
            {
              bank: 'Banesco',
              purpose: 'Préstamo',
              approvedAmount: 126,
              balanceLastYear: 0,
              balanceLast6Months: 0,
              balanceForecast: 126,
              rate: 6.5,
              surety: 'Bien Inmueble'
            }
          ]
        }
      },
      history: {
        headers: [
          t('# days', { days: 30 }),
          t('# days', { days: 60 }),
          t('# days', { days: 90 }),
          t('# days', { days: 120 }),
          t('# days', { days: 150 }),
          t('# days', { days: 180 }),
          t('# days', { days: 210 }),
          t('# days', { days: 240 }),
          t('# days', { days: 270 }),
          t('# days', { days: 300 }),
          t('# days', { days: 330 }),
          t('+# days', { days: 331 }),
          t('Totals')
        ],
        data: [
          {
            name: 'Banesco',
            data: [100, 80, 80, 80, 100, 80, 80, 80, 100, 80, 80, 200, 0]
          },
          {
            name: 'Banesco',
            data: [100, 80, 80, 80, 100, 80, 80, 80, 100, 80, 80, 200, 0]
          }
        ]
      }
    });

    setFinancialInformation({
      headers: ['Yarly Reyes (sep-19)', 'Yarly Reyes (sep-20)'],
      data: [
        { key: 'Participación', values: ['50%', '50%'] },
        { key: '- ACTIVOS', values: null },
        { key: 'Efectivos', values: ['$2400', '$3000'] },
        { key: 'Acciones', values: ['$10000', '$10000'] },
        { key: 'TOTAL ACTIVOS', values: ['$12400', '$13000'] },
        { key: '- PASIVOS', values: null },
        { key: 'Prestamos', values: ['$2800', '$2800'] },
        { key: '- PATRIMONIO', values: null },
        { key: 'Patrimonio Personal', values: ['$10000', '$10000'] },
        { key: '- TOTAL PASIVO Y PATRIMONIO', values: ['$12800', '$12800'] }
      ]
    });

    setCapex({
      sources: [
        { id: 10010, description: 'Presupuesto 1', usage: 0.4, shareholders: 0, bank: 0 },
        { id: 10020, description: 'Presupuesto 2', usage: 0.8, shareholders: 0, bank: 0 },
        { id: 10030, description: 'Presupuesto 3', usage: 1.2, shareholders: 0, bank: 0 },
        { id: 10040, description: 'Presupuesto 4', usage: 1.1, shareholders: 0, bank: 0 },
      ],
      budgets: [
        { id: 10010, description: 'Costo Directo de Construcción', value: 100_000 },
        { id: 10020, description: 'Costo Directo de Infraestructura', value: 120_000 },
      ],
      projects: [
        { id: 10010, description: 'Area de construcción total m2', value: 1000 },
        { id: 10020, description: 'Area de construcción infraestructura total m2', value: 1000 },
      ],
      attachments: []
    });

    setBackground({
      items: {
        headers: ['2012', '2013', '2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021'],
        data: [{
          id: 10010,
          description: 'Monto Aprobado Corto Plazo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10020,
          description: 'Desembolsos Corto Plazo (Utilizaciones)',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10030,
          description: 'Desembolsos Largo Plazo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10040,
          description: 'Consumo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10050,
          description: 'TOTAL Desembolsos',
          values: [200, 200, 0, 0, 0, 0, 0, 0, 2000, 4000]
        }, {
          id: 10060,
          description: 'Pagos Capital Corto Plazo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10070,
          description: 'Pagos Intereses Corto Plazo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10080,
          description: 'Amortización Capital Largo Plazo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10090,
          description: 'Amortización Intereses Largo Plazo',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10100,
          description: 'Consumo (Capital + Intereses)',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10110,
          description: 'TOTAL Pagos a Capital',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10120,
          description: 'TOTAL Pago a Intereses',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10130,
          description: 'Saldos LP + CP (al cierre)',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10140,
          description: 'Saldos Corto Plazo (al cierre)',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10150,
          description: 'Saldos Largo Plazo (al cierre)',
          values: [100, 100, 0, 0, 0, 0, 0, 0, 1000, 2000]
        }, {
          id: 10160,
          description: 'Refinanciamientos (#)',
          values: [1, 1, 0, 0, 0, 0, 0, 0, 1, 2]
        }, {
          id: 10170,
          description: 'Reestructuraciones (#)',
          values: [1, 1, 0, 0, 0, 0, 0, 0, 1, 2]
        }, {
          id: 10180,
          description: 'Clasificación de Riesgos',
          values: ['', '', '', '', '', '', '', '', '', '']
        }]
      },
      facilities: [
        {
          id: 22151,
          debtor: { id: 15050, name: 'Arturo Vidal' },
          facility: { id: 1090, name: 'Facilidad' },
          amount: 12_000,
          balance: 10_000,
          quota: 0,
          capital: 0,
          status: '',
          classification: '',
          interests: 2.5,
          charges: 0,
          refi1: '',
          refi2: '',
          currentStatus: '',
          comments: ''
        }
      ],
      restructuring: {
        headers: ['jun-16', 'nov-18', 'jun-20 (actual)'],
        data: [
          {
            description: 'Concepto',
            values: ['Reestructuración', '', '']
          },
          {
            description: 'Monto (Miles $US)',
            values: [5.142, '', '']
          },
          {
            description: 'Tasa',
            values: ['5%', '', '']
          },
          {
            description: 'Vencimiento',
            values: ['5 años', '', '']
          },
          {
            description: 'Solicitud',
            values: ['Reestructuración', '', '']
          },
          {
            description: 'Plan de Pagos',
            values: ['Primeros 2 años: $25,750 (Capital e Intereses), 3er año: $30,000 (Capital e Intereses), 4to año: $35,000 (Capital e intereses)', '', '']
          }
        ]

      },
      guarantees: {
        headers: ['jun-16', 'nov-18', 'jun-20 (actual)'],
        data: [
          {
            description: 'Empresa Avaluadora',
            values: ['AIR Avaluos', '', '']
          },
          {
            description: 'Valor de Venta Rápida (Miles $US)',
            values: [5.400, '', '']
          },
          {
            description: 'LTV',
            values: ['95%', '', '']
          }
        ]
      }
    });

    setDocument({
      date: '25-ago-2021',
      code: '###',
      owner: { id: 1010, name: 'Carolina Delgado' }
    });

  }

  function handleNewDebt(newDebt) {

  }

  function handleNewHistoryDetail(newHistoryDetail) {

  }

  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    props.onSubmit(values);
  }

  function handleSave() {

    history.push(url.URL_DASHBOARD);
  }

  const [n, t, c] = translationHelpers('navigation', 'commercial_credit', 'common');

  return (
    <div className="page-content">

      <Breadcrumbs title={n("Credit Analysis")} breadcrumbItem={n("Financial Report")} />

      {proposal && (
        <Row>
          <Col xl="12">
            <CreditProposal products={proposal.products} />
          </Col>
        </Row>
      )}

      <Row>
        <Col xl="12">
          <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example" className="mb-3">
            <Tab eventKey="home" title="Deudor 1">

            </Tab>
            <Tab eventKey="profile" title="Deudor 2">

            </Tab>
            <Tab eventKey="contact" title="Deudor 3">

            </Tab>
          </Tabs>
        </Col>
      </Row>

      {/* {policies.length > 0 && ( */}
      {(
        <Row>
          <Col xl="12">
            <CreditPolicyReview policies={policies} data={location.data} />
          </Col>
        </Row>
      )}

      {/* { financialSummary && (
        <FinancialSummary editMode={ true } client={ financialSummary.client }
          financialStatus={ financialSummary.financialStatus }
          balances={ financialSummary.balances }
          debts={ financialSummary.debts } addDebt={ handleNewDebt }
          history={ financialSummary.history } addHistoryDetail={ handleNewHistoryDetail } />
      )}

      { financialInformation && (
        <FinancialInformation data={ financialInformation } />
      )}  */}

      <ExchangeRisk />

      {/* { capex && (
      <Capex sources={ capex.sources }
        budgets={ capex.budgets }
        projects={ capex.projects } />
      )} */}

      <Workload />

      <Projections />

      {background && (
        <Background items={background.items}
          facilities={background.facilities}
          restructuring={background.restructuring}
          guarantees={background.guarantees} />
      )}

      <AvForm className="needs-validation" onSubmit={handleSubmit}>

        <FormCreditRisk />

        <FormRecommendations />

        {/*
        { document && (
        <Row className="my-2">
          <Col xl="6" className="offset-md-6">
            <DocumentFooter document={ document } />
          </Col>
        </Row>
        )}
        */}

        <Row className="my-2">
          <Col xl="12 text-end">
            <Link to={{ pathname: "/dashboard" }} className="btn btn-dark">
              {c("Cancel")}
            </Link>
            <Button id="btnSubmit" color="success" type="submit" style={{ margin: '5px' }}>
              {t("Generate Document")}
            </Button>
            <Button id="btnSave" color="success" type="button" style={{ margin: '5px' }} onClick={handleSave}>
              {c("Save")}
            </Button>
          </Col>
        </Row>

      </AvForm>

    </div>
  )
};

export default FinancialReportPage;
