import React, { useEffect, useState } from "react"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"

import {
  Row,
  Col,
  Button
} from "reactstrap"
import { Link } from "react-router-dom"

// UI Components
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import {
  ChangeSummary, CorporateExposure,CorporateExposureClient,
  FacilityList, FinancialSummary,
  GeneralSection
} from "../../../components/PropuestaCreditoComercial"

// Services
import { BackendServices, CoreServices, BpmServices, } from "../../../services";
import ApiServiceBackend from "../../../services/BackendServices/Services";
import {
  ServicioExposicionCorporativa,
} from "../../../services/PropuestaCredito";
import LoadingOverlay from "react-loading-overlay"

// Services
const coreServices = new CoreServices();
const backServices = new BackendServices();


const CommercialCreditProposalPage = (props) => {

  const history = useHistory();
  const location = useLocation();

  const [proposal, setProposal] = useState(null);
  const [exposicionCorporativa, setExposicionCorporativa] = useState([]);
  const [exposicionCorporativaClientes, setExposicionCorporativaClientes] = useState([]);
  const [changeSummaryItems, setChangeSummaryItems] = useState([]);
  const [facilityListItems, setFacilityListItems] = useState([]);
  const [identificationList, setIdentificationList] = useState([]);
  const [financialSummary, setFinancialSummary] = useState(null);
  const [prospect, setProspect] = useState({});
  const [isActiveLoading, setIsActiveLoading] = useState(true);


  // services
  const servicioExposicionCorporativa = new ServicioExposicionCorporativa();

  const [n, t, c, tr] = translationHelpers('navigation', 'commercial_credit', 'common', 'translation');

  // const customerNumberT24 =location.data.customerId;
  const customerNumberT24 = '600133774';

  useEffect(() => {
    getProspect();
  }, []);

  function fetchProposal() {

    setProposal({
      proposal: {
        id: 1010,
        revisionType: 'Bimensual',
        preapproved: false,
        minSib: 'Provision',
        relatedPart: false,
        approvalLevel: 'Nivel',
        flowType: 'Normal',
        applicationDate: '24-sep-2021',
        lastApplicationDate: '24-sep-2021',
        nextRevisionDate: '24-sep-2021',
        expirationDate: '24-sep-2021'
      },
      client: {
        number: '3223213',
      },
      economicGroup: {
        number: '32132006176',
        name: 'Grupo Tova SA',
        business: 'Actividad seleccionada'
      },
      risk: {
        countryClass: 'Panamá',
        classification: 'Medio',
        countryRisk: 'Nicaragua'
      },
      accountExecutive: {
        name: 'Elibeth'
      },
      responsibleUnit: {
        name: 'Unidad'
      }
    });

    servicioExposicionCorporativa.consultarExposicionCliente(customerNumberT24)
      .then((results) => {
        setExposicionCorporativaClientes(results);
      });

    servicioExposicionCorporativa.consultarExposicionCorporativa(customerNumberT24)
      .then((results) => {
        setExposicionCorporativa(results);
      });

    setChangeSummaryItems([
      { description: 'Incremento / Disminución (Exposición)' },
      { description: 'Plazos: Fac 1' },
      { description: 'Tasa: Fac 1' },
      { description: 'Cuotas' },
      { description: 'Garantías' },
      { description: 'Covenats' },
      { description: 'Fiadores / Codeudores' },
      { description: 'Otras condiciones' },
    ]);


    setFinancialSummary({
      client: {
        name: 'Nombre Deudor'
      },
      financialStatus: {
        headers: ['Auditados Dic-18', 'Auditados Dic-19', 'Auditados Dic-20', 'Proyecciones Dic-21'],
        items: [
          {
            label: 'Clasificación SAFI',
            data: ['-', '-', '5:6 Riesgo', '-']
          },
          {
            label: 'Crecimiento',
            data: ['-', '-3.4%', '-12.5%', '0.0%']
          },
          {
            label: 'Costo de Ventas',
            data: ['(1550)66.2%', '(1510)66.8%', '(1360)68.8%', '(1360)68.8%']
          },
          {
            label: 'Beneficio Mutuo en Operaciones',
            data: ['(790)66.2%', '(751)66.8%', '(618)68.8%', '(618)68.8%']
          },
          {
            label: 'Margen Bruto',
            data: ['', '', '', '']
          }
        ]
      },
      balances: {
        active: {
          title: t('Active Balance'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        passive: {
          title: t('Passive Balance and Capital'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        application: {
          title: t('Origin and Application Status'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        indicators: {
          title: t('Indicators'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        }
      },
      debts: {
        short: {
          items: [
            {
              bank: 'Capital Bank',
              purpose: 'Linea de Crédito',
              approvedAmount: 200,
              balanceLastYear: 200,
              balanceLast6Months: 154,
              balanceForecast: 200,
              rate: 7.0,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'Linea de Crédito',
              approvedAmount: 320,
              balanceLastYear: 200,
              balanceLast6Months: 436,
              balanceForecast: 450,
              rate: 7.5,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'TDC',
              approvedAmount: 11,
              balanceLastYear: 11,
              balanceLast6Months: 0,
              balanceForecast: 11,
              rate: null,
              surety: ''
            }
          ]
        },
        long: {
          items: [
            {
              bank: 'Banesco',
              purpose: 'Préstamo',
              approvedAmount: 126,
              balanceLastYear: 0,
              balanceLast6Months: 0,
              balanceForecast: 126,
              rate: 6.5,
              surety: 'Bien Inmueble'
            }
          ]
        }
      }
    });

    cargarIdentificaciones();

    setIsActiveLoading(false)
  }

  function cargarIdentificaciones() {

    return new Promise((resolve, reject) => {
      backServices.consultarCatalogoTipoIdentificacion()
        .then((data) => {
          if (data !== null && data !== undefined) {
            setIdentificationList(data);
          }
          resolve();
        })
        .catch((error) => {
          resolve();
          console.error('api error: ', error);
        });
    })
  }

  function handleReturn() {
    history.push(url.URL_DASHBOARD);
  }

  function handleClose() {
    history.push(url.URL_DASHBOARD);
  }

  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }

  function getProspect() {
    backServices.consultarUsuarioProspecto(location.data.transactionId).then(async (resp) => {
      console.log(resp);
      // customerNumberT24 = resp.customerNumberT24;
      setProspect(customerNumberT24);
      cargarListaFacilidades(customerNumberT24);
      fetchProposal();
    });
  }


  function cargarListaFacilidades(idcliente) {
    return new Promise(function (resolve, reject) {
      coreServices.getFacilidades(idcliente)
        .then(resp => {
          setFacilityListItems(resp);
          resolve();
        })
        .catch(err => {
          resolve();
          console.log(err);
        });
    })
  }

  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }

  return (
    <div className="page-content">

      <Breadcrumbs title={t("Commercial Credit")} breadcrumbItem={n("Credit Analysis")} />

      <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
        {proposal && (
          <>
            <Row>
              <Col xl="12">
                <GeneralSection proposal={proposal} prospect={prospect} />
              </Col>
            </Row>
            {exposicionCorporativa.length > 0 && (
              <Row>
                <Col xl="12">
                  <CorporateExposure prospect={prospect} title={t("CorporateExhibition")} customerNumberT24={"600133774"} />
                </Col>
              </Row>
            )}

            {exposicionCorporativaClientes.length > 0 && (
              <Row>
                <Col xl="12">
                  <CorporateExposureClient title={t("CorporateExhibitionClient")} customerNumberT24={"600133774"} />
                </Col>
              </Row>
            )}

            {changeSummaryItems.length > 0 && (
              <Row>
                <Col xl="12">
                  <ChangeSummary title={tr("SummaryOfChanges")} items={changeSummaryItems} />
                </Col>
              </Row>
            )}

            {facilityListItems.length > 0 && (
              <Row>
                <Col xl="12">
                  {identificationList.length > 0 ?
                    <FacilityList title={t("Facility List")}
                      items={facilityListItems}
                      editMode={true}
                      onSaveFacility={handleSaveFacility}
                      identificationList={identificationList} />
                    : null}
                </Col>
              </Row>
            )}

            {financialSummary && (
              <FinancialSummary client={financialSummary.client}
                financialStatus={financialSummary.financialStatus}
                balances={financialSummary.balances}
                debts={financialSummary.debts} />
            )}

            <Row className="my-2">
              <Col xl="12 text-end">
                <Button id="btnClose" color="dark" type="button" style={{ margin: '5px' }} onClick={handleClose}>
                  {c("Close")}
                </Button>
                <Button id="btnSave" color="success" type="button" style={{ margin: '5px' }} onClick={handleReturn}>
                  {c("Return")}
                </Button>
                <Link to={{ pathname: "/creditocomercial/analisiscredito/informefinanciero", data: location.data }} className="btn btn-success" style={{ margin: '5px' }}>{t("Financial Report")}</Link>

              </Col>
            </Row>
          </>
        )}
      </LoadingOverlay >

    </div>
  );

}


export default CommercialCreditProposalPage;
