import PropTypes from 'prop-types'
import { translationHelpers } from '../../../helpers/translation-helper';


import {
  Modal,
  ModalHeader,
  ModalBody,
  Label,
  Row,
  Col,
  Button
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"


const ModalNewSurety = (props) => {

  const [t, c] = translationHelpers('commercial_credit', 'common');

  function handleSubmit(event, errors, value) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    props.onSave(value);
    props.toggle();
  }

  function handleCancel() {
    props.toggle();
  }

  return (
    <Modal isOpen={ props.isOpen }
      toggle={ props.toggle }
      centered={true}
      size="md">
        <ModalHeader toggle={ props.toggle } color="primary">{ t("Sureties") }</ModalHeader>
        <ModalBody>
          <AvForm className="needs-validation" onSubmit={ handleSubmit }>
            <AvGroup className="mb-3">
              <Label htmlFor="type">{ t("Surety Type") }</Label>
              <AvField
                className="form-control"
                name="type"
                type="select"
                id="type">
                <option>Type 1</option>
                <option>Type 2</option>
                <option>Type 3</option>
              </AvField>
            </AvGroup>
            <AvGroup className="mb-3">
              <Label htmlFor="description">{ t("Description") }</Label>
              <AvField
                className="form-control"
                name="description"
                type="textarea"
                id="description"
                rows="4" />
            </AvGroup>
            <Row className="my-2">
              <Col xl="12 text-end">
                <Button id="btnCancel" color="dark" type="button" style={{ margin: '5px' }} onClick={ handleCancel }>
                  { c("Cancel") }
                </Button>
                <Button id="btnSave" color="success" type="submit" style={{ margin: '5px' }}>
                  { c("Save") }
                </Button>
                </Col>
            </Row>
          </AvForm>
        </ModalBody>
    </Modal>
  )
};

ModalNewSurety.propTypes = {
  onSave: PropTypes.func.isRequired,
  toggle: PropTypes.func,
  isOpen: PropTypes.bool
};


export default ModalNewSurety;
