/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types';
import { Link } from "react-router-dom"
import { useLocation, useHistory } from 'react-router-dom'
// import * as url from "../../../helpers/url_helper"

import {
  Card,
  CardBody,
  CardFooter,
  Button,
  Row,
  Col,
  Label,
  CardHeader,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"

//i18n
import { withTranslation } from "react-i18next"

import { BackendServices, CoreServices, BpmServices, } from "../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import ModalReportManagment from "../7_SupervisorAnalisisCredito/ModalSupervisor/ModalManagmentReport";
import ModalCreditProposal from "../7_SupervisorAnalisisCredito/ModalSupervisor/ModalCreditProposal";
import ModalFinancialReport from "../7_SupervisorAnalisisCredito/ModalSupervisor/ModalFinancialReport";
import ModalTermsOfConditions from "./ModalAceptacionCliente/ModalTermsOfConditions";
import ModalPrevicualizarIGR from "../4_InformeGestionReforzado/FormularioIGR/previsualizarIGR";
import ModalPrevisualizarPropCred from "../5_PropuestaCredito/previsualizarPropCred";

import Switch from "react-switch";
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { Select } from 'antd';

import { translationHelpers } from "../../../helpers"
import ModalPrevicualizarAIF from "../6_AnalisisCredito/previsualizarAIF";
import * as url from "../../../helpers/url_helper"


const Offsymbol = () => {
  const { Option } = Select;
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      No
    </div>
  );
};
const OnSymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      Si
    </div>
  );
};

const AutonomiaCredito = props => {

  const [t, c, tr] = translationHelpers('translation', 'common', 'translation');


  const history = useHistory();
  const location = useLocation()
  const [dataLocation, setdataLocation] = useState(location.data ?? JSON.parse(sessionStorage.getItem('locationData')));

  const [ModalPrevisualizardata, settoogleModalPrevisualizar] = useState(false);

  const [ShowModelReportManagment, setShowModelReportManagment] = useState(false);
  const [ShowModelCreditProposal, setShowModelCreditProposal] = useState(false);
  const [ShowModelFinancialReport, setShowModelFinancialReport] = useState(false);
  const [ShowModelTermsOfConditions, setShowModelTermsOfConditions] = useState(false);
  const [ModalPrevisualizardataProp, settoogleModalPrevisualizarProp] = useState(false);

  const [acceptClient, setacceptClient] = useState(false);
  const [CustomerAcceptance, setCustomerAcceptance] = useState(null);

  const apiServiceBackend = new BackendServices();


  const [locationData, setLocationData] = useState(null);
  const [personId, setpersonId] = useState(null);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);


  React.useEffect(() => {
    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      } else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    } else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = location.data;
      }
    }

    apiServiceBackend.consultPrincipalDebtor(dataSession.transactionId).then(resp => {
      setpersonId(resp.personId)
      apiServiceBackend.getCustomerAcceptance(dataSession.transactionId).then(resp => {
        console.log(resp);
        let data = resp?.customerAcceptance[0]
        setacceptClient(data.accept);
        setCustomerAcceptance(data);
      }).catch(err => {

      });
    }).catch(err => {

    });
  }, []);


  function toggleShowModelCreditProposal() {
    setShowModelCreditProposal(!ShowModelCreditProposal)
  }
  function toogleModalPrevisualizar() {
    settoogleModalPrevisualizar(!ModalPrevisualizardata);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  function toggleShowModelFinancialReport() {
    setShowModelFinancialReport(!ShowModelFinancialReport)
  }

  function toggleShowModelTermsOfConditions() {
    setShowModelTermsOfConditions(!ShowModelTermsOfConditions)
  }

  function toogleModalPrevisualizarProp() {
    settoogleModalPrevisualizarProp(!ModalPrevisualizardataProp);
    removeBodyCss()
  }

  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    let data = {
      transactId: +locationData.transactionId,
      personId: personId,
      accept: acceptClient,
      date: values.date
    }

    !CustomerAcceptance && apiServiceBackend.saveCustomerAcceptance(data).then(resp => {
      if (resp.statusCode == 200) {
        setsuccessSave_dlg(true)
      } else {
        seterror_dlg(true)
      }
    }).catch(err => {
      seterror_dlg(true)
    })

    CustomerAcceptance && apiServiceBackend.updateCustomerAcceptance(data).then(resp => {
      if (resp.statusCode == 200) {
        setsuccessSave_dlg(true)
      } else {
        seterror_dlg(true)
      }
    }).catch(err => {
      seterror_dlg(true)
    })
  }

  /*
  // Camino Feliz ... Acepta
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si"
  };
    
  //Terminar No Acepta ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"no"
  };

  //Terminar Sin Fideicomiso ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si",
    "fide":"no"
  };

  //Terminar Con Fideicomiso ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si",
    "fide":"si"
  };
  */


  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={t("CommercialCredit")} breadcrumbItem={t("ClientAcceptance")} />

        <Card>
        <CardHeader>
              <Row>
                <Col>
                  <h4 className="card-title">{t("ClientAcceptance")}</h4>
                  <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                </Col>
              </Row> 
              <Row>
                <Col sm={6}>
                  <h5 className="card-title">{mainDebtor != null ? (mainDebtor.typePerson === "1" ? (mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2) : (mainDebtor.name)): ""} </h5>
                </Col>
                <Col sm={6}>
                  <h5 className="card-title" style={{ textAlign: "right" }}>{t("Tramit")}:{" "}#{locationData?.transactionId}</h5>
                </Col>
              </Row>                           
            </CardHeader>
        
          <CardBody>


            <Row>
              <div className="d-flex flex-row justify-content-between">
                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toogleModalPrevisualizar() }}>
                  {" "} {t("ConsultManagementReport")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>

                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toogleModalPrevisualizarProp() }}>
                  {" "} {t("ConsultCreditProposal")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>

                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleShowModelFinancialReport() }}>
                  {" "} {t("ConsultFinancialReport")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>

                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleShowModelTermsOfConditions() }}>
                  {" "} {t("GenerateLetterOfTermsOfConditions")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>
              </div>
            </Row>


          </CardBody>
          <CardFooter style={{ textAlign: "right" }}>
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit} autoComplete="off">
              <Row>
                <Col md="3" className="d-flex flex-column justify-content-center">
                  <label class="kt-checkbox d-flex flex-row justify-content-start align-items-start">
                    <span>{t("ClientAccepts")}</span>
                    <AvGroup check className="d-flex flex-row justify-content-start align-items-start">
                      <Switch name="preapproval"
                        uncheckedIcon={<Offsymbol />}
                        checkedIcon={<OnSymbol />}
                        onColor="#007943"
                        className="form-label"
                        onChange={(e) => { setacceptClient(!acceptClient) }}
                        checked={acceptClient}
                      />
                    </AvGroup>
                  </label>
                </Col>
                <Col md="2">
                  <AvGroup >
                    <Label className="d-felx" style={{ display: "flex" }} htmlFor="requestDate">{t("Date")}</Label>
                    <AvField
                      className="form-control"
                      name="date"
                      type="date"
                      id="date"
                      value={CustomerAcceptance?.date ?? new Date()}
                    />
                  </AvGroup>
                </Col>
                <Col md="4">
                </Col>
                <Col md="3" className="d-flex flex-row align-items-center">
                  <Button id="btnSearch" color="negative" type="button" style={{ margin: '5px' }} onClick={() => { }}><i className="mdi mdi-call-missed mid-12px"></i>
                    {" "} {t("Refuse")}</Button>
                  <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>
                    {" "} {t("Advance")}</Button>
                </Col>
              </Row>
            </AvForm>

          </CardFooter>
        </Card>
      </div>
      {/* <ModalPrevisualizarPropCred isOpen={ModalPrevisualizardataProp} toggle={() => { toogleModalPrevisualizarProp() }} /> */}
      <ModalPrevicualizarAIF isOpen={ShowModelFinancialReport} toggle={() => { toggleShowModelFinancialReport() }} />
      <ModalTermsOfConditions isOpen={ShowModelTermsOfConditions} toggle={() => { toggleShowModelTermsOfConditions() }} />
      <ModalPrevicualizarIGR isOpen={ModalPrevisualizardata} toggle={() => { toogleModalPrevisualizar() }} />
     
      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)

          }}
        >
          { }
        </SweetAlert>
      ) : null}

      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  )
}

AutonomiaCredito.propTypes = {
  //    onSelectIdPropuesta: PropTypes.func.isRequired
  selectedIdPropuesta: PropTypes.any,
  // onSelectClient: PropTypes.func.isRequired
}

export default (withTranslation()(AutonomiaCredito))
