                  <div className="mb-3">
                    <Label htmlFor="activityType">{t("Typeofactivity")}</Label>
                    {activityTypeRender}
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="3">
                  <div className="mb-3">
