import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import Switch from "react-switch";
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'

import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper"

const Offsymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      No
    </div>
  );
};

const OnSymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      Si
    </div>
  );
};

const AspectosAmbientales = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();

  const { locationData } = props;
    
  const [formValid, setFormValid] = useState(false);
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmAspectosAmbientales');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  const [dataReturn, setDataReturn] = useState(props.dataAspectosAmbientales);
  const [switch1, setswitch1] = useState(props.dataAspectosAmbientales.natureLocationProject);
  const [switch2, setswitch2] = useState(props.dataAspectosAmbientales.physicalResettlement);
  const [switch3, setswitch3] = useState(props.dataAspectosAmbientales.environmentalPermits);
  const [switch4, setswitch4] = useState(props.dataAspectosAmbientales.newProject);
  const [switch5, setswitch5] = useState(props.dataAspectosAmbientales.workersContractors);

  // Form Submission
  function handleSubmit(event, errors, values) {
    dataReturn.natureLocationProject = switch1;
    dataReturn.physicalResettlement = switch2;
    dataReturn.environmentalPermits = switch3;
    dataReturn.newProject = switch4;
    dataReturn.workersContractors = switch5;
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    //props.onSubmit(values);
  }
 React.useEffect(() => {
  setDataReturn(props.dataAspectosAmbientales);
  setswitch1(props.dataAspectosAmbientales.natureLocationProject);
  setswitch2(props.dataAspectosAmbientales.physicalResettlement);
  setswitch3(props.dataAspectosAmbientales.environmentalPermits);
  setswitch4(props.dataAspectosAmbientales.newProject);
  setswitch5(props.dataAspectosAmbientales.workersContractors);
  }, [props]);
  function changeAll(e, tipo) {
    if (tipo == "riskPreClassification") {
      dataReturn.riskPreClassification = e.target.value;
    }
    if (tipo == "sustainableCreditRating") {
      dataReturn.sustainableCreditRating = "2";
    }
    if (tipo == "riskClassificationConfirmation") {
      dataReturn.riskClassificationConfirmation = e.target.value;
    }
    // if (tipo == "natureLocationProject") {
    //   dataReturn.natureLocationProject = switch1;
    // }
    // if (tipo == "physicalResettlement") {
    //   dataReturn.physicalResettlement = switch2;
    // }
    // if (tipo == "environmentalPermits") {
    //   dataReturn.environmentalPermits = switch3;
    // }
    // if (tipo == "newProject") {
    //   dataReturn.newProject = switch4;
    // }
    // if (tipo == "workersContractors") {
    //   dataReturn.workersContractors = switch5;
    // }
    setDataReturn(dataReturn);
  }
  return (
    <React.Fragment>
      <h5>
        {t("EnvironmentalAspects")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmAspectosAmbientales" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="4">
            <div className="mb-3">
              <Label htmlFor="preClassificationRisk">{t("PreClassificationRisk")}</Label>
              <AvField
                className="form-control"
                type="select"
                name="riskPreClassification"
                onChange={(e) => { changeAll(e, "riskPreClassification") }}
                value={dataReturn.riskPreClassification}
                validate={{
                  required: { value: true, errorMessage: t("Required Field") },
                }}
                id="riskPreClassification"
              >
                <option value="1">Bajo</option>
                <option value="2">Medio</option>
                <option value="3">Medio-Alto</option>
                <option value="4">Alto</option>
              </AvField>
            </div>
          </Col>
          {/*
            <Col md="4">
            <div className="mb-3">
              
                <Label htmlFor="sustainableCreditRating">{t("EnvironmentalRiskclassification")}</Label>
                <select name="sustainableCreditRating" id="sustainableCreditRating" className="form-control">
                  <option value="1">Opcion 1</option>
                  <option value="2">Opcion 2</option>
                  <option value="3">Opcion 3</option>
                  <option value="4">Opcion 4</option>
                </select> 
              </div>      
            </Col>
             */}
          <Col md="4">
            <div className="mb-3">
              <Label htmlFor="riskClassificationConfirmation">{t("EnvironmentalRiskclassification")}</Label>
              <AvField
                className="form-control"
                type="select"
                name="riskClassificationConfirmation"
                onChange={(e) => { changeAll(e, "riskClassificationConfirmation") }}
                value={dataReturn.riskClassificationConfirmation}
                validate={{
                  required: { value: true, errorMessage: t("Required Field") },
                }}
                id="riskClassificationConfirmation"
              >
                <option value="1">Bajo</option>
                <option value="2">Medio</option>
                <option value="3">Medio-Alto</option>
                <option value="4">Alto</option>
              </AvField>
            </div>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <AvGroup check className="mb-3">
              <Switch name="isProjectLocatedProtectedNaturalArea"
                uncheckedIcon={<Offsymbol />}
                checkedIcon={<OnSymbol />}
                onColor="#007943"
                className="form-label"
                onChange={() => {
                  setswitch1(!switch1);
                }}
                checked={switch1}
              />
              {'   '}
              <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label>
            </AvGroup>
          </Col>
          <Col md="12">
            <AvGroup check className="mb-3">
              <Switch name="theProjectInvolvePhysicalEconomicResettlement"
                uncheckedIcon={<Offsymbol />}
                checkedIcon={<OnSymbol />}
                onColor="#007943"
                className="form-label"
                onChange={() => {
                  setswitch2(!switch2);
                }}
                checked={switch2}
              />
              {'   '}
              <Label style={{ textAlign: "left" }} htmlFor="theProjectInvolvePhysicalEconomicResettlement">{t("Does the project involve physical and or economic resettlement of more than 100 people")}</Label>
            </AvGroup>
          </Col>
          <Col md="12">
            <AvGroup check className="mb-3">
              <Switch name="isPresentationEnvironmentalPermitsNecessary"
                uncheckedIcon={<Offsymbol />}
                checkedIcon={<OnSymbol />}
                onColor="#007943"
                className="form-label"
                onChange={() => {
                  setswitch3(!switch3);
                }}
                checked={switch3}
              />
              {'   '}
              <Label style={{ textAlign: "left" }} htmlFor="isPresentationEnvironmentalPermitsNecessary">{t("Is the presentation of environmental permits necessary for the execution of the project")}</Label>
            </AvGroup>
          </Col>
          <Col md="12">
            <AvGroup check className="mb-3">
              <Switch name="isProjectNew"
                uncheckedIcon={<Offsymbol />}
                checkedIcon={<OnSymbol />}
                onColor="#007943"
                className="form-label"
                onChange={() => {
                  setswitch4(!switch4);
                }}
                checked={switch4}
              />
              {'   '}
              <Label style={{ textAlign: "left" }} htmlFor="isProjectNew">{t("Is the project new and involves the use of more than 35 hectares of land")}</Label>
            </AvGroup>
          </Col>
          <Col md="12">
            <AvGroup check className="mb-3">
              <Switch name="doesActivityEmployMoreThan50Workers"
                uncheckedIcon={<Offsymbol />}
                checkedIcon={<OnSymbol />}
                onColor="#007943"
                className="form-label"
                onChange={() => {
                  setswitch5(!switch5);
                }}
                checked={switch5}
              />
              {'   '}
              <Label style={{ textAlign: "left" }} htmlFor="doesActivityEmployMoreThan50Workers">{t("Does the activity employ more than 50 workers and handle more than 15 contractors")}</Label>  {'  '}
            </AvGroup>
          </Col>
        </Row>
        <AttachmentFileCore locationData={{instanceId:locationData.instanceId,processId:locationData.processId,activityId:OPTs.ACT_ASPECTOSAMBIENTALES}} />
      </AvForm>
    </React.Fragment>
  );

});


AspectosAmbientales.propTypes = {
  onSubmit: PropTypes.func.isRequired,
  locationData: PropTypes.any,
}


export default AspectosAmbientales;
