          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Frecuebnciarevi">
                {t("Numero de Linea o Limite")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="lineId"
                id="lineId"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-2">
              <Label>{t("Moneda")}</Label>
              <Select
                showSearch
                style={{ width: "100%" }}
                placeholder={t("SearchtoSelect")}
                optionFilterProp="children"
                // defaultValue={props.dataCapex1.back}
                onChange={(e) => { setmonedasSet(e) }}
                filterOption={(input, option) =>
                  option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                {monedas != null ?

                  monedas.map((item, index) => (
                    <Option key={index} value={item.Description}>{item.Description}</Option>
                  ))
                  : null}
              </Select>
            </div>
          </Col>
          <Col md="3">
            <div className="mb-2">
              <Label>{t("Paies de Riesgo")}</Label>
              <Select
                showSearch
                style={{ width: "100%" }}
                placeholder={t("SearchtoSelect")}
                optionFilterProp="children"
                // defaultValue={props.dataCapex1.back}
                onChange={(e) => { setpaisesSet(e); changeCountry(e); }}
                filterOption={(input, option) =>
                  option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                {paises != null ?

                  paises.map((item, index) => (
                    <Option key={index} value={item.Description}>{item.Description}</Option>
                  ))
                  : null}
              </Select>
            </div>
          </Col>
          <Col md="3">
            <div className="mb-2">
              <Label>{t("Provincia")}</Label>
              <Select
                showSearch
                id="province"
                style={{ width: "100%" }}
                placeholder={t("SearchtoSelect")}
                optionFilterProp="children"
                // defaultValue={props.dataCapex1.back}
                onChange={(e) => { setprovinciasSet(e) }}
                filterOption={(input, option) =>
                  option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                {provincias != null ?
                  provincias.map((item, index) => (
                    <Option key={index} value={item.Description}>{item.Description}</Option>
                  ))
                  : null}
              </Select>
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Actividad Economica")}
              </Label>
              <Select
                showSearch
                style={{ width: "100%" }}
                placeholder={t("SearchtoSelect")}
                optionFilterProp="children"
                // defaultValue={props.dataCapex1.back}
                onChange={(e) => { setActividadEmpresaSet(e) }}
                filterOption={(input, option) =>
                  option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                {ActividadEmpresa != null ?
                  ActividadEmpresa.map((item, index) => (
                    <Option key={index} value={item.label}>{item.label}</Option>
                  ))
                  : null}
              </Select>
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Fecha de Propuesta")}</Label>
              <AvField
                className="form-control"
                name="proposalDate"
                type="date"
                id="proposalDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Fecha de Aprobacion")}</Label>
              <AvField
                className="form-control"
                name="approvalDate"
                type="date"
                id="approvalDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Fecha de Vencimiento")}</Label>
              <AvField
                className="form-control"
                name="dueDate"
                type="date"
                id="dueDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Fecha de Limite")}</Label>
              <AvField
                className="form-control"
                name="limitDate"
                type="date"
                id="limitDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Frecuencia de Revision")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="revisionFrequency"
                id="revisionFrequency"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Monto">{t("Monto")}</Label>
              <AvField
                className="form-control"
                type="text"
                name="amount"
                id="amount"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Totmaximo">{t("Total Maximo")}</Label>
              <AvField
                className="form-control"
                type="text"
                name="maxTotal"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
                id="maxTotal"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Monto Modificado")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
                name="modifiedAmount"
                id="modifiedAmount"
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="additionalInfo">
                {t("Informacion Adicional")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="additionalInfo"
                id="additionalInfo"
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Autonomia">
                {t("Autonomia")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="autonomy"
                id="autonomy"
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Contabiliza")}</Label>
              <AvGroup check className="mb-3">
                <Switch name="isProjectLocatedProtectedNaturalArea"
                  uncheckedIcon={<Offsymbol />}
                  checkedIcon={<OnSymbol />}
                  onColor="#007943"
                  id="check1"
                  className="form-label"
                  onChange={() => {
                    setswitch1(!switch1);
                  }}
                  checked={switch1}
                />
                {'   '}
                {/* <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label> */}
              </AvGroup>
            </div>
          </Col>
        </Row>
        <Row>
          <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
            <Link
              to={{
                pathname: '/creditocomercial/garantias/',
                data: { facilityId: dataLocation?.facilityId ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
              }}
            >
              <Button
                id="btnNew"
                color="dark"
                type="button"
                style={{ margin: "5px" }}
                onClick={props.toggle}
                data-dismiss="modal"
              >
                <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                {t("Cancel")}
              </Button>
            </Link>
            <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
              <i className="mdi mdi-content-save mdi-12px"></i>
              {" "} {typeGarantia == "nuevo" ? t("Save") : t("Edit")}</Button>
          </Col>
        </Row>
      </AvForm>
      {successSave_dlg2 ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          confirmButtonText= {t("Confirm")}
          cancelButtonText= {t("Cancel")}          
          onConfirm={() => {
            setsuccessSave_dlg2(false)
            window.location.reload();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}
      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          confirmButtonText= {t("Confirm")}
          cancelButtonText= {t("Cancel")}          
          onConfirm={() => {
            seterror_dlg(false)
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );
