import React, { useState } from 'react'
import { useTranslation } from "react-i18next";

import {
    Card,
    CardBody,
    CardTitle,
    Label,
    Row,
    Col,
    Container,
    Button,
    Table
} from 'reactstrap'
import Select from 'react-select'
import { AvField, AvForm } from 'availity-reactstrap-validation';
import useOpenModal from './Hooks/useOpenModal';
import ModalData from '../../../components/Common/ModalData';
import { CoreServices, BackendServices } from '../../../services'
import { useForm } from './Hooks/useForm';

const initialFormSearchClient = {
    idnumber: "",
    personType: "Natural",
}

const CardSearchClient = () => {
    // console.log("props", props.showModal)
    const { t, i18n } = useTranslation();
    const [clientToSearch, setClientToSearch] = useState({})
    const {
        form,
        dataClient,
        handleOnChange,
        handleOnSubmit,
        handleSearchClient,
    } = useForm(initialFormSearchClient)
    // const dataClient = useSearchClient(form)
    const [showModal, setShowModal] = useState(false);
    console.log("dataClient", dataClient.length);
    const options = [
        { label: 'Natural', value: 'Natural' },
        { label: 'Jurídica', value: 'Juridico' }
    ]
    const optionsIdType = [
        { value: '1', label: 'RUC' },
        { value: '2', label: 'RIF' },
        { value: '3', label: 'Cédula' },
    ]

    const handleClickShowModal = () => {
        setShowModal(true)
    }

    const handleClickCloseModal = () => {
        setShowModal(false)
    }

    const handleTest = () => {
    }

    return (
        <Container>
            <AvForm>
                {/* <AvForm onSubmit={handleSubmitSearchClient}> */}
                <Row>
                    <Col md={6} lg={3}>
                        <div className="">
                            <Label htmlFor="idnumber">{t("IdNumber")}</Label>
                            <AvField
                                id="idnumber"
                                name="idnumber"
                                options={optionsIdType}
                                value={form.idnumber}
                                validate={{ required: { value: true } }}
                                errorMessage={t("Required Field")}
                                onChange={handleOnChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3}>
                        <div className="mb-3">
                            <Label htmlFor="personType">{t("PersonType")}</Label>
                            <Select
                                id="personType"
                                name="personType"
                                options={options}
                                defaultValue={options[0]}
                                value={options.find(option => option.value === form.personType.value)}
                                onChange={handleOnChange}
                            />
                        </div>
                    </Col>
                    <Col md={6} lg={3} className="mt-4">
                        <div className="">
                            <Button
                                color="success"
                                type="submit"
                                className='mt-1'
                                onClick={handleSearchClient}
                            >
                                <i className="mdi mdi-file-find mdi-12px me-2"></i>
                                {t("Search")}
                            </Button>
                        </div>
                    </Col>
                    <Col md={6} lg={3} className="mt-4">
                        <div className="">
                            <Button
                                color="primary"
                                type="button"
                                className='mt-1 float-end'
                                onClick={handleClickShowModal}
                            >
                                <i className="mdi mdi-account-plus mdi-12px me-2"></i>
                                {t("NewClient")}
                            </Button>
                        </div>
                    </Col>
                </Row>
            </AvForm>
            {
                dataClient.length !== 0
                &&
                (
                    <div className="table-responsive styled-table-div mt-5">
                        <Table className="table table-striped table-hover styled-table table">
                            <thead>
                                <tr>
                                    <th>{t("Client Number")}</th>
                                    <th>{t("Client Name")}</th>
                                    <th>{t("ID Type")}</th>
                                    <th>{t("ID Number")}</th>
                                    <th>{t("Action")}</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    dataClient.map((data, key) => (
                                        <tr key={key}>
                                            <td>{data.id}</td>
                                            <td>{data.clientname}</td>
                                            <td>{data.idtype}</td>
                                            <td>{data.idnumber}</td>
                                            <td><i className="mdi mdi-check-box-multiple-outline mdi-24px"></i></td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                        </Table>
                    </div>
                )

            }
            <ModalData
                titleModal={t("Quoter")}
                show={showModal}
                toggle={handleClickCloseModal}
            />
        </Container >
    )
}

export default CardSearchClient
