import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalReciprocidad from "./ModalReciprocidad"
import { BackendServices } from "../../../../../services"
import SweetAlert from "react-bootstrap-sweetalert"
const dataReciprocity = [
  { id: 1, year: "2019", month: "enero", sales: '1500000', deposits: '1000000', averagebalance: "2000", reciprocity: "43%", sow: "12%" },
  { id: 2, year: "2019", month: "enero", sales: '1500000', deposits: '1000000', averagebalance: "2000", reciprocity: "43%", sow: "12%" },
  { id: 3, year: "2019", month: "enero", sales: '1500000', deposits: '1000000', averagebalance: "2000", reciprocity: "43%", sow: "12%" },
  { id: 4, year: "2019", month: "enero", sales: '1500000', deposits: '1000000', averagebalance: "2000", reciprocity: "43%", sow: "12%" },
];
const Reciprocidad = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()

  const [dataReturn, setdataReturn] = useState(props.dataReciprocidad);
  const [dataLocation, setData] = useState(location.data);
  /* -------------------------------------------------------- -------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [tipo, setTipo] = useState("")
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [dataDelete, setDataDelete] = useState([]);
  const [dataReciprocityRows, setdataReciprocityRows] = useState(null);
  const [dataRecipro, setdataRecipro] = useState({
    transactId: null,
    year: null,
    month: null,
    sales: null,
    deposits: null,
    averageBalance: null,
    reciprocity: null,
    sow: null
  });
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmReprocididad');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  const [showModalReciprocidad, setShowModalReciprocidad] = useState(false);

  React.useEffect(() => {
    // Read Api Serv ice ddasat a
    initializeData();
  }, []);
  function initializeData() {
    const api = new BackendServices();
    api.consultarListaReciprocidadIGR(dataLocation.transactionId).then(resp => {
      if (resp.reciprocity.length > 0) {
        dataReturn.dataTableReciprocidad = resp.reciprocity;
        setdataReturn(dataReturn);
        setdataReciprocityRows(resp.reciprocity.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.year}</td>
              <td>{data.month}</td>
              <td>{data.sales}</td>
              <td>{data.deposits}</td>
              <td>{data.averageBalance}</td>
              <td>{data.reciprocity}</td>
              <td>{data.sow}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { verDatosReciprocidad(data) }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { actualizarDatosReciprocidad(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { eliminarDatoRecriprocidad(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setdataReciprocityRows(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function gestionDatos(data, tipo) {
    const api = new BackendServices()
    data.transactId = dataLocation.transactionId;
    let datos = {
      "transactId": dataLocation.transactionId,
      "year": Number(data.year),
      "month": data.month,
      "sales": Number(data.sales),
      "deposits": Number(data.deposits),
      "averageBalance": Number(data.averageBalance),
      "reciprocity": Number(data.reciprocity),
      "sow": data.sow
    }
    if (tipo == "guardar") {
      api.nuevoReciprocidadIGR(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      api.actualizarReciprocidadIGR(data).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModalReciprocidad(!showModalReciprocidad);
    removeBodyCss()
  }
  function verDatosReciprocidad(data) {
    setdataRecipro(data)
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function actualizarDatosReciprocidad(data) {
    setdataRecipro(data);
    setTipo("editar");
    setbotonValidation(true);
    toggleShowModelAttachment();
  }
  function eliminarDatoRecriprocidad(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    //props.onSubmit(values); 
  }


  return (
    <React.Fragment>
      <h5>
        {t("Reciprocity")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmReprocididad" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Year")}</th>
                  <th>{t("Month")}</th>
                  <th>{t("Sales")}</th>
                  <th>{t("Deposits")}</th>
                  <th>{t("AverageBalance")}</th>
                  <th>{t("Reciprocity")}</th>
                  <th>{t("Sow")}</th>
                  <th>
                    <Link className="float-end" onClick={() => {
                      setbotonValidation(true); setdataRecipro({
                        transactId: null,
                        year: null,
                        month: null,
                        sales: null,
                        deposits: null,
                        averageBalance: null,
                        reciprocity: null,
                        sow: null
                      }); setTipo("guardar"); toggleShowModelAttachment();
                    }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th>
                </tr>
              </thead>
              <tbody>
                {dataReciprocityRows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDescription">{t("Description")}</Label>

              <AvField
                type="textarea"
                name="companyHistoryDetails"
                id="companyHistoryDetails"
                maxLength="1000"
                rows="7"
                value={props.dataReciprocidad.observations}
                validate={{
                  required: { value: true, errorMessage: t("Required Field") },
                }}
                onChange={(e) => { dataReturn.observations = e.target.value; setdataReturn(dataReturn) }}
              />
            </div>
          </Col>
        </Row>
      </AvForm>
      <ModalReciprocidad tipo={tipo} botones={botonValidation} dataRecipro={dataRecipro} gestionDatos={gestionDatos} isOpen={showModalReciprocidad} toggle={() => { toggleShowModelAttachment() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarReciprocidadIGR({ transactId: dataLocation.transactionId, reciprocityId: dataDelete.reciprocity_id }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );

});


Reciprocidad.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}


export default Reciprocidad;
