import PropTypes from 'prop-types';
import React, { useEffect, useState } from "react"
import { withTranslation } from "react-i18next"
import { Link } from "react-router-dom"

import {
  Table,
  Card,
  CardBody,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
  Button,
  Row,
  Col,
} from "reactstrap"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";

import * as url from "../../../../helpers/url_helper"
import { useLocation, useHistory } from 'react-router-dom'
import LoadingOverlay from 'react-loading-overlay';
import { translationHelpers } from "../../../../helpers"


const ResultadosBusqueda = (props) => {

  const [JSONCompare, setJSONCompare] = useState(props.JSONCompare);

  const history = useHistory();
  const location = useLocation()
  const [locationData, setLocationData] = useState(location.data);

  const [DataServer, setDataServer] = useState({
    rotary: false,
    commercialLoan: false,
    multipleRotary: false,
    rotaryNumber: false,
    overdraft: false,
    transfer: false,
    bail: false,
    trust: false,
    agroPawn: false,
    naturalBail: false,
    legalBail: false,
    crossedLegalBail: false,
    crossedNaturalBail: false,
    promiseLetter: false,
    privateRecord: false,
    trustRecord: false,
    loanGuarantees: false,
    fiduciaryLine: false,
    prefinanced: false,
  });

  const [comment, setcomment] = useState(null);
  const [dataRows, setdataRows] = useState(null);
  const [isActiveLoading, setIsActiveLoading] = useState(false);

  const [t, c, tr] = translationHelpers('translation', 'common', 'translation');

  const apiServiceBackend = new BackendServices();

  React.useEffect(() => {

    let dataSession;

    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      } else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    } else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');

      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }

    //setIsActiveLoading(true);

    /*Promise.allSettled([
      LoadDocLegal(dataSession),
      loadComment(),
    ]).then(resp => {

      const [{ value: DocLegal }, { value: Comment }] = resp;

      DocLegal && setDataServer(DocLegal);

      Comment && setcomment(Comment);
      setdataRows(drawRows(Comment, DocLegal));

      setIsActiveLoading(false);
    }).catch(err => {
      setIsActiveLoading(false);
    });
*/
  loadLegalDocs();
  }, []);

  function drawRows(dataServer, DocLegal) {
    return dataServer.map((data, index) => (
      <tr key={data.id}>
        <th scope="row"><input type="checkbox" checked={DocLegal[JSONCompare[index]]} name={JSONCompare[index]} onChange={(e) => { SenData(e.target) }} /></th>
        <td>{/*JSONCompare[index]*/}{capitalizarPrimeraLetra(data.documentName)}</td>
      </tr>)
    );
  }

  function capitalizarPrimeraLetra(str) {
    return str.charAt(0).toUpperCase() + str.slice(1);
  }

  /*function loadComment() {
    return new Promise((resolve, reject) => {
      apiServiceBackend.getLegalDocsFunction().then(data => {
        console.log("getLegalDocsFunction",data);
        resolve(data);
      })
    })
  }*/

  /*function LoadDocLegal(dataSession) {
    return new Promise((resolve, reject) => {
      apiServiceBackend.consultarDocumentacionLegal(dataSession.transactionId).then(data => {
        //resolve(data);
      })
    })
  }*/

  function loadLegalDocs(){
    apiServiceBackend.getLegalDocsFunction().then(result => {

      setdataRows( result.map((data, index) => (
        <tr key={data.id}>
          <th scope="row"><input type="checkbox" onChange={(e) => { SenData(e.target) }} /></th>
          <td>{/*JSONCompare[index]*/}{capitalizarPrimeraLetra(data.documentName)}</td>
        </tr>)
      ))
    })
  }

  async function SenData(data) {

    DataServer[data.name] = data.checked;

    let Datos = {
      transactId: locationData.transactionId,
      ...DataServer
    };

    !DataServer?.transactId && apiServiceBackend.nuevoDocumentacionLegal(Datos)

    DataServer?.transactId && apiServiceBackend.actualizarDocumentacionLegal(Datos)

    setDataServer(DataServer);
    setdataRows(drawRows(DataServer));

  }

  return (
    <React.Fragment>
      <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}></LoadingOverlay>
      <Row>
        <Col lg="12">
          <div className="table-responsive">
            <Table className="table mb-0">
              <thead className="table-light">
              </thead>
              <tbody>
                {dataRows}
              </tbody>
            </Table>
          </div>
        </Col>
      </Row>
    </React.Fragment>
  );
}

ResultadosBusqueda.propTypes = {
  // results: PropTypes.array.isRequired, //Resultado de la busqueda
}

export default (withTranslation()(ResultadosBusqueda));
