import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'

import ModalOtrosBancosActivosPasivos from "./ModalOtrosBancosActivosPasivos.js"
import ModalNegociosCompromisos from "./ModalNegociosCompromisos.js"
import ModalExpedientesOtros from "./ModalExpedientesOtros.js"
import ModalMatrizCompetitiva from "./ModalMatrizCompetitiva.js"
import { BackendServices } from "../../../../../services/index.js"
import SweetAlert from "react-bootstrap-sweetalert"
import ModalMatrizCompetitivaPosicionBanesco from "./ModalMatrizPosicionBanesco.js"
const estiloTotales = { backgroundColor: "lightgrey", color: "black" }
const posicionBanesco = [
  { id: 1, product: "Cuenta Corriente", vol: "5", participacion: "5", style: {} },
  { id: 2, product: "Cuenta Ahorro", vol: "5", participacion: "5", style: {} },
  { id: 3, product: "DPF", vol: "5", participacion: "5", style: {} },
  { id: 4, product: "Total", vol: "5", participacion: "5", style: {} },
  { id: 5, product: "Linea de Credito", vol: "5", participacion: "5", style: {} },
  { id: 6, product: "Prestamo LP", vol: "5", participacion: "5", style: {} },
  { id: 7, product: "Total Activo", vol: "5", participacion: "5", style: estiloTotales },
];
const productosTransaccionales = [
  { id: 1, product: "Transferencias", vol: "5", participacion: "5", style: {} },
  { id: 2, product: "Facturacion POS/MPos", vol: "5", participacion: "5", style: {} },
  { id: 3, product: "ECommerce", vol: "5", participacion: "5", style: {} },
  { id: 4, product: "TOTAL", vol: "5", participacion: "5", style: estiloTotales },
];
const matrizRentabilidad = [
  { id: 1, type: "Utilidad Antes de Impuesto", quantity: "5" },
  { id: 2, type: "Utilidad DespuÃ©s de Impuesto", quantity: "5" },
  { id: 3, type: "Retorno sobre volumen de Negocio", quantity: "5" },
  { id: 4, type: "Retorno sobre Prestamos", quantity: "5" }
];
const otrosBancosActivosPasivos = [
  { id: 1, bank: "Banitsmo", pasivo: "5", participacion: "5", largoplazo: "5", cortoplazo: "5", total: "5" },
  { id: 2, bank: "Bicsa", pasivo: "5", participacion: "5", largoplazo: "5", cortoplazo: "5", total: "5" },
  { id: 3, product: "Banesco", pasivo: "5", participacion: "5", largoplazo: "5", cortoplazo: "5", total: "5" }
];
const nuevosNegociosCompromisos = [
  { id: 1, tipo: "TDC" },
  { id: 2, tipo: "Punto de Ventas" },
  { id: 3, tipo: "Depositos a Plazos" },
];
const expedientesOtros = [
  { id: 1, tipo: "Activos" },
  { id: 2, tipo: "Pasios" },
  { id: 3, tipo: "APC" },
  { id: 4, tipo: "Ultima Visita" },
];
const MatrizCompetitiva = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataReturn, setdataReturn] = useState({});
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [dataSet, setdataSet] = useState({
    "transactId": 0,
    "bank": "",
    "passivePercentage": 0,
    "participation": 0,
    "longTerm": 0,
    "shortTerm": 0
  });
  const [tipo, settipo] = useState("");
  const [posicionBanescoRows, setposicionBanescoRows] = useState(null);
  const [productosTransaccionalesRows, setproductosTransaccionalesRows] = useState(null);
  const [matrizRentabilidadRows, setmatrizRentabilidadRows] = useState(null);
  const [otrosBancosActivosPasivosRows, setotrosBancosActivosPasivosRows] = useState(null);
  const [nuevosNegociosCompromisosRows, setnuevosNegociosCompromisosRows] = useState(null);
  const [expedientesOtrosRows, setexpedientesOtrosRows] = useState(null);
  const [monto, setmonto] = useState(0);
  const [participacion, setparticipacion] = useState(0);
  const [montoTransacciones, setmontoTransacciones] = useState(0);
  const [participacionTransaccion, setparticipacionTransaccion] = useState(0);

  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmMatrizCompetitiva');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  const [showModalOtrosBancosActivosPasivos, setShowModalOtrosBancosActivosPasivos] = useState(false);
  const [showModalNegociosCompromisos, setShowModalNegociosCompromisos] = useState(false);
  const [showModalExpedientesOtros, setShowModalExpedientesOtros] = useState(false);
  const [showModalMatrizCompetitiva, setShowModalMatrizCompetitiva] = useState(false);
  const [showModalMatrizCompetitivaPosicionBanesco, setShowModalMatrizCompetitivaPosicionBanesco] = useState(false);
  const api = new BackendServices();
  React.useEffect(() => {
    // Read Api Service  data
    initializeData();
  }, []);
  function initializeData() {
    api.consultarMatrizPosicionBanescoIGR(dataLocation.transactionId).then(resp => {
      if (resp.length > 0) {

        setposicionBanescoRows(resp.map((data) => (
          <tr key={data.id}>
            <td>{data.product}</td>
            <td>{data.closeVol}</td>
            <td>{data.participation}</td>
            <td style={{ textAlign: "right" }}>
              
              <Button type="button" color="link" onClick={(resp) => {
                settipo("EposicionBanesco"); editarPosicionTransacciones(data)
              }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => {
                settipo("DposicionBanesco"); eliminarDato(data)
              }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
        let monto1 = 0;
        let participacion1 = 0;
        for (let i = 0; i < resp.length; i++) {
          monto1 = parseFloat(monto1) + parseFloat(resp[i].closeVol);
          participacion1 = parseFloat(participacion1) + parseFloat(resp[i].participation);
        }
        setmonto(monto1);
        setparticipacion(participacion1);

      } else {
        setposicionBanescoRows(
          <tr key={1}>
            <td colSpan="3" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarMatrizTransaccionBanescoIGR(dataLocation.transactionId).then(resp => {
      if (resp.length > 0) {
        let monto1 = 0;
        let participacion1 = 0;
        for (let i = 0; i < resp.length; i++) {
          monto1 = parseFloat(monto1) + parseFloat(resp[i].closeVol);
          participacion1 = parseFloat(participacion1) + parseFloat(resp[i].participation);
        }
        setmontoTransacciones(monto1);
        setparticipacionTransaccion(participacion1);
        setproductosTransaccionalesRows(resp.map((data) => (
          <tr key={data.id}>
            <td>{data.product}</td>
            <td>{data.closeVol}</td>
            <td>{data.participation}</td>
            <td style={{ textAlign: "right" }}>
             
              <Button type="button" color="link" onClick={(resp) => {
                settipo("EproductosTransaccionales"); editarPosicionTransacciones(data)
              }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => {
                settipo("DproductosTransaccionales"); eliminarDato(data)
              }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setproductosTransaccionalesRows(
          <tr key={1}>
            <td colSpan="3" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarMatrizOtrosBancosIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setotrosBancosActivosPasivosRows(resp.competitiveMatrixOtherBanks.map((data) => (
          <tr key={data.id}>
            <td>{data.bank}</td>
            <td>{data.passivePercentage}</td>
            <td>{data.participation}</td>
            <td>{data.longTerm}</td>
            <td>{data.shortTerm}</td>
            <td>{parseFloat(data.bank) + parseFloat(data.passivePercentage) + parseFloat(data.participation) + parseFloat(data.longTerm) + parseFloat(data.shortTerm)}</td>
            <td style={{ textAlign: "right" }}>
              
              <Button type="button" color="link" onClick={(resp) => {
                settipo("EOTROBANCO"); editarPosisionBanco(data)
              }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => {
                settipo("DOTROBANCO"); eliminarDato(data)
              }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setotrosBancosActivosPasivosRows(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarMatrizNuevosNegociosIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setnuevosNegociosCompromisosRows(resp.newBusiness.map((data) => (
          <tr key={data.id}>
            <td>{data.observations}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarNegociosCompromisos(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { eliminarDato(data); settipo("DNUEVONEGOCIO"); }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setnuevosNegociosCompromisosRows(
          <tr key={1}>
            <td colSpan="1" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarMatrizExpedientesIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setexpedientesOtrosRows(resp.files.map((data) => (
          <tr key={data.id}>
            <td>{data.observations}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarExpedientesOtros(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { eliminarDato(data); settipo("DEXPEDIENTES") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setexpedientesOtrosRows(
          <tr key={1}>
            <td colSpan="1" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarMatrizRentabilidadIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setmatrizRentabilidadRows(resp.profitability.map((data) => (
          <tr key={data.id}>
            <td>{data.matrixType.code}</td>
            <td>{data.quantity}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarMatrixCompetitiva(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { eliminarDato(data); settipo("DRENTABILIDAD") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setmatrizRentabilidadRows(
          <tr key={1}>
            <td colSpan="2" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function guardarDatos(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    const api = new BackendServices()
    /* ---------------------------------------------------------------------------------------------- */
    /*                                  Guardamos los datos del cash                                  */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "OTROBANCO") {
      api.nuevoMatrizOtrosBancosIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalOtrosBancosActivosPasivos();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "NUEVONEGOCIO") {
      api.nuevoMatrizNuevosNegociosIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalNegociosCompromisos();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "EXPEDIENTES") {
      api.nuevoMatrizExpedientesIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalExpedientesOtros();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "RENTABILIDAD") {
      api.nuevoMatrizRentabilidadIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalMatrizCompetitiva();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "posicionBanesco") {
      api.nuevoMatrizPosicionBanescoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalMatrizCompetitivaPosicionBanesco();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "productosTransaccionales") {
      api.nuevoMatrizTransaccionBanescoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalMatrizCompetitivaPosicionBanesco();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function actualizarDatos(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    const api = new BackendServices()
    /* ---------------------------------------------------------------------------------------------- */
    /*                                    Editita el flujo de caja                                    */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "EOTROBANCO") {
      api.actualizarMatrizOtrosBancosIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalOtrosBancosActivosPasivos();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ENUEVONEGOCIO") {
      api.actualizarMatrizNuevosNegociosIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalNegociosCompromisos();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "EEXPEDIENTES") {
      api.actualizarMatrizExpedientesIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalExpedientesOtros();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ERENTABILIDAD") {
      api.actualizarMatrizRentabilidadIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalMatrizCompetitiva();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "EposicionBanesco") {
      data.positionId = dataSet.positionId;
      api.actualizarMatrizPosicionBanescoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalMatrizCompetitivaPosicionBanesco();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "EproductosTransaccionales") {
      data.transactionalId = dataSet.transactionalId;
      api.actualizarMatrizTransaccionBanescoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalMatrizCompetitivaPosicionBanesco();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function verPosicionTransacciones(data) {
    setdataSet(data)
    setbotonValidation(false);
    toggleShowModalMatrizCompetitivaPosicionBanesco()
  }
  function editarPosicionTransacciones(data) {
    setdataSet(data)
    setbotonValidation(true);
    toggleShowModalMatrizCompetitivaPosicionBanesco()
  }
  function verPosisionBanco(data) {
    setdataSet(data)
    setbotonValidation(false);
    toggleShowModalOtrosBancosActivosPasivos()
  }
  function editarPosisionBanco(data) {
    setdataSet(data)
    setbotonValidation(true);
    toggleShowModalOtrosBancosActivosPasivos()
  }
  function verDatosClientes(data) {
    setdataSet(data)
    setbotonValidation(false);
    toggleShowModalOtrosBancosActivosPasivos()
  }
  function verNegociosCompromisos(data) {
    settipo("NUEVONEGOCIO");
    setdataSet(data)
    setbotonValidation(false);
    toggleShowModalNegociosCompromisos()
  }
  function editarNegociosCompromisos(data) {
    settipo("ENUEVONEGOCIO");
    setdataSet(data)
    setbotonValidation(true);
    toggleShowModalNegociosCompromisos()
  }
  function verExpedientesOtros(data) {
    settipo("EXPEDIENTES");
    setdataSet(data)
    setbotonValidation(false);
    toggleShowModalExpedientesOtros()
  }
  function editarExpedientesOtros(data) {
    settipo("EEXPEDIENTES");
    setdataSet(data)
    setbotonValidation(true);
    toggleShowModalExpedientesOtros()
  }
  function verMatrixCompetitiva(data) {
    settipo("RENTABILIDAD");
    setdataSet(data)
    setbotonValidation(false);
    toggleShowModalMatrizCompetitiva()
  }
  function editarMatrixCompetitiva(data) {
    settipo("ERENTABILIDAD");
    setdataSet(data)
    setbotonValidation(true);
    toggleShowModalMatrizCompetitiva()
  }
  function eliminarDato(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalOtrosBancosActivosPasivos() {
    setShowModalOtrosBancosActivosPasivos(!showModalOtrosBancosActivosPasivos);
    removeBodyCss()
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalNegociosCompromisos() {
    setShowModalNegociosCompromisos(!showModalNegociosCompromisos);
    removeBodyCss()
  }
  //
  function toggleShowModalExpedientesOtros() {
    setShowModalExpedientesOtros(!showModalExpedientesOtros);
    removeBodyCss()
  }
  //
  function toggleShowModalMatrizCompetitiva() {
    setShowModalMatrizCompetitiva(!showModalMatrizCompetitiva);
    removeBodyCss()
  }
  function toggleShowModalMatrizCompetitivaPosicionBanesco() {
    setShowModalMatrizCompetitivaPosicionBanesco(!showModalMatrizCompetitivaPosicionBanesco);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    //props.onSubmit(values);
  }


  return (
    <React.Fragment>
      <h5>
        {t("Matriz Competitiva")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmMatrizCompetitiva" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="6">
            <Row>
              <Col md="12">
                <h6 className="float-start mt-1">{t("BanescoPosition")}</h6>
              </Col>
            </Row>
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Product")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("ParticipationBanesco")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      product: "",
                      closeVol: 0,
                      participation: 0
                    }); settipo("posicionBanesco"); setbotonValidation(true); toggleShowModalMatrizCompetitivaPosicionBanesco()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {posicionBanescoRows}
                <tr style={estiloTotales}><td><strong>{t("Total")}</strong></td><td><strong>{monto}</strong></td><td><strong>{participacion}</strong></td></tr>
              </tbody>
            </Table>
          </Col>

          <Col md="6">
            <Row>
              <Col md="12">
                <h6 className="float-start mt-1">{t("OtherBanksLiabilitiesAssets")}</h6>
              </Col>
            </Row>
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Bank")}</th>
                  <th>{t("Passive")}</th>
                  <th>{t("Participation")}</th>
                  <th>{t("LongTerm")}</th>
                  <th>{t("ShortTerm")}</th>
                  <th>{t("Total")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      "transactId": 0,
                      "bank": "",
                      "passivePercentage": 0,
                      "participation": 0,
                      "longTerm": 0,
                      "shortTerm": 0
                    }); settipo("OTROBANCO"); setbotonValidation(true); toggleShowModalOtrosBancosActivosPasivos()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {otrosBancosActivosPasivosRows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="6">
            <Row>
              <Col md="12">
                <h6 className="float-start mt-1">{t("BanescoTransactionalProducts")}</h6>
              </Col>
            </Row>
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Product")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("ParticipationBanesco")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      product: "",
                      closeVol: 0,
                      participation: 0
                    }); settipo("productosTransaccionales"); setbotonValidation(true); toggleShowModalMatrizCompetitivaPosicionBanesco()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {productosTransaccionalesRows}
                <tr style={estiloTotales}><td><strong>{t("Total")}</strong></td><td><strong>{montoTransacciones}</strong></td><td><strong>{participacionTransaccion}</strong></td></tr>
              </tbody>
            </Table>
          </Col>
          <Col md="6">
            <Row>
              <Col md="12">
                <h6 className="float-start mt-1">{t("NewBusinessesCommitments")}</h6>
              </Col>
            </Row>
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Type")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      "transactId": 0,
                      "businessType": {
                        "code": "",
                        "name": ""
                      },
                      "businessStatus": "",
                      "observations": ""
                    }); settipo("NUEVONEGOCIO"); setbotonValidation(true); toggleShowModalNegociosCompromisos()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {nuevosNegociosCompromisosRows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="6">
            <Row>
              <Col md="12">
                <h6 className="float-start mt-1">{t("ProfitabilityMatrix")}</h6>
              </Col>
            </Row>
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Type")}</th>
                  <th>{t("Quantity")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      "transactId": 0,
                      "matrixType": {
                        "code": "",
                        "name": ""
                      },
                      "quantity": 0,
                      "status": true
                    }); settipo("RENTABILIDAD"); setbotonValidation(true); toggleShowModalMatrizCompetitiva()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {matrizRentabilidadRows}
              </tbody>
            </Table>
          </Col>
          <Col md="6">

            <Row>
              <Col md="12">
                <h6 className="float-start mt-1">{t("FilesOthers")}</h6>
              </Col>
            </Row>

            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Type")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setdataSet({
                      "transactId": 5,
                      "fileType": {
                        "code": "",
                        "name": ""
                      },
                      "fileStatus": "",
                      "observations": ""
                    }); settipo("EXPEDIENTES"); setbotonValidation(true); toggleShowModalExpedientesOtros()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {expedientesOtrosRows}
              </tbody>
            </Table>

          </Col>
        </Row>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="comment">{t("Comment")}</Label>
              <AvField
                type="textarea"
                name="comment"
                id="comment"
                maxLength="1000"
                rows="7"
                value={props.dataMatrizCompetitiva.observations}
                onChange={(e) => { dataReturn.observations = e.target.value; setdataReturn(dataReturn); }}
               
              />
            </div>
          </Col>
        </Row>
      </AvForm>
      <ModalMatrizCompetitivaPosicionBanesco jsonSow={dataSet} tipo={tipo} actualizarDatos={actualizarDatos} guardarDatos={guardarDatos} botones={botonValidation} isOpen={showModalMatrizCompetitivaPosicionBanesco} toggle={() => { toggleShowModalMatrizCompetitivaPosicionBanesco() }} />
      <ModalMatrizCompetitiva jsonSow={dataSet} tipo={tipo} actualizarDatos={actualizarDatos} guardarDatos={guardarDatos} botones={botonValidation} isOpen={showModalMatrizCompetitiva} toggle={() => { toggleShowModalMatrizCompetitiva() }} />
      <ModalOtrosBancosActivosPasivos jsonSow={dataSet} tipo={tipo} actualizarDatos={actualizarDatos} guardarDatos={guardarDatos} botones={botonValidation} isOpen={showModalOtrosBancosActivosPasivos} toggle={() => { toggleShowModalOtrosBancosActivosPasivos() }} />
      <ModalNegociosCompromisos jsonSow={dataSet} tipo={tipo} actualizarDatos={actualizarDatos} guardarDatos={guardarDatos} botones={botonValidation} isOpen={showModalNegociosCompromisos} toggle={() => { toggleShowModalNegociosCompromisos() }} />
      <ModalExpedientesOtros jsonSow={dataSet} tipo={tipo} actualizarDatos={actualizarDatos} guardarDatos={guardarDatos} botones={botonValidation} isOpen={showModalExpedientesOtros} toggle={() => { toggleShowModalExpedientesOtros() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            if (tipo == "DOTROBANCO") {

              apiBack.eliminarMatrizOtrosBancosIGR({ transactId: Number(dataLocation.transactionId), otherBankId: dataDelete.otherBankId }).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DNUEVONEGOCIO") {

              apiBack.eliminarMatrizNuevosNegociosIGR({ transactId: Number(dataLocation.transactionId), newBusinessId: dataDelete.newBusinessId }).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DEXPEDIENTES") {

              apiBack.eliminarMatrizExpedientesIGR({ transactId: Number(dataLocation.transactionId), filesId: dataDelete.filesId }).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DRENTABILIDAD") {

              apiBack.eliminarMatrizRentabilidadIGR({ transactId: Number(dataLocation.transactionId), profitabilityId: dataDelete.profitabilityId }).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DposicionBanesco") {

              apiBack.eliminarMatrizPosicionBanescoIGR({ transactId: Number(dataLocation.transactionId), positionId: dataDelete.positionId }).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
            if (tipo == "DproductosTransaccionales") {

              apiBack.eliminarMatrizTransaccionBanescoIGR({ transactId: Number(dataLocation.transactionId), transactionalId: dataDelete.transactionalId }).then(resp => {
                if (resp.statusCode == "500") {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                } else {
                  setconfirm_alert(false)
                  setsuccessSave_dlg(true)
                }
              }).catch(error => {
                setconfirm_alert(false)
                seterror_dlg(true)
              })
            }
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );

});


MatrizCompetitiva.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}


export default MatrizCompetitiva;
