import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'

import * as OPTs from "../../helpers/options_helper"

import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../services/index.js"
import ModalCapex1 from "../../pages/CommercialCredit/4_InformeGestionReforzado/FormularioIGR/Secciones/ModalCapex1"
import ModalCapex2 from "../../pages/CommercialCredit/4_InformeGestionReforzado/FormularioIGR/Secciones/ModalCapex2"
import ModalCapex3 from "../../pages/CommercialCredit/4_InformeGestionReforzado/FormularioIGR/Secciones/ModalCapex3"
import AttachmentFileCore from "../Common/AttachmentFileCore"



const Capex = (props) => {

  const { t, i18n } = useTranslation();

  const { locationData } = props;

  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);

  const [dataCapex1, setdataCapex1] = useState({
    "transactId": 0,
    "description": "",
    "useInMiles": 0,
    "shareholder": "",
    "bank": ""
  });
  const [dataCapex2, setdataCapex2] = useState({
    "transactId": 0,
    "capexId": 0,
    "budget": "",
    "amount": 0,
    "status": true
  });
  const [dataCapex3, setdataCapex3] = useState({
    "transactId": 0,
    "capexDetailsId": 0,
    "observations": "",
    "amount": 0,
    "status": true
  });
  const [tipo, setTipo] = useState(null);

  const [formValid, setFormValid] = useState(false);
  const [dataCapex1Rows, setdataCapex1Rows] = useState(null);
  const [dataCapex2Rows, setdataCapex2Rows] = useState(null);
  const [dataCapex3Rows, setdataCapex3Rows] = useState(null);



  const [showModalCapex1, setShowModalCapex1] = useState(false);
  const [showModalCapex2, setShowModalCapex2] = useState(false);
  const [showModalCapex3, setShowModalCapex3] = useState(false);
  React.useEffect(() => {
    // Read Api Service data
    initializeData();
  }, []);

  const api = new BackendServices();

  function initializeData() {
    api.consultarCapexIGR(dataLocation.transactionId).then(resp => {
      console.log(resp);
      if (resp != undefined && resp.capex.filter($$ => $$.status == true).length > 1) {
        setdataCapex1Rows(resp.capex.map((data) => (
          data.status ?
            <tr key={data.id}>
              <td>{data.description}</td>
              <td>{data.useInMiles}</td>
              <td>{data.shareholder}</td>
              <td>{data.bank}</td>
              <td style={{ textAlign: "right" }}>
                <Button type="button" color="link" onClick={(resp) => { verDatos(data, "CAPEX1") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { editarDatos(data, "ECAPEX1") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                <Button type="button" color="link" onClick={(resp) => { EliminarDatos(data, "DCAPEX1") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
              </td>
            </tr> : null)
        ));
      } else {
        setdataCapex1Rows(
          <tr key={1}>
            <td colSpan="4" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarCapexPresupuestoIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataCapex2Rows(resp.capexBudget.map((data) => (
          <tr key={data.capexId}>
            <td>{data.budget}</td>
            <td>{data.amount}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { verDatos(data, "CAPEX2") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { editarDatos(data, "ECAPEX2") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { EliminarDatos(data, "DCAPEX2") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setdataCapex2Rows(
          <tr key={1}>
            <td colSpan="2" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
    api.consultarCapexDetalleProyectoIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataCapex3Rows(resp.capexProjectDetail.map((data) => (
          <tr key={data.capexDetailsId}>
            <td>{data.budget}</td>
            <td>{data.amount}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { verDatos(data, "CAPEX3") }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { editarDatos(data, "ECAPEX3") }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { EliminarDatos(data, "DCAPEX3") }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setdataCapex3Rows(
          <tr key={1}>
            <td colSpan="2" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });

  }
  function verDatos(data, tipo2) {
    setTipo(tipo2);
    if (tipo2 == "CAPEX1") {
      setdataCapex1(data);
      toggleShowModalCapex1()
    }
    if (tipo2 == "CAPEX2") {
      setdataCapex2(data);
      toggleShowModalCapex2()
    }
    if (tipo2 == "CAPEX3") {
      setdataCapex3(data);
      toggleShowModalCapex3()
    }
    setbotonValidation(false);
  }
  function editarDatos(data, tipo2) {
    setTipo(tipo2);
    if (tipo2 == "ECAPEX1") {
      setdataCapex1(data);
      toggleShowModalCapex1()
    }
    if (tipo2 == "ECAPEX2") {
      setdataCapex2(data);
      toggleShowModalCapex2()
    }
    if (tipo2 == "ECAPEX3") {
      setdataCapex3(data);
      toggleShowModalCapex3()
    }
    setbotonValidation(true);
  }
  function EliminarDatos(data, tipo2) {
    setTipo(tipo2);
    if (tipo2 == "ECAPEX1") {
      setdataCapex1(data);
    }
    if (tipo2 == "ECAPEX2") {
      setdataCapex2(data);
    }
    if (tipo2 == "ECAPEX3") {
      setdataCapex3(data);
    }
    setconfirm_alert(true);
  }
  function guardarDatos(data, tipo) {
    data.transactId = Number(dataLocation.transactionId);
    /* ---------------------------------------------------------------------------------------------- */
    /*                                  Guardamos los datos del cash                                  */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "CAPEX1") {
      api.nuevoCapexIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex1();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CAPEX2") {
      api.nuevoCapexPresupuestoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex2();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CAPEX3") {
      api.nuevoCapexDetalleProyectoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex3();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
  }
  function actualizarDatos(data, tipo) {
    data.transactId = dataLocation.transactionId;
    /* ---------------------------------------------------------------------------------------------- */
    /*                                    Editita el flujo de caja                                    */
    /* ---------------------------------------------------------------------------------------------- */
    if (tipo == "ECAPEX1") {
      api.actualizarCapexIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex1();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECAPEX2") {
      api.actualizarCapexPresupuestoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex2();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECAPEX3") {
      api.actualizarCapexDetalleProyectoIGR(data).then(resp => {
        if (resp != undefined) {
          setconfirm_alert(false)
          setsuccessSave_dlg(true)
          toggleShowModalCapex3();
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }

  }

  //abrimos modal para adjunar archivos
  function toggleShowModalCapex1() {
    setShowModalCapex1(!showModalCapex1);
    removeBodyCss()
  }

  //abrimos modal para adjunar archivos
  function toggleShowModalCapex2() {
    setShowModalCapex2(!showModalCapex2);
    removeBodyCss()
  }

  //abrimos modal para adjunar archivos
  function toggleShowModalCapex3() {
    setShowModalCapex3(!showModalCapex3);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    //props.onSubmit(values);
  }

  return (
    <Card>
      <CardBody>
        <h4 className="card-title">{t("Capex")}</h4>
        <p className="card-title-desc"></p>

        <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
          <Row>
            <Col md="12">

              <Table className="table mt-1" responsive>
                <thead className="table-light">
                  <tr>
                    <th>{t("Description")}</th>
                    <th>{t("UsesInMiles")}</th>
                    {/* <th>{t("SourcesThousands")}</th> */}
                    <th>{t("Shareholder")}</th>
                    <th>{t("Bank")}</th>
                    <th><Link className="float-end" onClick={() => {
                      setbotonValidation(true); setdataCapex1({
                        "transactId": 0,
                        "description": "",
                        "useInMiles": 0,
                        "shareholder": "",
                        "bank": ""
                      }); setTipo("CAPEX1"); toggleShowModalCapex1()
                    }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                  </tr>
                </thead>
                <tbody>
                  {dataCapex1Rows}
                </tbody>
              </Table>

            </Col>
          </Row>

          <Row>
            <Col md="12">
              <Table className="table mt-1" responsive>
                <thead className="table-light">
                  <tr>
                    <th>{t("Budget")}</th>
                    <th>{t("Amount")}</th>
                    <th><Link className="float-end" onClick={() => {
                      setbotonValidation(true); setdataCapex2({
                        "transactId": 0,
                        "capexId": 0,
                        "budget": "",
                        "amount": 0,
                        "status": true
                      }); setTipo("CAPEX2"); toggleShowModalCapex2()
                    }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                  </tr>
                </thead>
                <tbody>
                  {dataCapex2Rows}
                </tbody>
              </Table>
            </Col>
          </Row>

          <Row>
            <Col md="12">
              <Table className="table mt-1" responsive>
                <thead className="table-light">
                  <tr>
                    <th>{t("ProjectDetails")}</th>
                    <th>{t("Amount")}</th>
                    <th> <Link className="float-end" onClick={() => {
                      setbotonValidation(true); setdataCapex3({
                        "transactId": 0,
                        "capexDetailsId": 0,
                        "observations": "",
                        "amount": 0,
                        "status": true
                      }); setTipo("CAPEX3"); toggleShowModalCapex3()
                    }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                  </tr>
                </thead>
                <tbody>
                  {dataCapex3Rows}
                </tbody>
              </Table>
            </Col>
          </Row>
          <AttachmentFileCore locationData={{ instanceId: locationData.instanceId, processId: locationData.processId, activityId: OPTs.ACT_CAPEX }} />
        </AvForm>
        <ModalCapex1 guardarDatos={guardarDatos} botones={botonValidation} tipo={tipo} actualizarDatos={actualizarDatos} dataCapex1={dataCapex1} isOpen={showModalCapex1} toggle={() => { toggleShowModalCapex1() }} />
        <ModalCapex2 guardarDatos={guardarDatos} botones={botonValidation} tipo={tipo} actualizarDatos={actualizarDatos} dataCapex2={dataCapex2} isOpen={showModalCapex2} toggle={() => { toggleShowModalCapex2() }} />
        <ModalCapex3 guardarDatos={guardarDatos} botones={botonValidation} tipo={tipo} actualizarDatos={actualizarDatos} dataCapex3={dataCapex3} isOpen={showModalCapex3} toggle={() => { toggleShowModalCapex3() }} />
        {successSave_dlg ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)
              initializeData();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
              initializeData();
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
        {confirm_alert ? (
          <SweetAlert
            title={t("Areyousure")}
            warning
            showCancel
            confirmButtonText={t("Yesdeleteit")}
            confirmBtnBsStyle="success"
            cancelBtnBsStyle="danger"
            onConfirm={() => {
              const apiBack = new BackendServices();
              if (tipo == "DCAPEX1") {
                apiBack.eliminarCapexIGR({ transactId: dataLocation.transactionId, capexsId: dataCapex1.capexId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
              if (tipo == "DCAPEX2") {
                apiBack.eliminarCapexPresupuestoIGR({ transactId: dataLocation.transactionId, capexId: dataCapex2.capexId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
              if (tipo == "DCAPEX3") {
                apiBack.eliminarCapexDetalleProyectoIGR({ transactId: dataLocation.transactionId, capexDetailsId: dataCapex3.capexId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
            }}
            onCancel={() => setconfirm_alert(false)}
          >
            {t("Youwontbeabletorevertthis")}
          </SweetAlert>
        ) : null}

      </CardBody>
    </Card>
  );
};

Capex.propTypes = {
  sources: PropTypes.array,
  budgets: PropTypes.array,
  projects: PropTypes.array,
};

Capex.defaultProps = {
  sources: [],
  budgets: [],
  projects: []
};

export default Capex;
